VALUES ('b91dd008-d128-4a14-916f-c5668153d9e5', 'chimay-blue', 'chimay-blue-trappist', '{"en":"Chimay Blue (Grande Réserve)"}'::jsonb, 'trappist', 'belgian', 'belgian_strong_dark', '{"trappist","authentic","abbey","strong","christmas","cellar-worthy","complex"}', 'Belgium', 'BE', 'Wallonia', 'Chimay', '{"en":"Abbaye Notre-Dame de Scourmont"}'::jsonb, 1862, 'trappist', true, '1948', '{"en":"Chimay Blue was first brewed in 1948 as a Christmas beer at the Scourmont Abbey. The Trappist monks have been brewing beer since 1862 to fund their monastery''s charitable activities. Father Théodore isolated the unique Chimay yeast strain in 1948, which gives all Chimay beers their distinctive character. It''s one of only 14 authentic Trappist breweries worldwide."}'::jsonb, '{"World Beer Awards Gold","International Trappist Association certified"}', NULL, '{"en":"One of the most respected Trappist beers in the world, often considered the benchmark for Belgian strong dark ales"}'::jsonb, '{"en":"A legendary Belgian Trappist quadrupel brewed by monks since 1948. Rich, complex, and full-bodied with notes of dark fruit, caramel, and spice. Ages beautifully for up to 5 years, developing even more complexity."}'::jsonb, '{"en":"Grande Réserve - The Blue Cap"}'::jsonb, 9, NULL, NULL, 35, NULL, NULL, 30, NULL, NULL, 'dark_brown', 'slightly_hazy', 'medium', 'full', 'top_fermented', '{"complex","fruity","spicy","caramel","warming","rich","malty"}', '{"en":"Rich and complex with dark fruit flavors (plum, raisin, fig), caramel sweetness, subtle chocolate notes, and Belgian yeast spiciness (clove, pepper). The finish is warming with lingering dark fruit and subtle bitterness."}'::jsonb, '{"en":"Dark fruits, caramel, Belgian yeast esters, subtle chocolate, and warming alcohol"}'::jsonb, '{"en":"Rich dark fruit sweetness balanced by spicy yeast character"}'::jsonb, '{"en":"Long, warming finish with lingering fruit, caramel, and gentle bitterness"}'::jsonb, '{"en":"Masterfully balanced between sweetness, alcohol warmth, and subtle bitterness"}'::jsonb, 2, 4, '{"Pilsner malt","Caramel malt","Munich malt"}', '{"German hops","American hops"}', 'Chimay house yeast (isolated 1948)', '{}', NULL, '{"Belgian candi sugar"}', 'goblet', 'cellar', 10, 14, '{"en":"Pour slowly into a chalice, leaving yeast sediment in bottle. Allow to warm slightly for full flavor expression."}'::jsonb, true, '2-3 fingers', '{"cheese","game","rich-dishes","desserts"}', '{"en":"Exceptional with strong cheeses (Roquefort, aged Gouda), game meats, beef stew, dark chocolate desserts, and dried fruit. The classic pairing with Chimay cheese (made at the abbey)."}'::jsonb, '{"Chimay cheese","Roquefort","Aged Gouda","Stilton"}', '{"Belgian","French","Game dishes"}', NULL, '{"autumn","winter","christmas"}', '{"celebration","dinner","tasting","holiday"}', false, false, false, false, '{"bottle"}', '{"330","750","1500"}', '{"chimay-red","chimay-white","chimay-gold"}', 'year_round', 'premium', 90, 'https://www.chimay.com', 'Official Chimay website and International Trappist Association', 1)
