INSERT INTO beers (id, slug, stable_key, name, status, style_category, style, tags, origin_country, origin_country_code, origin_region, origin_city, brewery, brewery_founded, brewery_type, is_trappist, first_brewed, history_story, history_awards, history_named_after, history_significance, description, tagline, abv, abv_min, abv_max, ibu, ibu_min, ibu_max, srm, og, fg, color, clarity, carbonation, body, fermentation, flavor_profile, taste_description, taste_aroma, taste_first_impression, taste_finish, taste_balance, bitterness_level, sweetness_level, ingredients_malts, ingredients_hops, ingredients_yeast, ingredients_adjuncts, ingredients_water, ingredients_special, serving_glass, serving_temperature, serving_temp_min, serving_temp_max, serving_pouring_notes, serving_head_retention, serving_ideal_head, pairing_food_categories, pairing_food, pairing_cheese, pairing_cuisine, pairing_avoid, season_tags, occasion_tags, is_gluten_free, is_non_alcoholic, is_organic, is_vegan, available_formats, available_sizes, related_beers, availability, price_tier, popularity, source_url, source_note, version)
VALUES ('8c28f375-bda1-4914-a7bb-9dea4c43bb67', 'guinness-draught', 'guinness-draught-irish-stout', '{"en":"Guinness Draught"}'::jsonb, 'international_classic', 'stout_porter', 'irish_stout', '{"irish","iconic","dry-stout","nitrogen","creamy","dublin","st-patricks"}', 'Ireland', 'IE', NULL, 'Dublin', '{"en":"St. James''s Gate Brewery"}'::jsonb, 1759, 'macro', false, '1959', '{"en":"Arthur Guinness signed a 9,000-year lease on St. James''s Gate Brewery in 1759 for £45 per year. While Guinness has been brewing stout since the 1820s, the modern Draught version with nitrogen was developed in 1959 by mathematician Michael Ash. The nitrogen technology creates the signature creamy head and smooth mouthfeel. Today, over 10 million glasses are enjoyed daily worldwide."}'::jsonb, '{"Countless awards","Most famous stout worldwide"}', '{"en":"Named after founder Arthur Guinness, who established the brewery in Dublin"}'::jsonb, '{"en":"The world''s most iconic stout and one of the most recognizable beer brands globally"}'::jsonb, '{"en":"The world''s most famous dry Irish stout. Deep black with a creamy tan head, it offers roasted barley bitterness balanced by smooth, almost velvety texture from the nitrogen pour. Surprisingly light-bodied for its appearance."}'::jsonb, '{"en":"Good things come to those who wait"}'::jsonb, 4.2, NULL, NULL, 45, NULL, NULL, 40, NULL, NULL, 'black', 'opaque', 'low', 'medium_light', 'top_fermented', '{"roasty","dry","creamy","coffee","smooth","balanced","bittersweet"}', '{"en":"Roasted barley dominates with coffee and chocolate notes, subtle malty sweetness, and a dry, slightly bitter finish. The nitrogen creates an exceptionally smooth, creamy mouthfeel that contrasts with the roasted bitterness."}'::jsonb, '{"en":"Roasted barley, coffee, subtle chocolate, and a hint of caramel"}'::jsonb, '{"en":"Creamy and smooth with immediate roasted character"}'::jsonb, '{"en":"Dry and roasty with lingering coffee bitterness"}'::jsonb, NULL, 3, 2, '{"Pale malt","Roasted barley","Flaked barley"}', '{"East Kent Goldings","Target"}', 'Guinness proprietary ale yeast', '{}', NULL, '{"Nitrogen gas for pour"}', 'pint', 'cool', 6, 8, '{"en":"The famous \"two-part pour\": Fill glass to 3/4, let settle until black and tan separate (119.5 seconds), then top off. The surge and settle creates the signature cascading effect."}'::jsonb, true, '1-1.5 inches', '{"irish-food","oysters","stew","cheese","chocolate"}', '{"en":"Classic with oysters, Irish stew, shepherd''s pie, blue cheese, and chocolate desserts. The roasted character complements grilled meats and sharp cheeses."}'::jsonb, '{"Stilton","Irish cheddar","Blue cheese"}', '{"Irish","British","BBQ"}', NULL, '{"all_year","autumn","winter"}', '{"pub_night","casual","holiday","celebration"}', false, false, false, true, '{"draft","can","bottle"}', '{"330","440","500"}', '{"guinness-extra-stout","guinness-foreign-extra"}', 'year_round', 'mid', 98, 'https://www.guinness.com', 'Official Guinness website and 250+ years of brewing heritage', 1)
