VALUES ('123af9a7-0a87-4b73-9fcd-91e7aad031ef', 'founders-breakfast-stout', 'founders-breakfast-stout-imperial', '{"en":"Founders Breakfast Stout"}'::jsonb, 'craft', 'stout_porter', 'imperial_stout', '{"craft","american","imperial-stout","coffee","chocolate","oats","michigan","bold","breakfast"}', 'United States', 'US', 'Michigan', 'Grand Rapids', '{"en":"Founders Brewing Company"}'::jsonb, 1997, 'craft', false, '2001', '{"en":"Founders Brewing Company launched Breakfast Stout in 2001 as one of the first American imperial stouts brewed with coffee and oats. The beer combines two of the most important things in life: breakfast and beer. Made with Sumatra and Kona coffee, flaked oats for silky texture, and chocolate malts, it became an instant cult classic. The beer helped establish Founders as a premier craft brewery and inspired countless coffee stouts. Originally a seasonal release, high demand made it year-round in 2017."}'::jsonb, '{"Great American Beer Festival Gold","World Beer Cup Gold","Top-rated imperial stout"}', '{"en":"Named for the breakfast staples used: coffee, oats, and chocolate - the most important meal of the day"}'::jsonb, '{"en":"One of the most influential American coffee stouts, pioneering the imperial breakfast stout category"}'::jsonb, '{"en":"A bold imperial stout brewed with coffee and oats. Pitch black with a thick tan head, it delivers intense coffee and chocolate flavors with smooth oatmeal texture. The double dose of coffee (Sumatra and Kona) creates a rich espresso character balanced by chocolate malt sweetness."}'::jsonb, '{"en":"The coffee lover''s consummate beer"}'::jsonb, 8.3, NULL, NULL, 60, NULL, NULL, 45, NULL, NULL, 'black', 'opaque', 'medium', 'full', 'top_fermented', '{"coffee","chocolate","roasty","bold","smooth","espresso","oaty","rich"}', '{"en":"Intense coffee flavors dominate with rich espresso, dark chocolate, and roasted malt character. The oats provide a silky, smooth mouthfeel that balances the bold coffee bitterness. Notes of cocoa, caramel, and dark fruit complexity. Strong but remarkably drinkable with coffee that shines through without being harsh."}'::jsonb, '{"en":"Strong roasted coffee, dark chocolate, cocoa, caramel, and roasted malt"}'::jsonb, '{"en":"Bold coffee punch with smooth, velvety oat texture"}'::jsonb, '{"en":"Long, rich coffee finish with chocolate and roasted malt lingering"}'::jsonb, NULL, 4, 3, '{"Pale malt","Chocolate malt","Roasted barley","Caramel malt","Flaked oats"}', '{"Magnum","Cascade"}', 'American ale yeast', '{}', NULL, '{"Sumatra coffee","Kona coffee","Flaked oats"}', 'snifter', 'cellar', 10, 13, '{"en":"Pour into a tulip or snifter glass to concentrate the coffee aromatics. Allow to warm slightly for full flavor development."}'::jsonb, true, '0.5-1 inch', '{"breakfast","dessert","chocolate","cheese","brunch"}', '{"en":"Perfect with brunch dishes, chocolate cake, tiramisu, coffee ice cream, bacon, pancakes, or smoked meats. The coffee pairs excellently with chocolate and breakfast foods."}'::jsonb, '{"Aged cheddar","Smoked gouda","Mascarpone","Blue cheese"}', '{"American","Breakfast","Dessert","BBQ"}', NULL, '{"all_year","winter","autumn"}', '{"brunch","dessert","nightcap","special_occasion"}', false, false, false, true, '{"draft","bottle","can"}', '{"355","473"}', '{"left-hand-milk-stout","old-rasputin"}', 'year_round', 'mid_high', 92, 'https://foundersbrewing.com', 'Official Founders Brewing website and coffee stout innovation since 2001', 1)
