INSERT INTO beers (id, slug, stable_key, name, status, style_category, style, tags, origin_country, origin_country_code, origin_region, origin_city, brewery, brewery_founded, brewery_type, is_trappist, first_brewed, history_story, history_awards, history_named_after, history_significance, description, tagline, abv, abv_min, abv_max, ibu, ibu_min, ibu_max, srm, og, fg, color, clarity, carbonation, body, fermentation, flavor_profile, taste_description, taste_aroma, taste_first_impression, taste_finish, taste_balance, bitterness_level, sweetness_level, ingredients_malts, ingredients_hops, ingredients_yeast, ingredients_adjuncts, ingredients_water, ingredients_special, serving_glass, serving_temperature, serving_temp_min, serving_temp_max, serving_pouring_notes, serving_head_retention, serving_ideal_head, pairing_food_categories, pairing_food, pairing_cheese, pairing_cuisine, pairing_avoid, season_tags, occasion_tags, is_gluten_free, is_non_alcoholic, is_organic, is_vegan, available_formats, available_sizes, related_beers, availability, price_tier, popularity, source_url, source_note, version)
VALUES ('0f42ecac-ecc6-4724-bbec-8c5f900089dc', 'hoegaarden', 'hoegaarden-witbier', '{"en":"Hoegaarden","it":"Hoegaarden","vi":"Hoegaarden"}'::jsonb, 'international_classic', 'belgian', 'belgian_witbier', '{"witbier","wheat","belgian","refreshing","spiced","classic","citrus"}', 'Belgium', 'BE', 'Flemish Brabant', 'Hoegaarden', '{"en":"Hoegaarden Brewery (AB InBev)","it":"Birrificio Hoegaarden (AB InBev)","vi":"Nhà máy bia Hoegaarden (AB InBev)"}'::jsonb, 1966, 'regional', false, '1445', '{"en":"Hoegaarden witbier dates back to 1445 when monks in the village of Hoegaarden brewed wheat beer with spices. Production died out in 1957, but in 1966, milkman Pierre Celis revived the style in his hay loft, recreating the traditional recipe with wheat, coriander, and orange peel. The beer became hugely successful, popularizing witbier worldwide and inspiring countless imitations. Hoegaarden is credited with single-handedly reviving the Belgian witbier style.","it":"La witbier Hoegaarden risale al 1445 quando i monaci nel villaggio di Hoegaarden producevano birra di frumento con spezie. La produzione cessò nel 1957, ma nel 1966, il lattaio Pierre Celis rianimò lo stile nel suo fienile, ricreando la ricetta tradizionale con frumento, coriandolo e scorza d''arancia. La birra ebbe un enorme successo, rendendo popolare la witbier in tutto il mondo e ispirando innumerevoli imitazioni. A Hoegaarden è attribuito il merito di aver rianimato da sola lo stile witbier belga.","vi":"Hoegaarden witbier có từ năm 1445 khi các tu sĩ ở làng Hoegaarden sản xuất bia lúa mì với gia vị. Sản xuất chấm dứt năm 1957, nhưng năm 1966, người giao sữa Pierre Celis đã hồi sinh phong cách trong gác xép cỏ khô của mình, tái tạo công thức truyền thống với lúa mì, rau mùi và vỏ cam. Bia trở nên cực kỳ thành công, phổ biến witbier trên toàn thế giới và truyền cảm hứng cho vô số bản sao. Hoegaarden được ghi nhận đã tự mình hồi sinh phong cách witbier Bỉ."}'::jsonb, '{"World Beer Awards","European Beer Star Gold"}', NULL, '{"en":"Revived and popularized the Belgian witbier style, becoming the benchmark for the category worldwide","it":"Ha rianimato e reso popolare lo stile witbier belga, diventando il punto di riferimento per la categoria in tutto il mondo","vi":"Đã hồi sinh và phổ biến phong cách witbier Bỉ, trở thành chuẩn mực cho thể loại trên toàn thế giới"}'::jsonb, '{"en":"The beer that revived the Belgian witbier style. Refreshing, cloudy wheat beer spiced with coriander and orange peel. Light, citrusy, and perfectly balanced - ideal for warm weather and casual drinking.","it":"La birra che ha rianimato lo stile witbier belga. Birra di frumento rinfrescante e torbida speziata con coriandolo e scorza d''arancia. Leggera, agrumata e perfettamente equilibrata - ideale per il clima caldo e il bere informale.","vi":"Bia đã hồi sinh phong cách witbier Bỉ. Bia lúa mì đục, sảng khoái được gia vị với rau mùi và vỏ cam. Nhẹ, cam quýt và cân bằng hoàn hảo - lý tưởng cho thời tiết ấm và uống giải trí."}'::jsonb, '{"en":"The Original Belgian White Beer","it":"L''Originale Birra Bianca Belga","vi":"Bia Trắng Bỉ Nguyên Bản"}'::jsonb, 4.9, NULL, NULL, 15, NULL, NULL, 3, NULL, NULL, 'straw', 'cloudy', 'high', 'light', 'top_fermented', '{"citrusy","spicy","refreshing","wheat","light","crisp","zesty"}', '{"en":"Light, refreshing, and perfectly balanced. Citrus (orange, lemon) from orange peel, coriander spice adds herbal, slightly peppery notes. Wheat provides soft, creamy texture. Very low bitterness with a clean, crisp finish. High carbonation makes it extremely refreshing and easy drinking.","it":"Leggera, rinfrescante e perfettamente equilibrata. Agrumi (arancia, limone) dalla scorza d''arancia, le spezie di coriandolo aggiungono note erbacee e leggermente pepate. Il frumento fornisce una consistenza morbida e cremosa. Amarezza molto bassa con un finale pulito e fresco. L''alta carbonazione la rende estremamente rinfrescante e facile da bere.","vi":"Nhẹ, sảng khoái và cân bằng hoàn hảo. Cam quýt (cam, chanh) từ vỏ cam, gia vị rau mùi thêm hương thảo mộc, hơi cay. Lúa mì cung cấp kết cấu mềm, kem. Đắng rất thấp với kết thúc sạch, giòn. Độ sủi bọt cao làm cho nó cực kỳ sảng khoái và dễ uống."}'::jsonb, '{"en":"Orange citrus, coriander spice, wheat, light yeast esters, subtle clove","it":"Agrumi d''arancia, spezie di coriandolo, frumento, leggeri esteri di lievito, sottili chiodi di garofano","vi":"Cam quýt cam, gia vị rau mùi, lúa mì, ester men nhẹ, đinh hương tinh tế"}'::jsonb, '{"en":"Bright citrus refreshment with subtle spice complexity","it":"Rinfrescante agrumata brillante con sottile complessità speziata","vi":"Sảng khoái cam quýt tươi sáng với độ phức tạp gia vị tinh tế"}'::jsonb, '{"en":"Clean, crisp, and refreshing with lingering citrus and spice","it":"Pulita, fresca e rinfrescante con agrumi e spezie persistenti","vi":"Sạch, giòn và sảng khoái với cam quýt và gia vị kéo dài"}'::jsonb, '{"en":"Perfectly balanced between citrus, spice, and wheat softness","it":"Perfettamente equilibrata tra agrumi, spezie e morbidezza del frumento","vi":"Cân bằng hoàn hảo giữa cam quýt, gia vị và độ mềm lúa mì"}'::jsonb, 1, 2, '{"Wheat malt","Pilsner malt","Oats"}', '{"Noble hops"}', 'Belgian witbier yeast', '{}', NULL, '{"Coriander seeds","Curaçao orange peel"}', 'weizen', 'cold', 3, 5, '{"en":"Pour into a tall wheat beer glass. Gently swirl the bottle before pouring the last bit to incorporate the yeast sediment for authentic cloudiness. Garnish with an orange slice if desired.","it":"Versare in un alto bicchiere da birra di frumento. Ruotare delicatamente la bottiglia prima di versare l''ultimo po'' per incorporare il sedimento di lievito per un''autentica torbidità. Guarnire con una fetta d''arancia se desiderato.","vi":"Rót vào ly bia lúa mì cao. Xoay nhẹ chai trước khi rót phần cuối để kết hợp cặn men cho độ đục chân thực. Trang trí với lát cam nếu muốn."}'::jsonb, true, '2 fingers', '{"seafood","light-dishes","salads","cheese"}', '{"en":"Perfect with mussels, grilled fish, light salads, goat cheese, sushi, Thai cuisine, and fresh fruit. The citrus and spice complement seafood beautifully. Classic pairing with Belgian mussels in white wine.","it":"Perfetta con cozze, pesce alla griglia, insalate leggere, formaggio di capra, sushi, cucina thailandese e frutta fresca. Gli agrumi e le spezie complementano magnificamente i frutti di mare. Abbinamento classico con cozze belghe al vino bianco.","vi":"Hoàn hảo với vẹm, cá nướng, salad nhẹ, phô mai dê, sushi, ẩm thực Thái và trái cây tươi. Cam quýt và gia vị bổ sung cho hải sản một cách tuyệt đẹp. Kết hợp cổ điển với vẹm Bỉ trong rượu trắng."}'::jsonb, '{"Goat cheese","Feta","Brie","Fresh mozzarella"}', '{"Belgian","Thai","Seafood","Vietnamese"}', NULL, '{"spring","summer","all_year"}', '{"casual","lunch","aperitivo","outdoor","social"}', false, false, false, true, '{"bottle","can","draft"}', '{330,750}', '{"blue-moon","allagash-white","st-bernardus-wit"}', 'year_round', 'affordable', 88, 'https://www.hoegaarden.com', 'Official Hoegaarden website and Belgian witbier heritage', 1)
