-- 19. Murphy's Irish Stout
INSERT INTO beers (id, slug, stable_key, name, status, style_category, style, tags, origin_country, origin_country_code, origin_region, origin_city, brewery, brewery_founded, brewery_type, is_trappist, first_brewed, history_story, history_awards, history_named_after, history_significance, description, tagline, abv, abv_min, abv_max, ibu, ibu_min, ibu_max, srm, og, fg, color, clarity, carbonation, body, fermentation, flavor_profile, taste_description, taste_aroma, taste_first_impression, taste_finish, taste_balance, bitterness_level, sweetness_level, ingredients_malts, ingredients_hops, ingredients_yeast, ingredients_adjuncts, ingredients_water, ingredients_special, serving_glass, serving_temperature, serving_temp_min, serving_temp_max, serving_pouring_notes, serving_head_retention, serving_ideal_head, pairing_food_categories, pairing_food, pairing_cheese, pairing_cuisine, pairing_avoid, season_tags, occasion_tags, is_gluten_free, is_non_alcoholic, is_organic, is_vegan, available_formats, available_sizes, related_beers, availability, price_tier, popularity, source_url, source_note, version)
VALUES ('c577d906-99de-49dc-91c6-bd53688d3c2e', 'murphys-irish-stout', 'murphys-irish-stout-cork', '{"en":"Murphy''s Irish Stout","it":"Murphy''s Irish Stout","vi":"Murphy''s Irish Stout"}'::jsonb, 'international_classic', 'stout_porter', 'irish_stout', '{"irish","stout","creamy","smooth","nitrogen","cork","roasty","malty"}', 'Ireland', 'IE', NULL, 'Cork', '{"en":"Murphy''s Brewery","it":"Birrificio Murphy''s","vi":"Nhà máy bia Murphy''s"}'::jsonb, 1856, 'macro', false, '1856', '{"en":"Founded by James J. Murphy in Cork in 1856, Murphy''s Irish Stout has been a proud alternative to Dublin stouts for over 160 years. The brewery was acquired by Heineken in 1983 but maintains its traditional Cork brewing heritage. Murphy''s is known for being smoother and less bitter than its Dublin rival, with a sweeter malt character and creamier texture. The nitrogen-dispensed version became widely available in the 1980s, making it a favorite in Cork pubs and beyond.","it":"Fondato da James J. Murphy a Cork nel 1856, Murphy''s Irish Stout è stata un''orgogliosa alternativa alle stout di Dublino per oltre 160 anni. Il birrificio fu acquisito da Heineken nel 1983 ma mantiene la sua tradizionale eredità di Cork. Murphy''s è conosciuta per essere più morbida e meno amara della sua rivale di Dublino, con un carattere maltato più dolce e una texture più cremosa. La versione spillata ad azoto divenne ampiamente disponibile negli anni ''80, rendendola una favorita nei pub di Cork e oltre.","vi":"Được thành lập bởi James J. Murphy ở Cork năm 1856, Murphy''s Irish Stout là một lựa chọn thay thế đáng tự hào cho các stout Dublin trong hơn 160 năm. Nhà máy bia được Heineken mua lại năm 1983 nhưng vẫn duy trì di sản sản xuất bia truyền thống Cork. Murphy''s nổi tiếng mượt hơn và ít đắng hơn đối thủ Dublin, với đặc tính mạch nha ngọt hơn và kết cấu kem hơn. Phiên bản rót nitrogen trở nên phổ biến rộng rãi vào những năm 1980, khiến nó trở thành yêu thích tại các quán rượu Cork và xa hơn."}'::jsonb, '{"World Beer Cup awards","Cork brewing heritage"}', '{"en":"Named after founder James J. Murphy, who established the brewery in Cork","it":"Prende il nome dal fondatore James J. Murphy, che fondò il birrificio a Cork","vi":"Được đặt theo tên người sáng lập James J. Murphy, người thành lập nhà máy bia ở Cork"}'::jsonb, '{"en":"Ireland''s second most popular stout, representing Cork''s brewing tradition as an alternative to Dublin stouts","it":"La seconda stout più popolare d''Irlanda, che rappresenta la tradizione birraria di Cork come alternativa alle stout di Dublino","vi":"Stout phổ biến thứ hai của Ireland, đại diện cho truyền thống sản xuất bia Cork như một lựa chọn thay thế cho stout Dublin"}'::jsonb, '{"en":"A smooth, creamy Irish stout from Cork with a sweeter, maltier profile than typical Dublin stouts. Deep black with a dense tan head, it offers chocolate and coffee notes with less roasted bitterness and a notably smooth, velvety mouthfeel from nitrogen dispense.","it":"Una stout irlandese morbida e cremosa da Cork con un profilo più dolce e maltato rispetto alle tipiche stout di Dublino. Nera intensa con una densa schiuma color nocciola, offre note di cioccolato e caffè con meno amarezza tostata e una sensazione in bocca notevolmente morbida e vellutata dalla spillatura ad azoto.","vi":"Một stout Ireland mượt mà, kem từ Cork với hương vị ngọt hơn, nhiều mạch nha hơn so với stout Dublin điển hình. Đen sâu với lớp bọt dày màu nâu nhạt, nó mang đến hương chocolate và cà phê với ít vị đắng rang hơn và cảm giác miệng mượt mà, nhung đáng chú ý từ việc rót nitrogen."}'::jsonb, '{"en":"Like the smoothest velvet","it":"Come il velluto più morbido","vi":"Như nhung mượt mà nhất"}'::jsonb, 4, NULL, NULL, 35, NULL, NULL, 40, NULL, NULL, 'black', 'opaque', 'low', 'medium', 'top_fermented', '{"smooth","creamy","malty","chocolate","coffee","sweet","velvety"}', '{"en":"Sweeter and maltier than Guinness, with pronounced chocolate and toffee notes. Less roasted bitterness, more coffee sweetness, and an exceptionally creamy, smooth mouthfeel from nitrogen. The finish is gentle and slightly sweet rather than dry and bitter.","it":"Più dolce e maltata di Guinness, con pronunciate note di cioccolato e caramello. Meno amarezza tostata, più dolcezza di caffè e una sensazione in bocca eccezionalmente cremosa e morbida dall''azoto. Il finale è delicato e leggermente dolce piuttosto che secco e amaro.","vi":"Ngọt hơn và nhiều mạch nha hơn Guinness, với hương chocolate và toffee rõ rệt. Ít vị đắng rang hơn, nhiều vị ngọt cà phê hơn và cảm giác miệng cực kỳ kem, mượt từ nitrogen. Kết thúc nhẹ nhàng và hơi ngọt thay vì khô và đắng."}'::jsonb, '{"en":"Chocolate, coffee, caramel, toasted malt, and subtle roasted notes","it":"Cioccolato, caffè, caramello, malto tostato e sottili note tostate","vi":"Chocolate, cà phê, caramel, mạch nha rang và hương rang tinh tế"}'::jsonb, '{"en":"Exceptionally creamy and smooth with sweet malty character","it":"Eccezionalmente cremosa e morbida con carattere maltato dolce","vi":"Cực kỳ kem và mượt với đặc tính mạch nha ngọt"}'::jsonb, '{"en":"Smooth and slightly sweet with gentle coffee notes","it":"Morbido e leggermente dolce con delicate note di caffè","vi":"Mượt và hơi ngọt với hương cà phê nhẹ nhàng"}'::jsonb, NULL, 2, 3, '{"Pale malt","Roasted barley","Chocolate malt","Caramel malt"}', '{"Irish hops","English hops"}', 'Murphy''s proprietary ale yeast', '{}', NULL, '{"Nitrogen gas for dispense"}', 'pint', 'cool', 6, 8, '{"en":"Pour in two stages with nitrogen tap. Fill to 3/4, let settle until cascading stops, then top off for a perfect creamy head.","it":"Versare in due fasi con spillatore ad azoto. Riempire a 3/4, lasciare riposare fino a quando la cascata si ferma, poi completare per una perfetta schiuma cremosa.","vi":"Rót hai giai đoạn với vòi nitrogen. Đổ đến 3/4, để lắng cho đến khi hiệu ứng xếp tầng dừng, sau đó đổ đầy để có lớp bọt kem hoàn hảo."}'::jsonb, true, '1-1.5 inches', '{"irish-food","seafood","stew","cheese","dessert"}', '{"en":"Excellent with seafood chowder, Irish stew, corned beef, aged cheddar, and chocolate desserts. The sweeter profile pairs well with shellfish and creamy dishes.","it":"Eccellente con zuppa di pesce, stufato irlandese, corned beef, cheddar stagionato e dessert al cioccolato. Il profilo più dolce si abbina bene con i frutti di mare e i piatti cremosi.","vi":"Tuyệt vời với súp hải sản, stew Ireland, thịt bò muối, cheddar lâu năm và món tráng miệng chocolate. Hương vị ngọt hơn kết hợp tốt với hải sản và món kem."}'::jsonb, '{"Irish cheddar","Gruyere","Blue cheese"}', '{"Irish","Seafood","British"}', NULL, '{"all_year","autumn","winter"}', '{"pub_night","casual","dinner","relaxation"}', false, false, false, false, '{"draft","can","bottle"}', '{330,440,500}', '{"guinness-draught","beamish-stout"}', 'year_round', 'mid', 75, 'https://www.murphysirishstout.com', 'Official Murphy''s website and Cork brewing heritage since 1856', 1)
