-- 21. Founders Breakfast Stout
INSERT INTO beers (id, slug, stable_key, name, status, style_category, style, tags, origin_country, origin_country_code, origin_region, origin_city, brewery, brewery_founded, brewery_type, is_trappist, first_brewed, history_story, history_awards, history_named_after, history_significance, description, tagline, abv, abv_min, abv_max, ibu, ibu_min, ibu_max, srm, og, fg, color, clarity, carbonation, body, fermentation, flavor_profile, taste_description, taste_aroma, taste_first_impression, taste_finish, taste_balance, bitterness_level, sweetness_level, ingredients_malts, ingredients_hops, ingredients_yeast, ingredients_adjuncts, ingredients_water, ingredients_special, serving_glass, serving_temperature, serving_temp_min, serving_temp_max, serving_pouring_notes, serving_head_retention, serving_ideal_head, pairing_food_categories, pairing_food, pairing_cheese, pairing_cuisine, pairing_avoid, season_tags, occasion_tags, is_gluten_free, is_non_alcoholic, is_organic, is_vegan, available_formats, available_sizes, related_beers, availability, price_tier, popularity, source_url, source_note, version)
VALUES ('00b36ff5-6d75-4bfb-ab58-595c4a18119c', 'founders-breakfast-stout', 'founders-breakfast-stout-imperial', '{"en":"Founders Breakfast Stout","it":"Founders Breakfast Stout","vi":"Founders Breakfast Stout"}'::jsonb, 'craft', 'stout_porter', 'imperial_stout', '{"craft","american","imperial-stout","coffee","chocolate","oats","michigan","bold","breakfast"}', 'United States', 'US', 'Michigan', 'Grand Rapids', '{"en":"Founders Brewing Company","it":"Birrificio Founders","vi":"Công ty Bia Founders"}'::jsonb, 1997, 'craft', false, '2001', '{"en":"Founders Brewing Company launched Breakfast Stout in 2001 as one of the first American imperial stouts brewed with coffee and oats. The beer combines two of the most important things in life: breakfast and beer. Made with Sumatra and Kona coffee, flaked oats for silky texture, and chocolate malts, it became an instant cult classic. The beer helped establish Founders as a premier craft brewery and inspired countless coffee stouts. Originally a seasonal release, high demand made it year-round in 2017.","it":"Founders Brewing Company ha lanciato Breakfast Stout nel 2001 come una delle prime imperial stout americane prodotte con caffè e avena. La birra combina due delle cose più importanti della vita: colazione e birra. Prodotta con caffè Sumatra e Kona, fiocchi d''avena per una texture setosa e malti al cioccolato, è diventata un classico di culto istantaneo. La birra ha contribuito a stabilire Founders come un birrificio artigianale di premier e ha ispirato innumerevoli stout al caffè. Originariamente un rilascio stagionale, l''alta domanda l''ha resa disponibile tutto l''anno nel 2017.","vi":"Founders Brewing Company ra mắt Breakfast Stout năm 2001 như một trong những imperial stout Mỹ đầu tiên được sản xuất với cà phê và yến mạch. Bia kết hợp hai trong số những điều quan trọng nhất trong cuộc sống: bữa sáng và bia. Được làm từ cà phê Sumatra và Kona, bột yến mạch cho kết cấu mượt mà và mạch nha chocolate, nó trở thành một tác phẩm kinh điển ngay lập tức. Bia đã giúp thiết lập Founders như một nhà máy bia craft hàng đầu và truyền cảm hứng cho vô số stout cà phê. Ban đầu là sản phẩm theo mùa, nhu cầu cao đã khiến nó có sẵn quanh năm vào năm 2017."}'::jsonb, '{"Great American Beer Festival Gold","World Beer Cup Gold","Top-rated imperial stout"}', '{"en":"Named for the breakfast staples used: coffee, oats, and chocolate - the most important meal of the day","it":"Prende il nome dagli ingredienti base della colazione utilizzati: caffè, avena e cioccolato - il pasto più importante della giornata","vi":"Được đặt theo tên các thành phần bữa sáng được sử dụng: cà phê, yến mạch và chocolate - bữa ăn quan trọng nhất trong ngày"}'::jsonb, '{"en":"One of the most influential American coffee stouts, pioneering the imperial breakfast stout category","it":"Una delle stout al caffè americane più influenti, pioniera della categoria imperial breakfast stout","vi":"Một trong những stout cà phê Mỹ có ảnh hưởng nhất, tiên phong trong danh mục imperial breakfast stout"}'::jsonb, '{"en":"A bold imperial stout brewed with coffee and oats. Pitch black with a thick tan head, it delivers intense coffee and chocolate flavors with smooth oatmeal texture. The double dose of coffee (Sumatra and Kona) creates a rich espresso character balanced by chocolate malt sweetness.","it":"Una coraggiosa imperial stout prodotta con caffè e avena. Nero intenso con una spessa schiuma color nocciola, offre intensi sapori di caffè e cioccolato con una texture morbida di farina d''avena. La doppia dose di caffè (Sumatra e Kona) crea un ricco carattere di espresso bilanciato dalla dolcezza del malto al cioccolato.","vi":"Một imperial stout táo bạo được sản xuất với cà phê và yến mạch. Đen tuyền với lớp bọt dày màu nâu nhạt, nó mang đến hương vị cà phê và chocolate mãnh liệt với kết cấu yến mạch mượt mà. Liều lượng cà phê kép (Sumatra và Kona) tạo ra đặc tính espresso phong phú được cân bằng bởi vị ngọt mạch nha chocolate."}'::jsonb, '{"en":"The coffee lover''s consummate beer","it":"La birra perfetta per gli amanti del caffè","vi":"Bia hoàn hảo cho người yêu cà phê"}'::jsonb, 8.3, NULL, NULL, 60, NULL, NULL, 45, NULL, NULL, 'black', 'opaque', 'medium', 'full', 'top_fermented', '{"coffee","chocolate","roasty","bold","smooth","espresso","oaty","rich"}', '{"en":"Intense coffee flavors dominate with rich espresso, dark chocolate, and roasted malt character. The oats provide a silky, smooth mouthfeel that balances the bold coffee bitterness. Notes of cocoa, caramel, and dark fruit complexity. Strong but remarkably drinkable with coffee that shines through without being harsh.","it":"Intensi sapori di caffè dominano con ricco espresso, cioccolato fondente e carattere di malto tostato. L''avena fornisce una sensazione in bocca setosa e morbida che bilancia l''ardita amarezza del caffè. Note di cacao, caramello e complessità di frutta scura. Forte ma straordinariamente bevibile con caffè che risalta senza essere aspro.","vi":"Hương vị cà phê mãnh liệt chiếm ưu thế với espresso phong phú, chocolate đen và đặc tính mạch nha rang. Yến mạch mang lại cảm giác miệng mượt mà, mịn màng cân bằng vị đắng cà phê táo bạo. Hương vị ca cao, caramel và phức tạp trái cây tối. Mạnh mẽ nhưng dễ uống đáng chú ý với cà phê tỏa sáng mà không khắc nghiệt."}'::jsonb, '{"en":"Strong roasted coffee, dark chocolate, cocoa, caramel, and roasted malt","it":"Forte caffè tostato, cioccolato fondente, cacao, caramello e malto tostato","vi":"Cà phê rang mạnh, chocolate đen, ca cao, caramel và mạch nha rang"}'::jsonb, '{"en":"Bold coffee punch with smooth, velvety oat texture","it":"Potente colpo di caffè con texture di avena morbida e vellutata","vi":"Hương cà phê mạnh mẽ với kết cấu yến mạch mượt, nhung"}'::jsonb, '{"en":"Long, rich coffee finish with chocolate and roasted malt lingering","it":"Lungo e ricco finale di caffè con cioccolato e malto tostato persistente","vi":"Kết thúc cà phê dài, phong phú với chocolate và mạch nha rang kéo dài"}'::jsonb, NULL, 4, 3, '{"Pale malt","Chocolate malt","Roasted barley","Caramel malt","Flaked oats"}', '{"Magnum","Cascade"}', 'American ale yeast', '{}', NULL, '{"Sumatra coffee","Kona coffee","Flaked oats"}', 'snifter', 'cellar', 10, 13, '{"en":"Pour into a tulip or snifter glass to concentrate the coffee aromatics. Allow to warm slightly for full flavor development.","it":"Versare in un bicchiere tulip o snifter per concentrare gli aromi del caffè. Lasciare riscaldare leggermente per lo sviluppo completo del sapore.","vi":"Rót vào ly tulip hoặc snifter để tập trung hương thơm cà phê. Để ấm lên một chút để phát triển hương vị đầy đủ."}'::jsonb, true, '0.5-1 inch', '{"breakfast","dessert","chocolate","cheese","brunch"}', '{"en":"Perfect with brunch dishes, chocolate cake, tiramisu, coffee ice cream, bacon, pancakes, or smoked meats. The coffee pairs excellently with chocolate and breakfast foods.","it":"Perfetta con piatti da brunch, torta al cioccolato, tiramisù, gelato al caffè, bacon, pancake o carni affumicate. Il caffè si abbina eccellentemente con cioccolato e cibi da colazione.","vi":"Hoàn hảo với món brunch, bánh chocolate, tiramisu, kem cà phê, thịt xông khói, bánh kếp hoặc thịt hun khói. Cà phê kết hợp tuyệt vời với chocolate và đồ ăn sáng."}'::jsonb, '{"Aged cheddar","Smoked gouda","Mascarpone","Blue cheese"}', '{"American","Breakfast","Dessert","BBQ"}', NULL, '{"all_year","winter","autumn"}', '{"brunch","dessert","nightcap","special_occasion"}', false, false, false, true, '{"draft","bottle","can"}', '{355,473}', '{"left-hand-milk-stout","old-rasputin"}', 'year_round', 'mid_high', 92, 'https://foundersbrewing.com', 'Official Founders Brewing website and coffee stout innovation since 2001', 1)
