-- 25. Weihenstephaner Hefeweissbier
INSERT INTO beers (id, slug, stable_key, name, status, style_category, style, tags, origin_country, origin_country_code, origin_region, origin_city, brewery, brewery_founded, brewery_type, is_trappist, first_brewed, history_story, history_awards, history_named_after, history_significance, description, tagline, abv, abv_min, abv_max, ibu, ibu_min, ibu_max, srm, og, fg, color, clarity, carbonation, body, fermentation, flavor_profile, taste_description, taste_aroma, taste_first_impression, taste_finish, taste_balance, bitterness_level, sweetness_level, ingredients_malts, ingredients_hops, ingredients_yeast, ingredients_adjuncts, ingredients_water, ingredients_special, serving_glass, serving_temperature, serving_temp_min, serving_temp_max, serving_pouring_notes, serving_head_retention, serving_ideal_head, pairing_food_categories, pairing_food, pairing_cheese, pairing_cuisine, pairing_avoid, season_tags, occasion_tags, is_gluten_free, is_non_alcoholic, is_organic, is_vegan, available_formats, available_sizes, related_beers, availability, price_tier, popularity, source_url, source_note, version)
VALUES ('71b1751f-bd19-4859-befc-219059831f16', 'weihenstephaner-hefeweissbier', 'weihenstephaner-hefeweissbier', '{"en":"Weihenstephaner Hefeweissbier","it":"Weihenstephaner Hefeweissbier","vi":"Weihenstephaner Hefeweissbier"}'::jsonb, 'international_classic', 'wheat', 'hefeweizen', '{"german","bavarian","wheat","unfiltered","banana","clove","oldest-brewery","traditional"}', 'Germany', 'DE', 'Bavaria', 'Freising', '{"en":"Weihenstephan Brewery","it":"Birrificio Weihenstephan","vi":"Nhà máy bia Weihenstephan"}'::jsonb, 1040, 'regional', false, '1040', '{"en":"Weihenstephan holds the title of the world''s oldest continuously operating brewery, founded by Benedictine monks in 1040. Located on the historic Weihenstephan hill in Freising, Bavaria, the brewery has nearly 1,000 years of brewing expertise. The monastery received the official brewing license from the city of Freising in 1040. Today, it is owned by the Bavarian state and closely tied to the Technical University of Munich''s brewing science program. Their Hefeweissbier is considered the gold standard for the style.","it":"Weihenstephan detiene il titolo del birrificio più antico del mondo ancora in funzione, fondato dai monaci benedettini nel 1040. Situato sulla storica collina di Weihenstephan a Frisinga, Baviera, il birrificio vanta quasi 1.000 anni di esperienza nella produzione di birra. Il monastero ricevette la licenza ufficiale di produzione dalla città di Frisinga nel 1040. Oggi è di proprietà dello stato bavarese e strettamente legato al programma di scienze della birra dell''Università Tecnica di Monaco. La loro Hefeweissbier è considerata il gold standard per questo stile.","vi":"Weihenstephan giữ danh hiệu nhà máy bia lâu đời nhất thế giới vẫn đang hoạt động liên tục, được thành lập bởi các tu sĩ Benedictine năm 1040. Nằm trên đồi Weihenstephan lịch sử ở Freising, Bavaria, nhà máy bia có gần 1.000 năm chuyên môn sản xuất bia. Tu viện nhận giấy phép sản xuất bia chính thức từ thành phố Freising năm 1040. Ngày nay, nó thuộc sở hữu của bang Bavaria và gắn liền với chương trình khoa học sản xuất bia của Đại học Kỹ thuật Munich. Hefeweissbier của họ được coi là tiêu chuẩn vàng cho phong cách này."}'::jsonb, '{}', '{"en":"Named after the Weihenstephan Abbey, where the brewery was founded","it":"Prende il nome dall''Abbazia di Weihenstephan, dove fu fondato il birrificio","vi":"Được đặt theo tên Tu viện Weihenstephan, nơi nhà máy bia được thành lập"}'::jsonb, NULL, '{"en":"The definitive Bavarian Hefeweizen from the world''s oldest brewery. Features a perfect balance of banana and clove aromatics from traditional wheat beer yeast, cloudy golden appearance, and silky smooth texture. The benchmark by which all other wheat beers are measured.","it":"La Hefeweizen bavarese definitiva dal birrificio più antico del mondo. Presenta un perfetto equilibrio di aromatici di banana e chiodi di garofano dal lievito tradizionale da birra di frumento, aspetto dorato torbido e texture setosa. Il punto di riferimento con cui tutte le altre birre di frumento vengono misurate.","vi":"Hefeweizen Bavaria tiêu chuẩn từ nhà máy bia lâu đời nhất thế giới. Có sự cân bằng hoàn hảo của hương thơm chuối và đinh hương từ men bia lúa mì truyền thống, vẻ ngoài vàng đục và kết cấu mịn như lụa. Tiêu chuẩn để đo lường tất cả các loại bia lúa mì khác."}'::jsonb, '{"en":"World''s Oldest Brewery''s Finest Wheat Beer","it":"La Miglior Birra di Frumento dal Birrificio Più Antico del Mondo","vi":"Bia Lúa Mì Tuyệt Nhất Từ Nhà Máy Bia Lâu Đời Nhất Thế Giới"}'::jsonb, 5.4, NULL, NULL, 14, NULL, NULL, 7, NULL, NULL, 'gold', 'hazy', 'high', 'medium', 'top_fermented', '{"banana","clove","creamy","refreshing","wheaty","fruity","spicy","balanced"}', '{"en":"Harmonious banana and clove esters from traditional Weihenstephan yeast strain, smooth wheat malt foundation, subtle citrus and vanilla notes, with a clean, refreshing finish. Impeccably balanced with low bitterness that showcases the yeast character.","it":"Armonioso esteri di banana e chiodi di garofano dal tradizionale ceppo di lievito Weihenstephan, base di malto di frumento liscia, sottili note di agrumi e vaniglia, con un finale pulito e rinfrescante. Impeccabilmente bilanciato con bassa amarezza che mette in risalto il carattere del lievito.","vi":"Este chuối và đinh hương hài hòa từ chủng men Weihenstephan truyền thống, nền malt lúa mì mượt mà, hương cam quýt và vani tinh tế, với kết thúc sạch, sảng khoái. Cân bằng hoàn hảo với độ đắng thấp làm nổi bật đặc tính men."}'::jsonb, '{"en":"Prominent banana and clove, subtle vanilla, hints of citrus and bubblegum","it":"Banana e chiodi di garofano prominenti, sottile vaniglia, accenni di agrumi e bubblegum","vi":"Chuối và đinh hương nổi bật, vanilla tinh tế, gợi ý cam quýt và kẹo cao su"}'::jsonb, '{"en":"Silky smooth wheat character with perfectly balanced banana-clove yeast expression","it":"Carattere di frumento setosamente liscio con espressione di lievito banana-chiodi di garofano perfettamente bilanciata","vi":"Đặc tính lúa mì mượt như lụa với biểu hiện men chuối-đinh hương cân bằng hoàn hảo"}'::jsonb, '{"en":"Clean, refreshing finish with delicate wheat sweetness and lingering yeast character","it":"Finale pulito e rinfrescante con delicata dolcezza di frumento e persistente carattere di lievito","vi":"Kết thúc sạch, sảng khoái với vị ngọt lúa mì tinh tế và đặc tính men kéo dài"}'::jsonb, NULL, 1, 3, '{"Wheat malt (60%)","Barley malt"}', '{"Hallertauer Tradition","Perle"}', 'Proprietary Weihenstephan Hefeweizen yeast strain', '{}', NULL, '{}', 'weizen', 'cold', 6, 8, '{"en":"Use a chilled Weizen glass. Pour slowly at 45-degree angle until 3/4 full. Gently swirl bottle to resuspend yeast sediment, then pour remainder to create a thick, creamy head.","it":"Usa un bicchiere Weizen freddo. Versare lentamente a 45 gradi fino a 3/4. Agitare delicatamente la bottiglia per risospendere il sedimento di lievito, quindi versare il resto per creare una schiuma spessa e cremosa.","vi":"Sử dụng ly Weizen ướp lạnh. Rót chậm nghiêng 45 độ cho đến 3/4 đầy. Xoay nhẹ chai để tái huyền phù trầm tích men, sau đó rót phần còn lại để tạo lớp bọt dày, kem."}'::jsonb, true, '3-4 fingers', '{"german-food","seafood","salads","breakfast","light-dishes"}', '{"en":"Perfect with traditional Bavarian Weisswurst and sweet mustard. Excellent with grilled fish, shellfish, Caesar salad, eggs Benedict, fresh mozzarella, and fruit-based desserts.","it":"Perfetto con tradizionale Weisswurst bavarese e senape dolce. Eccellente con pesce alla griglia, crostacei, insalata Caesar, uova alla Benedict, mozzarella fresca e dessert a base di frutta.","vi":"Hoàn hảo với Weisswurst Bavaria truyền thống và mù tạt ngọt. Tuyệt vời với cá nướng, động vật có vỏ, salad Caesar, trứng Benedict, mozzarella tươi và món tráng miệng trái cây."}'::jsonb, '{"Bavarian Obatzda","Fresh Mozzarella","Camembert","Brie"}', '{"German","Bavarian","Seafood","Brunch"}', NULL, '{"all_year","summer","spring"}', '{"brunch","casual","party","dinner","celebration"}', false, false, false, true, '{"bottle","draft"}', '{500,330}', '{"paulaner-hefeweizen","erdinger-weissbier"}', 'year_round', 'mid', 92, 'https://www.weihenstephaner.de', 'Official Weihenstephan website and world''s oldest brewery heritage', 1)
