INSERT INTO beers (id, slug, stable_key, name, status, style_category, style, tags, origin_country, origin_country_code, origin_region, origin_city, brewery, brewery_founded, brewery_type, is_trappist, first_brewed, history_story, history_awards, history_named_after, history_significance, description, tagline, abv, abv_min, abv_max, ibu, ibu_min, ibu_max, srm, og, fg, color, clarity, carbonation, body, fermentation, flavor_profile, taste_description, taste_aroma, taste_first_impression, taste_finish, taste_balance, bitterness_level, sweetness_level, ingredients_malts, ingredients_hops, ingredients_yeast, ingredients_adjuncts, ingredients_water, ingredients_special, serving_glass, serving_temperature, serving_temp_min, serving_temp_max, serving_pouring_notes, serving_head_retention, serving_ideal_head, pairing_food_categories, pairing_food, pairing_cheese, pairing_cuisine, pairing_avoid, season_tags, occasion_tags, is_gluten_free, is_non_alcoholic, is_organic, is_vegan, available_formats, available_sizes, related_beers, availability, price_tier, popularity, source_url, source_note, version)
VALUES ('954fcaf5-6397-48b6-ac22-f64ceda47d1f', 'spaten-oktoberfestbier', 'spaten-oktoberfestbier', '{"en":"Spaten Oktoberfestbier","it":"Spaten Oktoberfestbier","vi":"Spaten Oktoberfestbier"}'::jsonb, 'international_classic', 'lager', 'marzen', '{"german","bavarian","lager","oktoberfest","munich","amber","festbier","seasonal"}', 'Germany', 'DE', 'Bavaria', 'Munich', '{"en":"Spaten-Franziskaner-Bräu","it":"Birrificio Spaten-Franziskaner","vi":"Nhà máy bia Spaten-Franziskaner"}'::jsonb, 1397, 'regional', false, '1872', '{"en":"Spaten is one of Munich''s legendary breweries, founded in 1397. In 1872, Spaten brewer Josef Sedlmayr created the Oktoberfestbier style specifically for the Oktoberfest celebration, introducing the amber Märzen-style beer that became synonymous with the festival. Before this, darker beers dominated. Spaten''s innovation revolutionized Oktoberfest and created a new beer category. The brewery was also a pioneer in lager brewing techniques and refrigeration technology in the 19th century. Today, Spaten remains one of the six official Oktoberfest breweries allowed to serve at the festival, and their Oktoberfestbier is considered the original and most authentic version of this celebrated style.","it":"Spaten è uno dei birrifici leggendari di Monaco, fondato nel 1397. Nel 1872, il birraio Spaten Josef Sedlmayr creò lo stile Oktoberfestbier specificamente per la celebrazione dell''Oktoberfest, introducendo la birra ambrata in stile Märzen che divenne sinonimo del festival. Prima di questo, dominavano le birre più scure. L''innovazione di Spaten ha rivoluzionato l''Oktoberfest e creato una nuova categoria di birra. Il birrificio fu anche pioniere nelle tecniche di produzione lager e nella tecnologia di refrigerazione nel XIX secolo. Oggi, Spaten rimane uno dei sei birrifici ufficiali dell''Oktoberfest autorizzati a servire al festival, e la loro Oktoberfestbier è considerata la versione originale e più autentica di questo stile celebrato.","vi":"Spaten là một trong những nhà máy bia huyền thoại của Munich, được thành lập năm 1397. Năm 1872, thợ pha chế bia Spaten Josef Sedlmayr đã tạo ra phong cách Oktoberfestbier đặc biệt cho lễ hội Oktoberfest, giới thiệu bia hổ phách kiểu Märzen trở thành đồng nghĩa với lễ hội. Trước đó, bia tối màu chiếm ưu thế. Sự đổi mới của Spaten đã cách mạng hóa Oktoberfest và tạo ra một loại bia mới. Nhà máy bia cũng là tiên phong trong kỹ thuật sản xuất lager và công nghệ làm lạnh vào thế kỷ 19. Ngày nay, Spaten vẫn là một trong sáu nhà máy bia Oktoberfest chính thức được phép phục vụ tại lễ hội, và Oktoberfestbier của họ được coi là phiên bản gốc và đích thực nhất của phong cách được tôn vinh này."}'::jsonb, '{}', '{"en":"Spaten means \"spade\" in German, referencing the brewery''s historic brewing tools","it":"Spaten significa \"vanga\" in tedesco, in riferimento agli strumenti storici di produzione del birrificio","vi":"Spaten có nghĩa là \"cái xẻng\" trong tiếng Đức, ám chỉ các công cụ sản xuất bia lịch sử của nhà máy bia"}'::jsonb, NULL, '{"en":"The original Oktoberfest beer created in 1872. Rich amber lager with toasty malt character, subtle caramel sweetness, and clean finish. This is the beer that defined the Märzen/Oktoberfest style and remains the authentic taste of Munich''s most famous celebration.","it":"La birra Oktoberfest originale creata nel 1872. Lager ambrata ricca con carattere maltato tostato, sottile dolcezza di caramello e finale pulito. Questa è la birra che ha definito lo stile Märzen/Oktoberfest e rimane il gusto autentico della celebrazione più famosa di Monaco.","vi":"Bia Oktoberfest gốc được tạo ra năm 1872. Lager hổ phách phong phú với đặc tính malt rang, vị ngọt caramel tinh tế và kết thúc sạch. Đây là loại bia định nghĩa phong cách Märzen/Oktoberfest và vẫn là hương vị đích thực của lễ kỷ niệm nổi tiếng nhất Munich."}'::jsonb, '{"en":"The Original Oktoberfest Beer Since 1872","it":"L''Originale Birra Oktoberfest dal 1872","vi":"Bia Oktoberfest Gốc Từ Năm 1872"}'::jsonb, 5.9, NULL, NULL, 22, NULL, NULL, 14, NULL, NULL, 'amber', 'clear', 'medium', 'medium', 'bottom_fermented', '{"malty","toasty","caramel","bready","clean","balanced","smooth"}', '{"en":"Rich toasted malt with notes of fresh bread, caramel, and subtle toffee. Smooth malt sweetness balanced by moderate noble hop bitterness. Clean lager fermentation with no fruity esters. Full-bodied yet remarkably drinkable, with a dry finish that prevents cloying sweetness.","it":"Ricco malto tostato con note di pane fresco, caramello e sottile toffee. Dolcezza maltata liscia bilanciata da moderata amarezza di luppolo nobile. Fermentazione lager pulita senza esteri fruttati. Corposo ma straordinariamente bevibile, con un finale secco che previene la dolcezza stucchevole.","vi":"Malt rang phong phú với hương bánh mì tươi, caramel và kẹo bơ tinh tế. Vị ngọt malt mượt cân bằng bởi độ đắng hoa bia quý tộc vừa phải. Lên men lager sạch không có este trái cây. Đầy đặn nhưng dễ uống đáng kể, với kết thúc khô ngăn vị ngọt ngấy."}'::jsonb, '{"en":"Toasted bread, caramel malt, subtle toffee, light noble hops","it":"Pane tostato, malto caramellato, sottile toffee, luppolo nobile leggero","vi":"Bánh mì rang, malt caramel, kẹo bơ tinh tế, hoa bia quý tộc nhẹ"}'::jsonb, '{"en":"Rich malty sweetness with toasty character and smooth body","it":"Ricca dolcezza maltata con carattere tostato e corpo liscio","vi":"Vị ngọt malt phong phú với đặc tính rang và thân mượt"}'::jsonb, '{"en":"Clean, dry finish with lingering malt character and subtle hop bitterness","it":"Finale pulito e secco con persistente carattere maltato e sottile amarezza di luppolo","vi":"Kết thúc sạch, khô với đặc tính malt kéo dài và độ đắng hoa bia tinh tế"}'::jsonb, NULL, 2, 4, '{"Munich malt","Vienna malt","Pilsner malt"}', '{"Hallertauer Tradition","Tettnanger"}', 'Traditional Munich lager yeast', '{}', NULL, '{}', 'mug', 'cold', 7, 9, '{"en":"Serve in a traditional 1-liter Maßkrug (Oktoberfest mug). Pour at steady angle to create a dense 2-finger head. Traditional at Oktoberfest tents.","it":"Servire in un tradizionale Maßkrug da 1 litro (boccale Oktoberfest). Versare con angolo costante per creare una densa schiuma di 2 dita. Tradizionale nelle tende Oktoberfest.","vi":"Phục vụ trong Maßkrug 1 lít truyền thống (cốc Oktoberfest). Rót nghiêng đều để tạo lớp bọt dày 2 ngón. Truyền thống tại lều Oktoberfest."}'::jsonb, true, '2-3 fingers', '{"german-food","grilled-meats","sausages","roasted-dishes"}', '{"en":"Classic pairing with Oktoberfest foods: Hendl (roast chicken), Schweinshaxe (roasted pork knuckle), bratwurst, pretzels with Obatzda, roasted meats, and hearty Bavarian fare.","it":"Abbinamento classico con cibi Oktoberfest: Hendl (pollo arrosto), Schweinshaxe (stinco di maiale arrosto), bratwurst, pretzel con Obatzda, carni arrosto e sostanziosa cucina bavarese.","vi":"Kết hợp cổ điển với món ăn Oktoberfest: Hendl (gà nướng), Schweinshaxe (giò heo nướng), bratwurst, pretzel với Obatzda, thịt nướng và món ăn Bavaria thịnh soạn."}'::jsonb, '{"Obatzda","Emmental","Gruyère","Aged Cheddar"}', '{"German","Bavarian","Grilled","Roasted Meats"}', NULL, '{"fall","oktoberfest","autumn"}', '{"oktoberfest","celebration","party","festival","dinner"}', false, false, false, true, '{"bottle","draft","can"}', '{500,330}', '{"paulaner-oktoberfest","augustiner-helles"}', 'seasonal', 'mid', 85, 'https://www.spaten.de', 'Official Spaten brewery and Oktoberfest history', 1)
