INSERT INTO beers (id, slug, stable_key, name, status, style_category, style, tags, origin_country, origin_country_code, origin_region, origin_city, brewery, brewery_founded, brewery_type, is_trappist, first_brewed, history_story, history_awards, history_named_after, history_significance, description, tagline, abv, abv_min, abv_max, ibu, ibu_min, ibu_max, srm, og, fg, color, clarity, carbonation, body, fermentation, flavor_profile, taste_description, taste_aroma, taste_first_impression, taste_finish, taste_balance, bitterness_level, sweetness_level, ingredients_malts, ingredients_hops, ingredients_yeast, ingredients_adjuncts, ingredients_water, ingredients_special, serving_glass, serving_temperature, serving_temp_min, serving_temp_max, serving_pouring_notes, serving_head_retention, serving_ideal_head, pairing_food_categories, pairing_food, pairing_cheese, pairing_cuisine, pairing_avoid, season_tags, occasion_tags, is_gluten_free, is_non_alcoholic, is_organic, is_vegan, available_formats, available_sizes, related_beers, availability, price_tier, popularity, source_url, source_note, version)
VALUES ('fe6a2e06-654d-4fc1-a3c6-06a55a11ed0c', 'newcastle-brown-ale', 'newcastle-brown-ale-british-ale', '{"en":"Newcastle Brown Ale","it":"Newcastle Brown Ale","vi":"Newcastle Brown Ale"}'::jsonb, 'international_classic', 'ale', 'brown_ale', '{"british","brown-ale","iconic","geordie","classic"}', 'United Kingdom', 'GB', NULL, 'Newcastle upon Tyne', '{"en":"Newcastle Breweries","it":"Birrificio Newcastle","vi":"Nhà máy bia Newcastle"}'::jsonb, 1927, 'macro', false, '1927', '{"en":"Created in 1927 by Colonel Jim Porter, Newcastle Brown Ale quickly became the most popular bottled beer in the UK. The beer was specifically developed to appeal to the working-class population of Newcastle. Its distinctive blue star logo became iconic, and the beer earned the nickname \"The Dog\" among locals. It won the Challenge Cup for Champion Beer of Britain in its first year of production.","it":"Creata nel 1927 dal colonnello Jim Porter, Newcastle Brown Ale divenne rapidamente la birra in bottiglia più popolare nel Regno Unito. La birra fu sviluppata specificamente per attrarre la popolazione operaia di Newcastle. Il suo distintivo logo a stella blu divenne iconico e la birra guadagnò il soprannome \"The Dog\" tra la gente del posto. Vinse la Challenge Cup per Champion Beer of Britain nel suo primo anno di produzione.","vi":"Được tạo ra vào năm 1927 bởi Đại tá Jim Porter, Newcastle Brown Ale nhanh chóng trở thành bia chai phổ biến nhất ở Anh. Bia được phát triển đặc biệt để hấp dẫn tầng lớp lao động của Newcastle. Logo ngôi sao xanh đặc trưng của nó trở thành biểu tượng, và bia có biệt danh \"The Dog\" trong người dân địa phương. Nó đã giành được Challenge Cup cho Champion Beer of Britain trong năm sản xuất đầu tiên."}'::jsonb, '{"Challenge Cup 1927","International Beer Challenge Gold"}', '{"en":"Named after the city of Newcastle upon Tyne in Northeast England","it":"Prende il nome dalla città di Newcastle upon Tyne nel nord-est dell''Inghilterra","vi":"Được đặt tên theo thành phố Newcastle upon Tyne ở Đông Bắc Anh"}'::jsonb, NULL, '{"en":"A smooth, easy-drinking English brown ale with a distinctive ruby-brown color. This iconic beer combines a subtle sweetness with a gentle nutty character, creating a balanced and approachable beer that has become synonymous with British brewing tradition.","it":"Una brown ale inglese morbida e facile da bere con un distintivo colore bruno-rubino. Questa birra iconica combina una sottile dolcezza con un delicato carattere di nocciola, creando una birra equilibrata e accessibile che è diventata sinonimo della tradizione birraria britannica.","vi":"Một loại bia nâu Anh mượt mà, dễ uống với màu nâu ruby đặc trưng. Loại bia biểu tượng này kết hợp vị ngọt tinh tế với đặc tính hạt dẻo nhẹ nhàng, tạo ra một loại bia cân bằng và dễ tiếp cận đã trở thành từ đồng nghĩa với truyền thống bia Anh."}'::jsonb, '{"en":"The One and Only","it":"L''Unica e Sola","vi":"Độc Nhất Vô Nhị"}'::jsonb, 4.7, NULL, NULL, 23, NULL, NULL, 18, NULL, NULL, 'brown', 'clear', 'medium', 'medium', 'top_fermented', '{"smooth","nutty","caramel","malty","sweet","balanced"}', '{"en":"Smooth and mellow with flavors of caramel, toffee, and toasted nuts. A subtle sweetness is balanced by gentle hop bitterness. The beer has a creamy mouthfeel with notes of dried fruit and a hint of chocolate in the background.","it":"Morbida e delicata con sapori di caramello, toffee e noci tostate. Una sottile dolcezza è bilanciata da una delicata amarezza di luppolo. La birra ha una sensazione cremosa in bocca con note di frutta secca e un accenno di cioccolato sullo sfondo.","vi":"Mượt mà và dịu dàng với hương vị caramel, kẹo toffee và hạt rang. Vị ngọt tinh tế được cân bằng bởi vị đắng hoa bia nhẹ nhàng. Bia có cảm giác sảng khoái trong miệng với hương trái cây khô và một chút sô-cô-la phía sau."}'::jsonb, '{"en":"Gentle malt sweetness with caramel, toffee, and nutty notes; subtle fruity esters and minimal hop presence","it":"Delicata dolcezza di malto con note di caramello, toffee e nocciola; sottili esteri fruttati e presenza minima di luppolo","vi":"Vị ngọt mạch nha nhẹ nhàng với hương caramel, toffee và hạt; ester trái cây tinh tế và hoa bia tối thiểu"}'::jsonb, NULL, '{"en":"Smooth, clean finish with lingering sweetness and a gentle dry note","it":"Finale morbido e pulito con dolcezza persistente e una delicata nota secca","vi":"Kết thúc mượt mà, sạch với vị ngọt kéo dài và nốt khô nhẹ nhàng"}'::jsonb, NULL, 2, 3, '{"Pale malt","Crystal malt","Chocolate malt"}', '{"English hops","Fuggles","Goldings"}', 'English ale yeast', '{}', NULL, '{}', 'nonic', 'cool', 8, 10, '{"en":"Pour in one smooth motion into a tilted glass to create a small creamy head","it":"Versare in un movimento fluido in un bicchiere inclinato per creare una piccola schiuma cremosa","vi":"Rót trong một chuyển động mượt mà vào ly nghiêng để tạo lớp bọt kem nhỏ"}'::jsonb, true, NULL, '{"pub-food","grilled-meats","hearty-dishes"}', '{"en":"Excellent with hearty British fare: meat pies, grilled sausages, roasted chicken, burgers, and sharp cheddar. The nutty sweetness pairs well with barbecue and smoked meats. Also great with chocolate desserts.","it":"Eccellente con sostanziosi piatti britannici: torte di carne, salsicce grigliate, pollo arrosto, hamburger e cheddar stagionato. La dolcezza di nocciola si abbina bene con barbecue e carni affumicate. Ottima anche con dessert al cioccolato.","vi":"Tuyệt vời với món ăn Anh thịnh soạn: bánh thịt, xúc xích nướng, gà quay, burger và phô mai cheddar. Vị ngọt hạt kết hợp tốt với thịt nướng và hun khói. Cũng tuyệt vời với món tráng miệng sô-cô-la."}'::jsonb, '{"Sharp Cheddar","Gouda","Gruyère","Blue cheese"}', '{"British","American BBQ","Pub fare","Comfort food"}', NULL, '{"all_year","autumn","winter"}', '{"casual","pub_night","sports","relaxing","bbq"}', false, false, false, true, '{"bottle","can","draft"}', '{330,500,550}', '{}', 'year_round', 'value', 82, 'https://www.newcastlebrown.com', 'Official Newcastle Brown Ale website and brewing history', 1)
