-- 42. Shock Top Belgian White
INSERT INTO beers (id, slug, stable_key, name, status, style_category, style, tags, origin_country, origin_country_code, origin_region, origin_city, brewery, brewery_founded, brewery_type, is_trappist, first_brewed, history_story, history_awards, history_named_after, history_significance, description, tagline, abv, abv_min, abv_max, ibu, ibu_min, ibu_max, srm, og, fg, color, clarity, carbonation, body, fermentation, flavor_profile, taste_description, taste_aroma, taste_first_impression, taste_finish, taste_balance, bitterness_level, sweetness_level, ingredients_malts, ingredients_hops, ingredients_yeast, ingredients_adjuncts, ingredients_water, ingredients_special, serving_glass, serving_temperature, serving_temp_min, serving_temp_max, serving_pouring_notes, serving_head_retention, serving_ideal_head, pairing_food_categories, pairing_food, pairing_cheese, pairing_cuisine, pairing_avoid, season_tags, occasion_tags, is_gluten_free, is_non_alcoholic, is_organic, is_vegan, available_formats, available_sizes, related_beers, availability, price_tier, popularity, source_url, source_note, version)
VALUES ('f71fb600-2a32-48e3-b05a-f4e63a17fb87', 'shock-top-belgian-white', 'shock-top-belgian-white', '{"en":"Shock Top Belgian White","it":"Shock Top Belgian White","vi":"Shock Top Belgian White"}'::jsonb, 'international_classic', 'wheat', 'witbier', '{"wheat","belgian-style","citrus","orange","coriander","spiced","refreshing","hazy"}', 'United States', 'US', 'Colorado', 'Fort Collins', 'Shock Top Brewing Company', NULL, NULL, false, NULL, NULL, '{}', NULL, NULL, '{"en":"Shock Top Belgian White is an unfiltered Belgian-style wheat ale brewed with orange, lemon, and lime peels, along with coriander and other spices. The beer pours a cloudy pale golden color with a thick white foam head. It delivers a smooth, light-to-medium body with bright citrus flavors and subtle spice notes. The wheat base provides a slightly sweet, bready character that balances the zesty citrus profile. The finish is crisp and clean with a touch of tartness, making it highly refreshing and easy to drink.","it":"Shock Top Belgian White è un''ale di frumento non filtrata in stile belga, prodotta con scorze di arancia, limone e lime, insieme a coriandolo e altre spezie. La birra si presenta di un colore dorato pallido torbido con una densa schiuma bianca. Offre un corpo morbido, da leggero a medio, con vivaci sapori di agrumi e sottili note speziate. La base di frumento fornisce un carattere leggermente dolce e di pane che bilancia il profilo agrumato vivace. Il finale è frizzante e pulito con un tocco di acidità, rendendola altamente rinfrescante e facile da bere.","vi":"Shock Top Belgian White là một loại bia lúa mì kiểu Bỉ không lọc được pha chế với vỏ cam, chanh vàng và chanh xanh, cùng với rau mùi tây và các gia vị khác. Bia có màu vàng nhạt đục với lớp bọt trắng dày. Nó mang lại độ đậm nhẹ đến trung bình mượt mà với hương vị cam quýt tươi sáng và hương vị gia vị tinh tế. Nền lúa mì cung cấp đặc tính bánh mì hơi ngọt cân bằng hồ sơ cam quýt sảng khoái. Hậu vị giòn và sạch với một chút chua, làm cho nó rất sảng khoái và dễ uống."}'::jsonb, NULL, 5.2, NULL, NULL, 10, NULL, NULL, 4, NULL, NULL, 'pale', 'hazy', 'high', 'medium_light', 'top_fermented', '{}', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '{}', '{}', NULL, '{}', NULL, '{}', 'weizen', 'cold', NULL, NULL, NULL, true, NULL, '{}', NULL, '{}', '{}', NULL, '{"summer","spring","all_year"}', '{"casual","social","outdoor","brunch","relaxation"}', false, false, false, true, '{"bottle","can","draft"}', '{"12oz","16oz","24oz"}', '{}', 'year_round', 'mid', 85, NULL, NULL, 1)
