VALUES ('7cd3bde8-82a0-4206-982b-1035c583c3df', 'sapporo-premium', 'sapporo-premium-japanese-lager', '{"en":"Sapporo Premium Beer"}'::jsonb, 'international_classic', 'lager', 'japanese_lager', '{"japanese","premium","silver-can","oldest-japanese","rice","clean"}', 'Japan', 'JP', NULL, 'Sapporo, Hokkaido', '{"en":"Sapporo Breweries"}'::jsonb, 1876, 'macro', false, '1876', '{"en":"Sapporo is Japan''s oldest beer brand, founded in 1876 in Hokkaido by German-trained brewmaster Seibei Nakagawa. The star logo represents Hokkaido''s pioneering spirit. The distinctive silver can was introduced in 1984 and became iconic. Sapporo pioneered Japanese beer exports and remains one of the \"Big Four\" Japanese breweries."}'::jsonb, '{"World Beer Awards","Japan''s oldest brand"}', '{"en":"Named after the city of Sapporo, capital of Hokkaido, where brewing began"}'::jsonb, NULL, '{"en":"Japan''s oldest brand delivers a refined, crisp lager with a polished malt character and clean finish. The distinctive silver can and star logo are recognized worldwide as symbols of Japanese brewing excellence."}'::jsonb, '{"en":"Legendary Biru"}'::jsonb, 4.9, NULL, NULL, 18, NULL, NULL, 3, NULL, NULL, 'straw', 'brilliant', 'medium', 'light', 'bottom_fermented', '{"crisp","clean","refreshing","malty","balanced","smooth"}', '{"en":"Clean and refined with a polished malt character, subtle rice sweetness, and gentle hop bitterness. The finish is crisp and refreshing with no lingering aftertaste."}'::jsonb, '{"en":"Clean grain, subtle malt sweetness, light floral hops"}'::jsonb, NULL, '{"en":"Clean, crisp, and refreshing with subtle malt sweetness"}'::jsonb, NULL, 2, 2, '{"Malt","Rice","Corn starch"}', '{"German and Czech hops"}', 'Sapporo proprietary lager yeast', '{}', NULL, '{}', 'pilsner', 'very_cold', 2, 5, '{"en":"Pour into a chilled glass at a 45° angle for optimal foam"}'::jsonb, true, NULL, '{"japanese-food","sushi","grilled-dishes","seafood"}', '{"en":"Excellent with Japanese cuisine: sushi, ramen, yakitori, tempura. The clean finish complements delicate flavors without overwhelming them."}'::jsonb, '{}', '{"Japanese","Asian","Seafood"}', NULL, '{"all_year","summer"}', '{"dinner","casual","party"}', false, false, false, true, '{"can","bottle","draft"}', '{"330","350","500","650"}', '{}', 'year_round', 'mid', 82, 'https://www.sapporobeer.com', 'Official Sapporo website and Japanese brewing heritage', 1)
