VALUES ('5eaa54d5-3e50-4ffc-be6b-926b49d9b25d', 'carlsberg', 'carlsberg-danish-pilsner', '{"en":"Carlsberg"}'::jsonb, 'international_classic', 'lager', 'pilsner', '{"danish","premium","scientific-brewing","pure-yeast","green-bottle"}', 'Denmark', 'DK', NULL, 'Copenhagen', '{"en":"Carlsberg Brewery"}'::jsonb, 1847, 'macro', false, '1847', '{"en":"J.C. Jacobsen founded Carlsberg in 1847, naming it after his son Carl and the Danish word \"bjerg\" (hill) where the brewery was built. In 1883, Carlsberg scientist Emil Christian Hansen isolated the first pure lager yeast strain (Saccharomyces carlsbergensis), revolutionizing brewing worldwide. Jacobsen gave this yeast freely to the brewing industry, an unprecedented act of scientific generosity."}'::jsonb, '{"World Beer Cup","European Beer Star"}', '{"en":"Named after founder''s son Carl + Danish \"bjerg\" (hill)"}'::jsonb, '{"en":"Carlsberg''s isolation of pure lager yeast in 1883 transformed brewing from an art into a science"}'::jsonb, '{"en":"A classic Danish pilsner known for its balanced, refreshing taste and rich brewing heritage. The brewery''s contribution to brewing science - isolating pure lager yeast - changed beer production forever."}'::jsonb, '{"en":"Probably the best beer in the world"}'::jsonb, 5, NULL, NULL, 22, NULL, NULL, 4, NULL, NULL, 'gold', 'brilliant', 'medium', 'light', 'bottom_fermented', '{"crisp","balanced","refreshing","hoppy","clean"}', '{"en":"Well-balanced with a pleasant hop bitterness, subtle malt sweetness, and a clean, refreshing finish. A textbook example of a European pilsner."}'::jsonb, '{"en":"Floral hop notes with clean malt backbone"}'::jsonb, NULL, '{"en":"Clean, crisp finish with mild hop bitterness"}'::jsonb, NULL, 2, 2, '{"Pilsner malt"}', '{"European hops"}', 'Saccharomyces carlsbergensis (original pure lager strain)', '{}', NULL, '{}', 'pilsner', 'cold', 4, 6, NULL, true, NULL, '{"seafood","danish-food","light-dishes"}', '{"en":"Excellent with Danish smørrebrød, seafood, light salads, and grilled chicken."}'::jsonb, '{}', '{"Danish","Scandinavian","Seafood"}', NULL, '{"all_year","summer"}', '{"casual","party","sports"}', false, false, false, true, '{"bottle","can","draft"}', '{"330","500"}', '{}', 'year_round', 'mid', 85, 'https://www.carlsberg.com', 'Official Carlsberg website and brewing science heritage', 1)
