VALUES ('30881a74-a22c-4d4e-9448-62884404169b', 'tsingtao', 'tsingtao-chinese-lager', '{"en":"Tsingtao"}'::jsonb, 'international_classic', 'lager', 'pilsner', '{"chinese","german-heritage","rice","qingdao","asian"}', 'China', 'CN', 'Shandong Province', 'Qingdao', '{"en":"Tsingtao Brewery"}'::jsonb, 1903, 'macro', false, '1903', '{"en":"Tsingtao was founded in 1903 by German and British settlers in the German colonial territory of Qingdao (then spelled Tsingtao). The Germans brought their brewing expertise and established the brewery using the famous Laoshan spring water. After WWI, the brewery passed through Japanese and then Chinese ownership. Today it''s China''s second-largest brewery and the most exported Chinese beer globally."}'::jsonb, '{}', '{"en":"Named after Qingdao city (Wade-Giles romanization: Tsingtao)"}'::jsonb, NULL, '{"en":"China''s most famous export beer, brewed with German techniques and Laoshan spring water. A crisp, clean lager with subtle rice character that pairs beautifully with Chinese cuisine."}'::jsonb, NULL, 4.7, NULL, NULL, 18, NULL, NULL, 3, NULL, NULL, 'straw', 'brilliant', 'medium', 'light', 'bottom_fermented', '{"crisp","clean","light","refreshing","slightly_sweet"}', '{"en":"Light and crisp with subtle rice sweetness, mild hop character, and a clean, refreshing finish. The Laoshan water gives it a distinctive mineral quality."}'::jsonb, '{"en":"Light grain with subtle floral notes"}'::jsonb, NULL, '{"en":"Clean and crisp with minimal aftertaste"}'::jsonb, NULL, 1, 2, '{"Barley malt","Rice"}', '{"German and Chinese hops"}', 'German-origin lager yeast', '{}', 'Laoshan spring water', '{}', 'pilsner', 'very_cold', 2, 5, NULL, true, NULL, '{"chinese-food","dim-sum","seafood","spicy-food"}', '{"en":"The classic pairing with Chinese cuisine: dim sum, Peking duck, kung pao chicken, seafood dishes. The clean finish refreshes the palate between bites of rich or spicy food."}'::jsonb, '{}', '{"Chinese","Asian","Seafood","Spicy food"}', NULL, '{"all_year","summer"}', '{"dinner","casual","party"}', false, false, false, true, '{"bottle","can"}', '{"330","500","640"}', '{}', 'year_round', 'value', 80, 'https://www.tsingtaobeer.com', 'Official Tsingtao website', 1)
