INSERT INTO beers (id, slug, stable_key, name, status, style_category, style, tags, origin_country, origin_country_code, origin_region, origin_city, brewery, brewery_founded, brewery_type, is_trappist, first_brewed, history_story, history_awards, history_named_after, history_significance, description, tagline, abv, abv_min, abv_max, ibu, ibu_min, ibu_max, srm, og, fg, color, clarity, carbonation, body, fermentation, flavor_profile, taste_description, taste_aroma, taste_first_impression, taste_finish, taste_balance, bitterness_level, sweetness_level, ingredients_malts, ingredients_hops, ingredients_yeast, ingredients_adjuncts, ingredients_water, ingredients_special, serving_glass, serving_temperature, serving_temp_min, serving_temp_max, serving_pouring_notes, serving_head_retention, serving_ideal_head, pairing_food_categories, pairing_food, pairing_cheese, pairing_cuisine, pairing_avoid, season_tags, occasion_tags, is_gluten_free, is_non_alcoholic, is_organic, is_vegan, available_formats, available_sizes, related_beers, availability, price_tier, popularity, source_url, source_note, version)
VALUES ('f50ae976-db8f-4848-83cb-ec6df384d472', 'westvleteren-12', 'westvleteren-12-trappist', '{"en":"Westvleteren 12"}'::jsonb, 'trappist', 'belgian', 'belgian_strong_dark', '{"trappist","authentic","abbey","quadrupel","rare","cellar-worthy","world-class"}', 'Belgium', 'BE', 'West Flanders', 'Westvleteren', '{"en":"Saint Sixtus Abbey"}'::jsonb, 1838, 'trappist', true, '1940s', '{"en":"Westvleteren 12 is brewed by Trappist monks at Saint Sixtus Abbey since the 1940s. Often rated as the best beer in the world, it remains extremely limited in production as the monks brew only what they need to sustain the monastery. The beer can only be purchased by reservation at the abbey gate or at the In de Vrede café across the street. The monks reject commercial distribution to maintain their contemplative lifestyle."}'::jsonb, '{"RateBeer Best Beer in the World (multiple years)","International Trappist Association certified"}', NULL, '{"en":"Consistently ranked among the world''s best beers, representing the pinnacle of Trappist brewing tradition"}'::jsonb, '{"en":"The legendary Westvleteren 12, often called the best beer in the world. This extraordinarily complex quadrupel showcases dark fruit, chocolate, and spice notes with perfect balance. Extremely limited availability makes it one of the most sought-after beers globally."}'::jsonb, '{"en":"The Best Beer in the World"}'::jsonb, 10.2, NULL, NULL, 38, NULL, NULL, 32, NULL, NULL, 'dark_brown', 'slightly_hazy', 'medium', 'full', 'top_fermented', '{"complex","fruity","rich","malty","spicy","warming","smooth"}', '{"en":"Extraordinarily complex and balanced. Rich dark fruit (plum, fig, raisin, dark cherry), dark chocolate, toffee, and subtle coffee notes. Belgian yeast brings clove and pepper spice. Despite high ABV, remarkably smooth with no alcohol burn. The finish is long, warming, and perfectly balanced between sweet and bitter."}'::jsonb, '{"en":"Rich dark fruits, chocolate, caramel, subtle roast, Belgian yeast spice, and warming alcohol"}'::jsonb, '{"en":"Luxurious dark fruit richness with chocolate complexity"}'::jsonb, '{"en":"Exceptionally long and warming with perfect balance of sweet fruit, chocolate, and gentle bitterness"}'::jsonb, '{"en":"Perfect harmony between malt sweetness, dark fruit, and subtle hop bitterness"}'::jsonb, 2, 4, '{"Pale malt","Munich malt","Caramel malt"}', '{"Styrian Goldings","Hallertau"}', 'Westvleteren house yeast', '{}', NULL, '{"Belgian candi sugar"}', 'goblet', 'cellar', 12, 14, '{"en":"Pour carefully into a goblet or chalice. Leave sediment in bottle unless desired. Allow to warm slightly to release full complexity. Swirl gently to enhance aromatics."}'::jsonb, true, '2 fingers', '{"cheese","game","rich-dishes","desserts"}', '{"en":"Pairs beautifully with strong aged cheeses (Stilton, aged Gouda), game meats, wild boar, venison, beef bourguignon, and dark chocolate desserts. Also excellent with Belgian chocolate truffles."}'::jsonb, '{"Stilton","Aged Gouda","Roquefort","Chimay"}', '{"Belgian","French","Game dishes"}', NULL, '{"autumn","winter","christmas"}', '{"celebration","special-occasion","tasting","contemplation"}', false, false, false, false, '{"bottle"}', '{"330"}', '{"westvleteren-8","westvleteren-blonde","chimay-blue"}', 'limited', 'ultra_premium', 95, 'https://www.sintsixtus.be', 'Saint Sixtus Abbey official website and International Trappist Association', 1)
