INSERT INTO beers (id, slug, stable_key, name, status, style_category, style, tags, origin_country, origin_country_code, origin_region, origin_city, brewery, brewery_founded, brewery_type, is_trappist, first_brewed, history_story, history_awards, history_named_after, history_significance, description, tagline, abv, abv_min, abv_max, ibu, ibu_min, ibu_max, srm, og, fg, color, clarity, carbonation, body, fermentation, flavor_profile, taste_description, taste_aroma, taste_first_impression, taste_finish, taste_balance, bitterness_level, sweetness_level, ingredients_malts, ingredients_hops, ingredients_yeast, ingredients_adjuncts, ingredients_water, ingredients_special, serving_glass, serving_temperature, serving_temp_min, serving_temp_max, serving_pouring_notes, serving_head_retention, serving_ideal_head, pairing_food_categories, pairing_food, pairing_cheese, pairing_cuisine, pairing_avoid, season_tags, occasion_tags, is_gluten_free, is_non_alcoholic, is_organic, is_vegan, available_formats, available_sizes, related_beers, availability, price_tier, popularity, source_url, source_note, version)
VALUES ('594f9a74-8226-4f60-b427-2ab78d8a4716', 'leffe-blonde', 'leffe-blonde-abbey', '{"en":"Leffe Blonde"}'::jsonb, 'international_classic', 'belgian', 'belgian_blonde', '{"abbey","belgian","blonde","accessible","classic","smooth"}', 'Belgium', 'BE', 'Namur', 'Dinant', '{"en":"Abbaye de Leffe (brewed by AB InBev)"}'::jsonb, 1240, 'abbey', false, '1240', '{"en":"Leffe Abbey was founded in 1152, and the monks began brewing beer around 1240 to provide safe drinking water and sustenance. The abbey survived wars and the French Revolution, but brewing ceased in 1809. In 1952, the abbey licensed the Leffe name to Lootvoet Brewery, which created the modern Leffe Blonde recipe. Today, Leffe is brewed by AB InBev under license from the abbey. While not a Trappist beer, Leffe maintains its abbey heritage and traditional brewing methods."}'::jsonb, '{"World Beer Awards","European Beer Star"}', NULL, '{"en":"One of the most popular and accessible Belgian abbey beers worldwide, introducing millions to Belgian brewing traditions"}'::jsonb, '{"en":"A classic Belgian blonde abbey ale with smooth, accessible flavor. Golden color with balanced fruity sweetness, subtle spice, and gentle bitterness. An excellent introduction to Belgian abbey brewing traditions."}'::jsonb, '{"en":"Abbey Tradition Since 1240"}'::jsonb, 6.6, NULL, NULL, 25, NULL, NULL, 7, NULL, NULL, 'gold', 'clear', 'medium_high', 'medium', 'top_fermented', '{"fruity","smooth","sweet","spicy","malty","balanced","approachable"}', '{"en":"Smooth and balanced with fruity sweetness (banana, honey), subtle spicy notes (clove, vanilla), light caramel malt, and gentle hop bitterness. Clean finish with lingering sweetness and mild spice. Very approachable despite 6.6% ABV."}'::jsonb, '{"en":"Fruity (banana, honey), subtle spice (clove, vanilla), light caramel, gentle malt sweetness"}'::jsonb, '{"en":"Gentle fruity sweetness with smooth, inviting character"}'::jsonb, '{"en":"Clean and smooth with lingering sweetness and subtle spice"}'::jsonb, '{"en":"Balanced between fruity malt sweetness and gentle hop bitterness"}'::jsonb, 2, 3, '{"Pilsner malt","Munich malt"}', '{"Noble hops"}', 'Belgian abbey yeast', '{}', NULL, '{"Candi sugar"}', 'goblet', 'cold', 5, 7, '{"en":"Pour into a chalice or goblet at 45° angle for optimal head formation. The smooth, creamy head is part of the experience."}'::jsonb, true, '2 fingers', '{"cheese","poultry","light-dishes","appetizers"}', '{"en":"Pairs well with mild to medium cheeses (Brie, Gouda), roasted chicken, grilled fish, Belgian waffles, and light pasta dishes. The balanced sweetness complements a wide range of foods."}'::jsonb, '{"Brie","Gouda","Gruyère","Emmental"}', '{"Belgian","French","Mediterranean"}', NULL, '{"all_year","spring","summer"}', '{"casual","dinner","aperitivo","social"}', false, false, false, false, '{"bottle","can","draft"}', '{"330","750"}', '{"leffe-brune","leffe-tripel","grimbergen-blonde"}', 'year_round', 'moderate', 85, 'https://www.leffe.com', 'Official Leffe website and Belgian abbey brewing heritage', 1)
