VALUES ('4ffaf8d5-accb-435f-b633-791bd74b877d', 'sierra-nevada-pale-ale', 'sierra-nevada-pale-ale-craft-ipa', '{"en":"Sierra Nevada Pale Ale"}'::jsonb, 'craft_classic', 'ale', 'american_pale_ale', '{"craft","american","pioneer","hoppy","iconic"}', 'United States', 'US', NULL, 'Chico, California', '{"en":"Sierra Nevada Brewing Co."}'::jsonb, 1980, 'craft', false, '1980', '{"en":"Sierra Nevada Pale Ale is the beer that launched the American craft beer revolution. Founded by Ken Grossman and Paul Camusi in 1980, it was one of the first breweries to use whole-cone Cascade hops, creating a bold, piney flavor that defined the American Pale Ale style. This beer single-handedly inspired countless brewers and remains a benchmark for the style."}'::jsonb, '{"Great American Beer Festival Gold","World Beer Cup Winner"}', '{"en":"Named after the Sierra Nevada mountain range in California"}'::jsonb, NULL, '{"en":"A revolutionary American Pale Ale featuring bold Cascade hops with piney, citrus notes. Medium-bodied with a perfect balance of malt sweetness and hop bitterness, it set the standard for craft beer in America."}'::jsonb, '{"en":"The Original Craft Beer Revolution"}'::jsonb, 5.6, NULL, NULL, 38, NULL, NULL, 11, NULL, NULL, 'amber', 'clear', 'medium', 'medium', 'top_fermented', '{"hoppy","piney","citrus","balanced","crisp"}', '{"en":"Bold pine and citrus hop character balanced by a smooth malt backbone. Cascade hops provide distinctive grapefruit and floral notes with a clean, dry finish."}'::jsonb, '{"en":"Piney Cascade hops with grapefruit, floral notes, and subtle caramel malt"}'::jsonb, NULL, '{"en":"Clean, dry finish with lingering hop bitterness"}'::jsonb, NULL, 4, 2, '{"Pale malt","Caramel malt"}', '{"Cascade","Magnum"}', 'Ale yeast', '{}', NULL, '{}', 'pint', 'cool', 7, 10, '{"en":"Pour into a clean glass at a slight angle, creating a 1-2 finger white head"}'::jsonb, true, NULL, '{"burgers","grilled-meats","spicy-foods"}', '{"en":"Perfect with burgers, grilled salmon, spicy Mexican food, and aged cheddar. The hop bitterness cuts through rich, fatty foods."}'::jsonb, '{"Aged Cheddar","Gruyere","Blue cheese"}', '{"American","Mexican","BBQ"}', NULL, '{"all_year","summer"}', '{"casual","bbq","sports","dinner"}', false, false, false, true, '{"bottle","can","draft"}', '{"355","473"}', '{}', 'year_round', 'mid', 92, 'https://sierranevada.com', 'Official Sierra Nevada website and craft beer history', 1)
