VALUES ('803a67ec-d861-453c-8c4c-789353c70079', 'lagunitas-ipa', 'lagunitas-ipa-craft-ipa', '{"en":"Lagunitas IPA"}'::jsonb, 'craft_classic', 'ale', 'ipa', '{"craft","california","west-coast","hoppy","bold"}', 'United States', 'US', NULL, 'Petaluma, California', '{"en":"Lagunitas Brewing Company"}'::jsonb, 1993, 'craft', false, '1995', '{"en":"Lagunitas IPA was created by founder Tony Magee in the mid-90s during the West Coast IPA explosion. Known for its irreverent labels and bold hop character, Lagunitas became one of the fastest-growing craft breweries in America. This IPA embodies the California craft beer spirit with its aggressive hopping and unapologetic flavor."}'::jsonb, '{"World Beer Cup Medal","Great American Beer Festival Medal"}', '{"en":"Named after Lagunitas Creek in Marin County, California"}'::jsonb, NULL, '{"en":"A well-rounded West Coast IPA with a huge hop profile featuring citrus, pine, and tropical fruit notes. Full-bodied with a strong malt backbone to balance the assertive bitterness."}'::jsonb, '{"en":"Speak of the Devil"}'::jsonb, 6.2, NULL, NULL, 51, NULL, NULL, 9, NULL, NULL, 'golden', 'clear', 'medium', 'medium_full', 'top_fermented', '{"hoppy","citrus","piney","tropical","bitter"}', '{"en":"Explosive hop aroma with grapefruit, pine, and mango. Rich caramel malt sweetness supports the bold hop bitterness. Complex and layered with a dry, clean finish."}'::jsonb, '{"en":"Intense citrus and pine hops with mango, peach, and caramel malt"}'::jsonb, NULL, '{"en":"Long, dry finish with assertive hop bitterness"}'::jsonb, NULL, 5, 3, '{"Two-row pale malt","Caramel malt","Munich malt"}', '{"Cascade","Centennial","Chinook","Simcoe"}', 'Ale yeast', '{}', NULL, '{}', 'pint', 'cool', 7, 10, '{"en":"Pour aggressively to release hop aromatics, creating a thick white head"}'::jsonb, true, NULL, '{"spicy-foods","burgers","indian-cuisine"}', '{"en":"Excellent with spicy Thai or Indian curries, loaded burgers, buffalo wings, and sharp cheddar. The high IBU cuts through heat and fat."}'::jsonb, '{"Sharp Cheddar","Aged Gouda","Blue cheese"}', '{"Indian","Thai","American BBQ"}', NULL, '{"all_year"}', '{"casual","party","dinner","celebration"}', false, false, false, true, '{"bottle","can","draft"}', '{"355","473"}', '{}', 'year_round', 'mid', 88, 'https://lagunitas.com', 'Official Lagunitas website and brewing information', 1)
