VALUES ('88e809da-6a1d-4ff0-8d75-c27910e30f78', 'stone-ipa', 'stone-ipa-craft-ipa', '{"en":"Stone IPA"}'::jsonb, 'craft_classic', 'ale', 'ipa', '{"craft","san-diego","west-coast","hoppy","bold"}', 'United States', 'US', NULL, 'Escondido, California', '{"en":"Stone Brewing"}'::jsonb, 1996, 'craft', false, '1997', '{"en":"Stone IPA was created by Greg Koch and Steve Wagner as part of their mission to bring bold, flavorful beers to the masses. Stone Brewing helped define the San Diego craft beer scene and became known for their aggressive hop-forward beers. The gargoyle logo and \"Arrogant Bastard\" attitude made Stone a craft beer icon."}'::jsonb, '{"World Beer Cup Gold","Great American Beer Festival Gold"}', '{"en":"Named for the solid, uncompromising nature of the brewery''s philosophy"}'::jsonb, NULL, '{"en":"A bold West Coast IPA showcasing intense hop bitterness and flavor. Citrus, pine, and tropical fruit notes dominate, backed by a solid malt foundation. Unfiltered and dry-hopped for maximum hop impact."}'::jsonb, '{"en":"Unapologetically Bold"}'::jsonb, 6.9, NULL, NULL, 77, NULL, NULL, 8, NULL, NULL, 'golden', 'slightly_hazy', 'medium', 'medium_full', 'top_fermented', '{"hoppy","bitter","citrus","piney","resinous"}', '{"en":"Massive hop presence with grapefruit, pine resin, and tropical mango. Firm malt backbone provides structure while letting the hops shine. Aggressively bitter with a bone-dry finish."}'::jsonb, '{"en":"Intense citrus, pine, and tropical hops with subtle caramel malt"}'::jsonb, NULL, '{"en":"Bone-dry, intensely bitter finish with lingering hop resin"}'::jsonb, NULL, 5, 2, '{"Pale malt","Caramel malt"}', '{"Magnum","Chinook","Centennial","Citra"}', 'Ale yeast', '{}', NULL, '{}', 'pint', 'cool', 7, 10, '{"en":"Pour with authority to release hop aromatics and create a creamy head"}'::jsonb, true, NULL, '{"spicy-foods","grilled-meats","mexican"}', '{"en":"Perfect with spicy carnitas tacos, grilled steak, jalapeño poppers, and aged sharp cheeses. The intense bitterness stands up to bold flavors."}'::jsonb, '{"Extra Sharp Cheddar","Aged Manchego","Gorgonzola"}', '{"Mexican","American BBQ","Spicy Asian"}', NULL, '{"all_year"}', '{"casual","bbq","celebration","dinner"}', false, false, false, true, '{"bottle","can","draft"}', '{"355","650"}', '{}', 'year_round', 'mid', 85, 'https://stonebrewing.com', 'Official Stone Brewing website and beer information', 1)
