VALUES ('20e97d3e-3a1d-4085-87c3-2ac78342bf0c', 'dogfish-head-60-minute', 'dogfish-head-60-minute-craft-ipa', '{"en":"Dogfish Head 60 Minute IPA"}'::jsonb, 'craft_classic', 'ale', 'ipa', '{"craft","delaware","east-coast","continually-hopped","innovative"}', 'United States', 'US', NULL, 'Milton, Delaware', '{"en":"Dogfish Head Craft Brewery"}'::jsonb, 1995, 'craft', false, '2003', '{"en":"Dogfish Head 60 Minute IPA pioneered the concept of \"continual hopping\" where hops are added continuously throughout the 60-minute boil rather than in traditional intervals. Founder Sam Calagione used a vibrating football game to mechanically add hops, creating a smoother, less harsh bitterness. This innovative approach helped define East Coast IPAs."}'::jsonb, '{"World Beer Cup Gold","Great American Beer Festival Medal"}', '{"en":"Named after the 60-minute boil time with continuous hop additions"}'::jsonb, NULL, '{"en":"An East Coast IPA featuring continuous hop additions throughout the boil. Balanced and complex with citrus, pine, and caramel malt notes. Less aggressively bitter than West Coast IPAs, showcasing hop flavor over pure bitterness."}'::jsonb, '{"en":"Continually Hopped for Balanced Bitterness"}'::jsonb, 6, NULL, NULL, 60, NULL, NULL, 10, NULL, NULL, 'amber', 'clear', 'medium', 'medium', 'top_fermented', '{"hoppy","balanced","citrus","piney","smooth"}', '{"en":"Smooth hop bitterness from continuous additions creates a balanced profile. Citrus and pine notes blend with caramel malt sweetness. Complex and layered without harsh bitterness."}'::jsonb, '{"en":"Citrus and pine hops with toasted caramel malt and subtle floral notes"}'::jsonb, NULL, '{"en":"Smooth, balanced finish with moderate hop bitterness"}'::jsonb, NULL, 4, 3, '{"Two-row pale malt","Caramel malt"}', '{"Warrior","Amarillo","Simcoe","Cascade"}', 'Ale yeast', '{}', NULL, '{}', 'pint', 'cool', 7, 10, '{"en":"Pour steadily to create a medium white head, releasing hop aromatics"}'::jsonb, true, NULL, '{"seafood","grilled-fish","curry"}', '{"en":"Pairs well with grilled salmon, crab cakes, Thai curry, and aged cheddar. The balanced bitterness complements both seafood and spicy dishes."}'::jsonb, '{"Aged Cheddar","Fontina","Blue cheese"}', '{"Seafood","Thai","American"}', NULL, '{"all_year"}', '{"casual","dinner","celebration","craft-tasting"}', false, false, false, true, '{"bottle","can","draft"}', '{"355","473"}', '{}', 'year_round', 'mid', 90, 'https://dogfish.com', 'Official Dogfish Head website and continual hopping innovation', 1)
