VALUES ('f1ecb70f-9b8b-455e-bdaf-2bc908456d85', 'boddingtons-pub-ale', 'boddingtons-pub-ale-british-ale', '{"en":"Boddingtons Pub Ale"}'::jsonb, 'international_classic', 'ale', 'cream_ale', '{"british","cream-ale","nitro","smooth","manchester"}', 'United Kingdom', 'GB', NULL, 'Manchester', '{"en":"Boddingtons Brewery"}'::jsonb, 1778, 'macro', false, '1778', '{"en":"Founded in 1778 at the Strangeways Brewery in Manchester, Boddingtons became famous for its distinctively smooth and creamy texture. The beer gained national prominence in the 1990s with its memorable \"Cream of Manchester\" advertising campaign. Originally a cask ale, Boddingtons pioneered the use of nitrogen widget technology in cans, creating the signature creamy head that made it a pub favorite across Britain."}'::jsonb, '{"CAMRA Awards","British Brewing Awards"}', '{"en":"Named after the Boddington family, founders of the brewery"}'::jsonb, NULL, '{"en":"A smooth, creamy golden ale famous for its velvety texture and tight, long-lasting head. Known as \"The Cream of Manchester,\" this beer delivers a light, refreshing taste with a distinctive smooth mouthfeel created by nitrogen dispense. The perfect balance of malt sweetness and hop bitterness makes it incredibly drinkable."}'::jsonb, '{"en":"The Cream of Manchester"}'::jsonb, 4.7, NULL, NULL, 20, NULL, NULL, 6, NULL, NULL, 'gold', 'clear', 'medium', 'medium_light', 'top_fermented', '{"smooth","creamy","balanced","malty","refreshing","clean"}', '{"en":"Incredibly smooth and creamy with a light malt sweetness featuring biscuit and bread notes. Gentle hop bitterness provides balance without overpowering. The nitrogen dispense creates a silky, velvety mouthfeel. Light fruit esters add subtle complexity. Clean, refreshing finish with minimal aftertaste."}'::jsonb, '{"en":"Light malt with bready and biscuit notes, subtle hop character, and delicate fruity esters"}'::jsonb, NULL, '{"en":"Smooth, clean finish with a gentle dry note and subtle lingering sweetness"}'::jsonb, NULL, 2, 2, '{"Pale malt","Wheat malt"}', '{"English hops","Fuggles"}', 'English ale yeast', '{}', NULL, '{}', 'pint', 'cool', 6, 8, '{"en":"For canned version: open can, turn upside down into glass for cascading effect. For draft: pour smoothly to create the signature creamy head"}'::jsonb, true, NULL, '{"pub-food","light-dishes","british-classics"}', '{"en":"Excellent with traditional British pub fare: fish and chips, chicken dishes, mild curries, sandwiches, and salads. The smooth, creamy texture pairs well with fried foods. Also complements mild cheeses and light seafood dishes."}'::jsonb, '{"Mild Cheddar","Cheshire","Wensleydale","Lancashire"}', '{"British","Pub fare","Fish and chips","Light meals"}', NULL, '{"all_year","summer"}', '{"casual","pub_night","sports","relaxing","bbq"}', false, false, false, true, '{"can","draft","bottle"}', '{"440","500"}', '{}', 'year_round', 'value', 78, 'https://www.boddingtons.com', 'Official Boddingtons website and brewing heritage information', 1)
