-- 39. Samuel Smith Taddy Porter
INSERT INTO beers (id, slug, stable_key, name, status, style_category, style, tags, origin_country, origin_country_code, origin_region, origin_city, brewery, brewery_founded, brewery_type, is_trappist, first_brewed, history_story, history_awards, history_named_after, history_significance, description, tagline, abv, abv_min, abv_max, ibu, ibu_min, ibu_max, srm, og, fg, color, clarity, carbonation, body, fermentation, flavor_profile, taste_description, taste_aroma, taste_first_impression, taste_finish, taste_balance, bitterness_level, sweetness_level, ingredients_malts, ingredients_hops, ingredients_yeast, ingredients_adjuncts, ingredients_water, ingredients_special, serving_glass, serving_temperature, serving_temp_min, serving_temp_max, serving_pouring_notes, serving_head_retention, serving_ideal_head, pairing_food_categories, pairing_food, pairing_cheese, pairing_cuisine, pairing_avoid, season_tags, occasion_tags, is_gluten_free, is_non_alcoholic, is_organic, is_vegan, available_formats, available_sizes, related_beers, availability, price_tier, popularity, source_url, source_note, version)
VALUES ('a29ab182-d415-45b2-be26-a3c719c5df71', 'samuel-smith-taddy-porter', 'samuel-smith-taddy-porter-british-ale', '{"en":"Samuel Smith Taddy Porter"}'::jsonb, 'craft', 'stout_porter', 'porter', '{"british","porter","traditional","yorkshire","classic"}', 'United Kingdom', 'GB', NULL, 'Tadcaster', '{"en":"Samuel Smith Old Brewery"}'::jsonb, 1758, 'regional', false, '1758', '{"en":"Samuel Smith''s Old Brewery in Tadcaster, Yorkshire is Britain''s oldest brewery, dating back to 1758. Taddy Porter represents the brewery''s commitment to traditional methods, using the same Yorkshire Square fermentation system and water from the original well. The brewery still employs coopers to maintain traditional oak casks and uses shire horses for local deliveries. This porter is brewed exactly as it was centuries ago, making it one of the most authentic traditional English porters available."}'::jsonb, '{"Great British Beer Festival Awards","World Beer Awards"}', '{"en":"Named after the town of Tadcaster (nicknamed \"Taddy\") where the brewery is located"}'::jsonb, NULL, '{"en":"A classic English porter brewed using traditional Yorkshire Square fermentation. This deep, dark beer showcases rich roasted malt character with notes of chocolate, coffee, and dried fruit. Smooth and full-bodied with a perfect balance of sweetness and roasted bitterness, representing the authentic taste of historic English porter."}'::jsonb, '{"en":"Traditional Yorkshire Porter"}'::jsonb, 5, NULL, NULL, 30, NULL, NULL, 40, NULL, NULL, 'dark_brown', 'opaque', 'medium', 'medium_full', 'top_fermented', '{"roasty","chocolate","coffee","malty","smooth","complex","fruity"}', '{"en":"Rich and complex with dominant roasted malt flavors of dark chocolate, espresso, and burnt caramel. Subtle notes of dried fruit, licorice, and toffee add depth. The Yorkshire Square fermentation imparts a distinctive fruity character. Smooth, creamy mouthfeel with a perfect balance between malt sweetness and roasted bitterness."}'::jsonb, '{"en":"Rich roasted malt with chocolate, coffee, and caramel notes; subtle dark fruit and licorice undertones"}'::jsonb, NULL, '{"en":"Long, smooth finish with lingering roasted notes and subtle sweetness; dry but not astringent"}'::jsonb, NULL, 3, 3, '{"Pale malt","Crystal malt","Chocolate malt","Black malt"}', '{"Fuggles","Goldings"}', 'Samuel Smith house yeast (Yorkshire Square strain)', '{}', 'Original well water from Tadcaster', '{}', 'pint', 'cellar', 10, 13, '{"en":"Pour gently into a tilted pint glass to minimize foam, then straighten to create a modest tan head. Allow to warm slightly for fuller flavor development."}'::jsonb, true, NULL, '{"roasted-meats","hearty-dishes","desserts","cheese"}', '{"en":"Exceptional with rich, hearty foods: roasted meats, steak and kidney pie, venison, game birds, and aged cheeses. The roasted character pairs beautifully with chocolate desserts, tiramisu, and coffee-based sweets. Also excellent with oysters, smoked fish, and barbecued meats."}'::jsonb, '{"Stilton","Aged Cheddar","Wensleydale","Caerphilly","Roquefort"}', '{"British","Game meats","Chocolate desserts","BBQ"}', NULL, '{"all_year","autumn","winter"}', '{"dinner","pairing_dinner","relaxing","tasting","pub_night"}', false, false, false, true, '{"bottle","draft","cask"}', '{"330","550"}', '{}', 'year_round', 'craft', 70, 'https://www.samuelsmithsbrewery.co.uk', 'Official Samuel Smith Brewery website and traditional brewing methods documentation', 1)
