-- 10. Clover Club


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('93080495-f739-403c-a649-4a7cc0ef77fc', 'clover-club', 'ae84232a865262ae3e01e4f44a89d23e8cd42f53', '{"en":"Clover Club"}'::jsonb, 'iba_official', 'Unforgettables', '{"iba","official","classic","fruity","pre-prohibition","egg-white","pink"}', '{"en":"An elegant pre-Prohibition cocktail with a beautiful pink color and silky foam from egg white. The Clover Club combines gin with raspberry and lemon for a perfectly balanced, sophisticated drink that''s fruity without being sweet."}'::jsonb, '{"created_year":"1900s","origin":{"city":"Philadelphia","bar":"Bellevue-Stratford Hotel","country":"USA"},"story":{"en":"The Clover Club originated in the pre-Prohibition era at the Bellevue-Stratford Hotel in Philadelphia, where a men''s club of the same name met. The Clover Club was an exclusive gentlemen''s club of lawyers, writers, and businessmen who gathered to discuss literature and politics. The pink, frothy cocktail became their signature drink. After Prohibition and the rise of \"girly drink\" stigma, the cocktail fell out of favor, unfairly dismissed as too feminine. It was rediscovered in the craft cocktail renaissance of the 2000s and is now rightfully celebrated as a sophisticated classic."},"named_after":{"en":"Named after the Clover Club, an exclusive Philadelphia gentlemen''s club that met at the Bellevue-Stratford Hotel from the 1880s to 1920s."}}'::jsonb, '{"profile":["fruity","sour","dry","refreshing"],"description":{"en":"Bright and refreshing with a silky texture from the egg white. The raspberry provides fruitiness without cloying sweetness, while lemon adds tartness and gin contributes botanical backbone. The foam creates a luxurious mouthfeel."},"first_impression":{"en":"Bright raspberry and citrus with silky foam"},"finish":{"en":"Clean, dry finish with lingering berry notes"},"balance":{"en":"Perfectly balanced between fruit sweetness and citrus tartness, with gin providing structure"}}'::jsonb, '{"best_time":["evening"],"occasions":["date_night","celebration","brunch"],"seasons":["spring","summer"],"food_pairings":{"en":"Pairs beautifully with light appetizers, seafood, and berry desserts. Excellent brunch cocktail alongside eggs Benedict or fruit plates."},"ideal_for":{"en":"Anyone who enjoys sours and gin cocktails. Perfect for those who appreciate texture in their drinks. A great introduction to egg white cocktails."}}'::jsonb, '[{"ingredient_id":"ING_GIN_LONDON_DRY","quantity":{"amount":45,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_RASPBERRY_SYRUP","quantity":{"amount":15,"unit":"ml"},"display_name":"Raspberry Syrup","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh Lemon Juice","notes":null},{"ingredient_id":"ING_EGG_WHITE","quantity":{"amount":15,"unit":"ml"},"display_name":"Egg White","notes":"Few drops"}]'::jsonb, 'shake', '{"en":"Pour all ingredients into a cocktail shaker. Dry shake (without ice) vigorously to emulsify the egg white. Add ice and shake again until well chilled. Strain into a chilled cocktail glass. Garnish with fresh raspberries."}'::jsonb, 'Cocktail glass', '{"en":"Fresh raspberries"}'::jsonb, 'none', 'up', '{"ING_GIN_LONDON_DRY"}', '{"fruity","sour","dry","refreshing"}', 20, 165, 'medium', 90, '{"eggs","sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","gluten-free","dairy-free"}', '{"spring","summer"}', '{"date_night","celebration","brunch"}', false, false, '{"clover-club-royal"}', 'The dry shake is essential for the silky foam. Offer aquafaba for vegan guests. Fresh raspberries for garnish make it special. Don''t skip the egg white - it transforms the texture completely.', 'mid', 68, 'https://iba-world.com/clover-club/', 'IBA Official Recipe. Pre-Prohibition classic from Philadelphia.', 1)
