-- 14. Gin Fizz


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('901ee2bf-f842-4965-9b94-563eef2ea34c', 'gin-fizz', '07545457d85ab8218eb1134e55a665e36183885b', '{"en":"Gin Fizz"}'::jsonb, 'iba_official', 'Unforgettables', '{"iba","official","classic","refreshing"}', '{"en":"A refreshing classic from the golden age of cocktails. The Gin Fizz combines gin, fresh lemon juice, and sugar, topped with sparkling soda water for an effervescent, citrusy delight."}'::jsonb, '{"created_year":"1870","origin":{"city":"New Orleans","bar":"Various establishments","country":"United States"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The Gin Fizz became widely popular in America between 1900 and the 1940s, known as a hometown specialty of New Orleans. The fizz is a variation on the older sours family of cocktails, distinguished by the addition of carbonated water. Bars would employ teams of bartenders that would take turns shaking the drinks to keep up with demand. The most famous variant, the Ramos Gin Fizz, was created in 1888 by Henry C. Ramos at the Imperial Cabinet Saloon on Gravier Street. Before Prohibition, the Ramos version''s 12-minute mixing time required over 20 bartenders working simultaneously during busy periods."},"named_after":{"en":"Named for the \"fizz\" created by the soda water, which distinguishes it from the traditional sour."}}'::jsonb, '{"profile":["citrus","refreshing","effervescent"],"description":{"en":"A bright, refreshing cocktail that balances the botanical complexity of gin with tart lemon and subtle sweetness. The soda water adds a light, effervescent quality that makes it exceptionally refreshing and easy to drink."},"first_impression":{"en":"Bright citrus bursts forward with the lively fizz of soda, followed by the crisp botanical notes of gin. The drink is light and refreshing from the first sip."},"finish":{"en":"Clean and crisp with lingering citrus and a dry, botanical finish. The bubbles provide a refreshing lift that cleanses the palate."},"balance":{"en":"Well-balanced between tart lemon acidity, gentle sweetness, and gin''s botanical character. The soda water lightens the drink without diluting its flavor."}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["brunch","garden_party","summer","casual"],"seasons":["spring","summer"],"food_pairings":{"en":"Light seafood, fresh salads, grilled vegetables, lemon tarts, citrus-based desserts. Perfect with brunch dishes like eggs Benedict or smoked salmon."},"ideal_for":{"en":"Those seeking a refreshing, effervescent cocktail with balanced citrus flavors. Perfect for warm weather or as a revitalizing afternoon drink."}}'::jsonb, '[{"ingredient_id":"ING_GIN_LONDON_DRY","quantity":{"amount":45,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":30,"unit":"ml"},"display_name":"Fresh Lemon Juice","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":10,"unit":"ml"},"display_name":"Simple Syrup","notes":null},{"ingredient_id":"ING_SODA_WATER","quantity":{"amount":80,"unit":"ml"},"display_name":"Soda Water","notes":null}]'::jsonb, 'shake', '{"en":"Shake all ingredients with ice except soda water. Pour into thin tall tumbler glass, top with a splash of soda water. Serve without ice."}'::jsonb, 'Highball glass', '{"en":"Lemon slice with optional lemon zest."}'::jsonb, 'none', 'straight', '{"ING_GIN_LONDON_DRY"}', '{"citrus","refreshing","effervescent","light"}', 10, 150, 'easy', 180, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"brunch","garden_party","casual"}', false, false, '{"Ramos Gin Fizz","Silver Fizz","Golden Fizz"}', 'Always use fresh-squeezed lemon juice. Shake vigorously to properly chill and dilute. Add soda water gently after pouring to maintain carbonation. The drink should be served immediately while still fizzy.', 'mid', 75, 'https://iba-world.com/gin-fizz/', 'IBA Official Recipe.', 1)
