-- 24. Negroni


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('8a145bac-ece9-4d62-a98c-4b45c7542715', 'negroni', 'negroni', '{"en":"Negroni"}'::jsonb, 'iba_official', 'Unforgettables', '{"iba","official","classic","italian","bitter","aperitivo"}', '{"en":"The Negroni is a bold, bitter Italian aperitivo cocktail. Perfectly balanced with equal parts gin, Campari, and sweet vermouth, it''s a sophisticated drink that embodies la dolce vita. Voted the most ordered cocktail in the world in 2022."}'::jsonb, '{"created_year":"1919","origin":{"city":"Florence","bar":"Caffè Casoni (now Caffè Giacosa)","country":"Italy"},"creator":{"name":"Fosco Scarselli","profession":"bartender"},"story":{"en":"In 1919 at Caffè Casoni in Florence, Count Camillo Negroni asked bartender Fosco Scarselli to strengthen his favorite cocktail, the Americano, by replacing soda water with gin. Scarselli obliged and also added an orange garnish instead of the traditional lemon to signify the variation. The drink became so popular among other patrons requesting \"Count Negroni''s drink\" that it simply became known as the Negroni. The flamboyant count had returned from America when Prohibition began, having lived as a cowboy in the Wild West and a gambler in New York."},"named_after":{"en":"Named after Count Camillo Negroni, the customer for whom the drink was created in 1919."}}'::jsonb, '{"profile":["bitter","sweet","herbal","complex"],"description":{"en":"The Negroni delivers a complex interplay of flavors. Campari provides distinctive bitter orange notes, the gin adds botanical complexity and juniper backbone, while sweet vermouth brings herbal sweetness and depth. The equal proportions create perfect harmony between bitter and sweet."},"first_impression":{"en":"Immediately bold and bitter with Campari''s distinctive orange peel bitterness, followed quickly by gin''s botanical notes and vermouth''s sweet complexity."},"finish":{"en":"Long, warming finish with persistent bitter orange notes and herbal complexity. The sweetness fades gradually, leaving pleasant bitterness that stimulates the appetite."},"balance":{"en":"Masterfully balanced with the classic 1:1:1 ratio. The three components create synergy where bitter Campari, aromatic gin, and sweet vermouth achieve perfect equilibrium."}}'::jsonb, '{"best_time":["pre-dinner","aperitivo","evening"],"occasions":["aperitivo","date_night","social_gathering"],"seasons":["all_year"],"food_pairings":{"en":"Perfect aperitivo pairing with Italian antipasti: olives, cured meats, aged cheeses, bruschetta, and arancini. The bitterness prepares the palate for a meal."},"ideal_for":{"en":"Perfect for aperitivo hour, those who appreciate bitter flavors, cocktail enthusiasts, and anyone seeking an authentic Italian drinking experience. The world''s most ordered cocktail in 2022."}}'::jsonb, '[{"ingredient_id":"ING_GIN_LONDON_DRY","quantity":{"amount":30,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_CAMPARI","quantity":{"amount":30,"unit":"ml"},"display_name":"Bitter Campari","notes":null},{"ingredient_id":"ING_VERMOUTH_SWEET","quantity":{"amount":30,"unit":"ml"},"display_name":"Sweet Red Vermouth","notes":null}]'::jsonb, 'build', '{"en":"Pour all ingredients directly into an old fashioned glass filled with ice. Stir gently to combine and chill. Express the oils from an orange peel over the drink, then use the peel as garnish."}'::jsonb, 'Old fashioned glass', '{"en":"Half orange slice"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_GIN_LONDON_DRY"}', '{"bitter","sweet","herbal","complex"}', 24, 195, 'easy', 45, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"all_year"}', '{"aperitivo","date_night","social_gathering"}', false, false, '{}', 'The classic 1:1:1 ratio is essential - never deviate. Use quality Campari (no substitutions for authentic Negroni). Fresh ice is important as dilution affects the drink significantly. Build directly in the glass - no shaking or straining needed. Popular variations include Negroni Sbagliato (prosecco instead of gin) and White Negroni (Suze and Lillet Blanc). June is celebrated as Negroni Week globally.', 'mid', 95, 'https://iba-world.com/negroni/', 'IBA Official Recipe. Historical information from Caffè Giacosa, Florence, and cocktail historians. Voted most ordered cocktail worldwide in 2022 by Drinks International.', 1)
