-- 26. Paradise


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('dc50cf9b-c2f9-4a25-b9d1-0247575b1355', 'paradise', 'f7e6d5c4b3a29281706958473625140abcdef123', '{"en":"Paradise"}'::jsonb, 'iba_official', 'Unforgettables', '{"iba","official","classic","fruity","citrus","vintage"}', '{"en":"A delightful Prohibition-era cocktail that combines gin''s botanical character with the stone-fruit sweetness of apricot brandy and bright citrus. One of the original IBA cocktails, the Paradise offers a perfect balance of fruity and herbal notes."}'::jsonb, '{"created_year":"1930","origin":{"city":"London","bar":"Savoy Hotel","country":"UK"},"creator":{"name":"Harry Craddock","profession":"bartender"},"story":{"en":"The Paradise cocktail was created by legendary bartender Harry Craddock and appeared in his famous 1930 \"Savoy Cocktail Book.\" It has been part of the IBA cocktail list since its inception—included in the first official IBA Cocktail List of 50 cocktails created and approved in 1961 in Norway. The drink uses the classic 2:1:1 ratio of gin to apricot brandy to orange juice, making it easy to remember and scale."},"named_after":{"en":"Named to evoke the blissful, heavenly combination of flavors."}}'::jsonb, '{"profile":["fruity","citrus","botanical"],"description":{"en":"Bright and fruity with apricot sweetness balanced by citrus acidity and gin''s botanical backbone. The orange juice provides body while lemon adds zing, creating a harmonious and refreshing cocktail."},"first_impression":{"en":"Sweet apricot and orange greet you first, followed by botanical gin"},"finish":{"en":"Clean, citrusy finish with lingering apricot sweetness"},"balance":{"en":"Well-balanced between sweet fruit and tart citrus"}}'::jsonb, '{"best_time":["afternoon","early_evening","aperitivo"],"occasions":["aperitivo","brunch","garden_party","summer_gathering"],"seasons":["spring","summer"],"food_pairings":{"en":"Excellent with light appetizers, fruit salads, grilled chicken, or seafood. Pairs well with Mediterranean cuisine and fresh salads."},"ideal_for":{"en":"Perfect for gin lovers who enjoy fruity cocktails. Ideal for those seeking a refreshing, not-too-sweet drink with classic pedigree."}}'::jsonb, '[{"ingredient_id":"ING_GIN","quantity":{"amount":40,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_APRICOT_BRANDY","quantity":{"amount":20,"unit":"ml"},"display_name":"Apricot brandy","notes":null},{"ingredient_id":"ING_ORANGE_JUICE","quantity":{"amount":20,"unit":"ml"},"display_name":"Fresh orange juice","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":10,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null}]'::jsonb, 'shake', '{"en":"Pour all ingredients into cocktail shaker, shake well with ice, strain into chilled cocktail glass."}'::jsonb, 'Cocktail glass (Martini)', '{"en":"Orange twist or lemon twist"}'::jsonb, 'none', 'up', '{"ING_GIN"}', '{"fruity","citrus","botanical"}', 18, 160, 'easy', 60, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"aperitivo","brunch","garden_party"}', false, false, '{"aviation","clover-club","white-lady"}', 'Use fresh citrus juices only - bottled will not do. The 2:1:1 ratio is easy to remember. Quality apricot brandy makes a big difference. Shake well for proper dilution and to integrate fruit juices.', 'mid', 50, 'https://iba-world.com/iba-cocktail/paradise/', 'IBA Official Recipe. Historical information from Harry Craddock''s "Savoy Cocktail Book" (1930).', 1)
