-- 28. Porto Flip


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('7039b884-7868-403a-9610-902b90a9b250', 'porto-flip', 'c7d6e5f4a3b2c1d0e9f8a7b6c5d4e3f2a1b0c9d8', '{"en":"Porto Flip"}'::jsonb, 'iba_official', 'Unforgettables', '{"iba","official","classic","creamy","rich","after-dinner","historical"}', '{"en":"A rich, velvety historical cocktail combining brandy, ruby port, and egg yolk. Dating back to the 19th century, the Porto Flip is silky smooth, decadent, and warming - a luxurious after-dinner indulgence topped with freshly grated nutmeg."}'::jsonb, '{"created_year":"1887","origin":{"city":"Unknown","bar":"Unknown","country":"USA/UK"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The Porto Flip has deep roots dating back to the 19th century. The name \"Porto\" clearly nods to its main ingredient, Ruby Port, a fortified wine from Portugal. This drink first appeared in renowned bartending manuals of the time, including Jerry Thomas''s 1887 book \"How to Mix Drinks or The Bon Vivant''s Companion.\" \"Flip\" cocktails were a popular category in the 1800s, characterized by the addition of a whole egg or egg yolk, which creates a rich, frothy texture. The Porto Flip is a historical after-dinner cocktail that showcases the elegance of classic mixology."},"named_after":{"en":"Named after Porto (Port wine) and the \"flip\" style of cocktails which include egg."}}'::jsonb, '{"profile":["rich","creamy","sweet"],"description":{"en":"Rich and luxurious with velvety egg yolk creating a silky texture. The brandy provides warmth and depth, while ruby port adds sweet, fruity notes with hints of berries and spice. Nutmeg on top adds aromatic complexity. Decadent and satisfying."},"first_impression":{"en":"Rich, creamy texture with sweet port and aromatic nutmeg"},"finish":{"en":"Long, warming finish with lingering port sweetness and spice"},"balance":{"en":"Rich and indulgent with balanced sweetness and warmth"}}'::jsonb, '{"best_time":["after_dinner","late_night","dessert"],"occasions":["digestivo","nightcap","special_occasion","celebration"],"seasons":["autumn","winter"],"food_pairings":{"en":"Perfect with desserts like chocolate cake, tiramisu, or crème brûlée. Also pairs with nuts, dried fruits, aged cheese, or as a dessert replacement."},"ideal_for":{"en":"Perfect for those who enjoy rich, creamy after-dinner drinks. Ideal for brandy and port lovers seeking something unique and historical. Great for adventurous drinkers willing to try egg-based cocktails."}}'::jsonb, '[{"ingredient_id":"ING_BRANDY","quantity":{"amount":15,"unit":"ml"},"display_name":"Brandy","notes":null},{"ingredient_id":"ING_RUBY_PORT","quantity":{"amount":45,"unit":"ml"},"display_name":"Ruby port","notes":null},{"ingredient_id":"ING_EGG_YOLK","quantity":{"amount":1,"unit":"whole"},"display_name":"Egg yolk","notes":null}]'::jsonb, 'shake', '{"en":"Pour all ingredients into cocktail shaker, shake well with ice, strain into chilled cocktail glass. Sprinkle with fresh ground nutmeg."}'::jsonb, 'Cocktail glass (Coupe)', '{"en":"Freshly grated nutmeg"}'::jsonb, 'none', 'up', '{"ING_BRANDY","ING_RUBY_PORT"}', '{"rich","creamy","sweet"}', 14, 200, 'medium', 90, '{"egg","sulphites"}', '{"egg_intolerance","alcohol","sulphites_intolerance"}', '{"vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","gluten-free","dairy-free"}', '{"autumn","winter"}', '{"digestivo","nightcap","special_occasion"}', false, false, '{"sherry-flip","brandy-flip","alexander"}', 'Use fresh egg yolk only. Shake vigorously to properly emulsify the yolk. Ruby port is traditional - tawny port can also be used. Freshly grate nutmeg over the top just before serving. Some variations use whole egg instead of just yolk.', 'mid', 40, 'https://iba-world.com/iba-cocktail/porto-flip/', 'IBA Official Recipe. Historical information from Jerry Thomas "How to Mix Drinks" (1887) and 19th century cocktail documentation.', 1)
