-- 29. Ramos Gin Fizz


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('d11061d4-cfa2-4e8a-990d-10e4522221ad', 'ramos-gin-fizz', 'd9c8b7a6f5e4d3c2b1a098765432109fedcba987', '{"en":"Ramos Gin Fizz"}'::jsonb, 'iba_official', 'Unforgettables', '{"iba","official","classic","creamy","refreshing","fizz","labor-intensive"}', '{"en":"A legendary New Orleans classic known for its rich, creamy texture and refreshing citrus flavor. The Ramos Gin Fizz requires vigorous shaking to achieve its signature frothy, meringue-like consistency. Often called \"the workout in a glass,\" it''s one of the most labor-intensive cocktails."}'::jsonb, '{"created_year":"1888","origin":{"city":"New Orleans","bar":"Imperial Cabinet Saloon","country":"USA"},"creator":{"name":"Henry Charles Ramos","profession":"bartender"},"story":{"en":"The Ramos Gin Fizz was created in 1888 by Henry Charles Ramos at the Imperial Cabinet Saloon on the corner of Gravier and Carondelet Streets in New Orleans, Louisiana. Originally named the \"New Orleans Fizz,\" Henry used to call his famous creation \"the One And Only One.\" According to legend, Ramos himself routinely employed up to 30 bartenders dedicated solely to shaking up the drink, sometimes for 12 minutes apiece, to achieve the perfect consistency. The drink became so popular that during Mardi Gras, the bar had a dedicated \"shaker boy\" assembly line."},"named_after":{"en":"Named after its creator, Henry Charles Ramos."}}'::jsonb, '{"profile":["creamy","citrus","floral"],"description":{"en":"Silky smooth and cloud-like with a perfect balance of tart citrus, floral orange blossom, and creamy richness. The gin provides botanical backbone while the egg white creates an ethereal, meringue-like texture. Vanilla adds subtle sweetness."},"first_impression":{"en":"Fluffy, cloud-like foam with bright citrus and delicate orange blossom aroma"},"finish":{"en":"Clean, refreshing finish with lingering floral notes and gentle cream"},"balance":{"en":"Perfectly balanced between rich creaminess and bright, refreshing citrus"}}'::jsonb, '{"best_time":["brunch","afternoon","early_evening"],"occasions":["brunch","celebration","special_occasion","impressive"],"seasons":["spring","summer"],"food_pairings":{"en":"Perfect with brunch dishes like eggs Benedict, French toast, or fresh fruit. Also pairs well with light seafood, salads, or as a palate cleanser between courses."},"ideal_for":{"en":"Perfect for gin lovers who appreciate classic cocktails and don''t mind waiting for their drink. Ideal for special occasions when you want to impress guests with both flavor and presentation."}}'::jsonb, '[{"ingredient_id":"ING_GIN","quantity":{"amount":45,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":30,"unit":"ml"},"display_name":"Sugar syrup","notes":null},{"ingredient_id":"ING_HEAVY_CREAM","quantity":{"amount":60,"unit":"ml"},"display_name":"Cream","notes":null},{"ingredient_id":"ING_EGG_WHITE","quantity":{"amount":30,"unit":"ml"},"display_name":"Egg white","notes":null},{"ingredient_id":"ING_ORANGE_FLOWER_WATER","quantity":{"amount":3,"unit":"dash"},"display_name":"Orange flower water","notes":null},{"ingredient_id":"ING_VANILLA_EXTRACT","quantity":{"amount":2,"unit":"dash"},"display_name":"Vanilla extract","notes":null},{"ingredient_id":"ING_SODA_WATER","quantity":{"amount":30,"unit":"ml"},"display_name":"Soda water","notes":null}]'::jsonb, 'shake', '{"en":"Pour all ingredients except soda water in a cocktail shaker with ice. Shake for two minutes, double strain in a glass, pour the drink back in the shaker and hard shake without ice for one minute. Strain into a highball glass, top up with soda."}'::jsonb, 'Highball glass', '{"en":"None (the foam crown is the garnish)"}'::jsonb, 'none', 'straight_up', '{"ING_GIN"}', '{"creamy","citrus","floral"}', 8, 320, 'hard', 240, '{"egg","milk","sulphites"}', '{"egg_intolerance","lactose","alcohol","sulphites_intolerance"}', '{"vegetarian","pescatarian","gluten_free","nut_free"}', 0, '{"vegetarian","gluten-free"}', '{"spring","summer"}', '{"brunch","celebration","special_occasion"}', false, false, '{"gin-fizz","silver-fizz","royal-fizz"}', 'This is the most labor-intensive cocktail on the menu. Requires 3 minutes of vigorous shaking total. Use fresh egg white for best foam. Orange flower water is essential - don''t substitute. The double shake method (with ice, then without) is critical for proper texture. Soda should be added last and very gently to preserve the foam crown.', 'premium', 70, 'https://iba-world.com/iba-cocktail/ramos-fizz/', 'IBA Official Recipe. Historical information from New Orleans cocktail history and Henry Charles Ramos documentation.', 1)
