-- 32. Sazerac


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('0cc34d51-78a3-4f96-93d4-03b822e7257c', 'sazerac', 'a5b4c3d2e1f0a9b8c7d6e5f4a3b2c1d0e9f8a7b6', '{"en":"Sazerac"}'::jsonb, 'iba_official', 'Unforgettables', '{"iba","official","classic","new-orleans","stirred","strong","official-cocktail"}', '{"en":"New Orleans'' official cocktail, the Sazerac is a bold, spirit-forward classic combining cognac or rye whiskey with Peychaud''s bitters, sugar, and an absinthe rinse. One of America''s oldest cocktails, it embodies the soul of the Crescent City."}'::jsonb, '{"created_year":"1838","origin":{"city":"New Orleans","bar":"Sazerac Coffee House","country":"USA"},"creator":{"name":"Antoine Amédée Peychaud","profession":"pharmacist/bartender"},"story":{"en":"The Sazerac is a local variation of a cognac or whiskey cocktail originally from New Orleans, named for the Sazerac de Forge et Fils brand of cognac that served as its original main ingredient. Created around 1838 by Antoine Amédée Peychaud, a Creole apothecary who invented Peychaud''s bitters. The original recipe changed after the American Civil War when rye whiskey substituted cognac as it became hard to obtain. In 2008, the Louisiana Legislature declared the Sazerac as New Orleans'' official cocktail. The Sazerac Coffee House, where it was popularized, is now a protected historic site."},"named_after":{"en":"Named after Sazerac de Forge et Fils, the cognac brand originally used in the drink."}}'::jsonb, '{"profile":["complex","herbal","anise"],"description":{"en":"Bold and complex with anise and herbal notes from absinthe dominating the aroma, followed by the rich warmth of cognac or rye. Peychaud''s bitters provide distinctive fruity-floral complexity. The sugar softens the edges while lemon oils add brightness."},"first_impression":{"en":"Anise and herbal aromatics, followed by bold spirit and bitters"},"finish":{"en":"Long, warming finish with lingering anise, bitters, and lemon"},"balance":{"en":"Spirit-forward with aromatic complexity from absinthe and bitters"}}'::jsonb, '{"best_time":["aperitivo","evening","pre_dinner"],"occasions":["aperitivo","contemplative","sophisticated_gathering","celebration"],"seasons":["autumn","winter","spring","summer"],"food_pairings":{"en":"Excellent with rich Creole cuisine like gumbo, jambalaya, or blackened fish. Pairs well with oysters, spicy foods, or as a powerful aperitif before a hearty meal."},"ideal_for":{"en":"Perfect for whiskey or cognac enthusiasts who appreciate bold, aromatic cocktails with historical significance. Ideal for those who enjoy spirit-forward drinks with complex flavor profiles."}}'::jsonb, '[{"ingredient_id":"ING_COGNAC","quantity":{"amount":50,"unit":"ml"},"display_name":"Cognac","notes":null},{"ingredient_id":"ING_ABSINTHE","quantity":{"amount":10,"unit":"ml"},"display_name":"Absinthe","notes":null},{"ingredient_id":"ING_SUGAR_CUBE","quantity":{"amount":1,"unit":"cube"},"display_name":"Sugar cube","notes":null},{"ingredient_id":"ING_PEYCHAUDS_BITTERS","quantity":{"amount":2,"unit":"dash"},"display_name":"Peychaud''s bitters","notes":null}]'::jsonb, 'stir', '{"en":"Rinse a chilled old-fashioned glass with the absinthe, add crushed ice and set it aside. Stir the remaining ingredients over ice in a mixing glass. Discard the ice and any excess absinthe from the prepared glass, strain the mixed drink into the glass. Add the lemon peel for garnish."}'::jsonb, 'Old Fashioned glass', '{"en":"Lemon peel"}'::jsonb, 'none', 'up', '{"ING_COGNAC"}', '{"complex","herbal","anise"}', 35, 150, 'medium', 120, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"autumn","winter","spring","summer"}', '{"aperitivo","contemplative","sophisticated_gathering"}', false, false, '{"rye-sazerac","old-fashioned","vieux-carre"}', 'The absinthe rinse is essential - coat the glass and discard excess. Peychaud''s bitters are specific and non-negotiable. Traditional recipe uses cognac, but rye whiskey is also acceptable (and often preferred in New Orleans today). Express lemon oils over drink but traditionally don''t drop the peel in.', 'premium', 70, 'https://iba-world.com/iba-cocktail/sazerac/', 'IBA Official Recipe. Historical information from New Orleans cocktail history and Antoine Amédée Peychaud documentation.', 1)
