-- 35. Stinger


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('187178fd-f36a-4c76-9685-a701f6bfaa2f', 'stinger', 'f4a3b2c1d0e9f8a7b6c5d4e3f2a1b0c9d8e7f6a5', '{"en":"Stinger"}'::jsonb, 'iba_official', 'Unforgettables', '{"iba","official","classic","after-dinner","digestif","two-ingredient"}', '{"en":"A sophisticated two-ingredient after-dinner cocktail combining cognac with white crème de menthe. The Stinger is refreshingly minty yet warming - a classic digestif that''s both simple and elegant, perfect for ending an evening on a refined note."}'::jsonb, '{"created_year":"1890s","origin":{"city":"New York City","bar":"Unknown","country":"USA"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The Stinger likely originated in the 1890s and became particularly popular during Prohibition (1920-1933) when the mint helped mask inferior spirits. The drink gained significant cultural cache in the mid-20th century, becoming the preferred nightcap of high society and appearing in numerous films and novels. It was famously enjoyed by characters in works by F. Scott Fitzgerald and Evelyn Waugh. The simplicity of just two ingredients belies its sophisticated appeal."},"named_after":{"en":"Named for the \"sting\" of mint combined with the warmth of brandy."}}'::jsonb, '{"profile":["minty","warming","smooth"],"description":{"en":"Cool, refreshing mint balanced by warming cognac. The white crème de menthe provides clean, crisp mint flavor without heaviness, while the cognac adds depth, warmth, and complexity. Smooth and sophisticated."},"first_impression":{"en":"Cool mint followed immediately by warming brandy"},"finish":{"en":"Long, warming finish with lingering mint freshness"},"balance":{"en":"Perfect balance between cooling mint and warming cognac"}}'::jsonb, '{"best_time":["after_dinner","late_night","nightcap"],"occasions":["digestivo","nightcap","sophisticated_gathering","date_night"],"seasons":["autumn","winter","spring","summer"],"food_pairings":{"en":"Perfect as a digestif after dinner, especially after rich meals. Pairs well with dark chocolate, chocolate mint desserts, or as a palate cleanser between courses."},"ideal_for":{"en":"Perfect for cognac lovers seeking a refreshing twist. Ideal for those who enjoy after-dinner drinks and appreciate the combination of mint and brandy."}}'::jsonb, '[{"ingredient_id":"ING_COGNAC","quantity":{"amount":50,"unit":"ml"},"display_name":"Cognac","notes":null},{"ingredient_id":"ING_CREME_DE_MENTHE_WHITE","quantity":{"amount":20,"unit":"ml"},"display_name":"White crème de menthe","notes":null}]'::jsonb, 'stir', '{"en":"Pour all ingredients into mixing glass with ice cubes. Stir well. Strain into chilled martini cocktail glass."}'::jsonb, 'Cocktail glass (Martini)', '{"en":"None (or mint leaf optional)"}'::jsonb, 'none', 'up', '{"ING_COGNAC"}', '{"minty","warming","smooth"}', 30, 160, 'easy', 60, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"autumn","winter","spring","summer"}', '{"digestivo","nightcap","date_night"}', false, false, '{"vodka-stinger","brandy-alexander","grasshopper"}', 'Use white crème de menthe (not green) for a clear appearance. Can be served on the rocks if preferred. Quality cognac is important - the simplicity means there''s nowhere to hide. Some guests may request it shaken for extra chill.', 'mid', 50, 'https://iba-world.com/iba-cocktail/stinger/', 'IBA Official Recipe. Historical information from cocktail history sources and literary references.', 1)
