-- 42. Black Russian


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('7924dad6-510c-4a86-a703-bc0e530a81e3', 'black-russian', 'black_russian_iba_contemporary_classic', '{"en":"Black Russian"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","simple","coffee","after-dinner","spirit-forward"}', '{"en":"A simple yet sophisticated cocktail combining vodka with coffee liqueur. Strong, smooth, and dangerously easy-drinking - the Black Russian is the perfect after-dinner indulgence."}'::jsonb, '{"created_year":"1949","origin":{"city":"Brussels","bar":"Hotel Metropole","country":"Belgium"},"creator":{"name":"Gustave Tops","profession":"bartender"},"story":{"en":"Created by bartender Gustave Tops at the Hotel Metropole in Brussels, Belgium in 1949. It was created for Perle Mesta, the U.S. Ambassador to Luxembourg. The drink gained popularity during the Cold War era, and its name reflects the Russian origins of vodka. The White Russian variation (adding cream) came later."},"named_after":{"en":"Named \"Black Russian\" for the Russian origins of vodka and the dark color from coffee liqueur."}}'::jsonb, '{"profile":["coffee","sweet","boozy"],"description":{"en":"Rich coffee flavor with smooth vodka backbone. Kahlúa provides sweet coffee notes while vodka adds clean strength. Smooth, slightly sweet, and deceptively strong."},"first_impression":{"en":"Sweet coffee with smooth vodka strength"},"finish":{"en":"Long, warming finish with lingering coffee notes"},"balance":{"en":"Well-balanced between sweet coffee liqueur and clean vodka"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["after-dinner","nightcap","digestivo","casual"],"seasons":["autumn","winter","spring","summer"],"food_pairings":{"en":"Perfect after dinner or with desserts, especially chocolate, tiramisu, or coffee-flavored sweets."},"ideal_for":{"en":"Perfect for coffee and vodka lovers seeking a simple, strong cocktail. Ideal for those who prefer spirit-forward drinks."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":50,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_COFFEE_LIQUEUR","quantity":{"amount":20,"unit":"ml"},"display_name":"Coffee liqueur (Kahlúa)","notes":null}]'::jsonb, 'build', '{"en":"Pour ingredients into an old fashioned glass filled with ice cubes. Stir gently. No garnish required."}'::jsonb, 'Old Fashioned glass', '{"en":"None"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_VODKA"}', '{"coffee","sweet","boozy"}', 28, 220, 'easy', 30, '{"sulphites"}', '{"alcohol","sulphites_intolerance","caffeine"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"autumn","winter","spring","summer"}', '{"after-dinner","nightcap","digestivo","casual"}', false, false, '{"white-russian","dirty-russian","blind-russian"}', 'Simple two-ingredient build. The White Russian adds cream on top. Kahlúa is the traditional coffee liqueur, but any good coffee liqueur works. Can be served neat or on the rocks. Deceptively strong - warn guests.', 'low', 70, 'https://iba-world.com/black-russian/', 'IBA Official Recipe.', 1)
