-- 43. Bloody Mary


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('7cb9421d-e800-4ece-b9a6-67186eba7f52', 'bloody-mary', 'bloody_mary_iba_contemporary_2025', '{"en":"Bloody Mary"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","savory","brunch","spicy","complex"}', '{"en":"The ultimate brunch cocktail, combining vodka with tomato juice, lemon juice, and savory spices. Famous for its complex, savory flavor and reputation as a hangover cure. Endlessly customizable with garnishes."}'::jsonb, '{"created_year":"1921","origin":{"city":"Paris","bar":"Harry''s New York Bar","country":"France"},"creator":{"name":"Fernand Petiot","profession":"bartender"},"story":{"en":"Created by Fernand Petiot at Harry''s New York Bar in Paris in 1921, mixing vodka with tomato juice. When Petiot moved to New York''s King Cole Bar in the 1930s, he refined the recipe with spices, creating the modern Bloody Mary. The drink became synonymous with brunch culture and hangover remedies."},"named_after":{"en":"Named after Queen Mary I of England (aka \"Bloody Mary\") or a waitress named Mary at a Chicago bar. Origins are disputed."}}'::jsonb, '{"profile":["savory","spicy","umami","citrus"],"description":{"en":"Complex, savory, and thoroughly unique among cocktails. Tomato juice provides umami richness, lemon adds brightness, Worcestershire and Tabasco create depth and heat, while celery salt ties everything together."},"first_impression":{"en":"Savory tomato with immediate spice and acidity"},"finish":{"en":"Long, savory finish with lingering heat and umami"},"balance":{"en":"Complex balance of savory, spicy, sour, and salty elements"}}'::jsonb, '{"best_time":["brunch","morning","afternoon"],"occasions":["brunch","casual","recovery"],"seasons":["all_seasons"],"food_pairings":{"en":"Perfect with brunch dishes: eggs Benedict, bacon, breakfast sandwiches, oysters, shrimp cocktail. Often served as a meal replacement with elaborate garnishes."},"ideal_for":{"en":"Perfect for brunch lovers and those who appreciate savory cocktails. Famous as a hangover remedy. Not for those who prefer sweet drinks."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":45,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_TOMATO_JUICE","quantity":{"amount":90,"unit":"ml"},"display_name":"Tomato juice","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null},{"ingredient_id":"ING_WORCESTERSHIRE","quantity":{"amount":2,"unit":"dash"},"display_name":"Worcestershire sauce","notes":null},{"ingredient_id":"ING_TABASCO","quantity":{"amount":2,"unit":"dash"},"display_name":"Tabasco sauce","notes":null},{"ingredient_id":"ING_CELERY_SALT","quantity":{"amount":1,"unit":"pinch"},"display_name":"Celery salt","notes":null},{"ingredient_id":"ING_BLACK_PEPPER","quantity":{"amount":1,"unit":"pinch"},"display_name":"Black pepper","notes":null}]'::jsonb, 'roll', '{"en":"Pour all ingredients into a highball glass. Roll gently (pour back and forth between glasses). Garnish with celery stalk and lemon wedge. Season to taste."}'::jsonb, 'Highball glass', '{"en":"Celery stalk and lemon wedge"}'::jsonb, 'cubes', 'built', '{"ING_VODKA"}', '{"savory","spicy","umami","citrus"}', 12, 125, 'easy', 60, '{"sulphites","celery","fish"}', '{"alcohol","sulphites_intolerance","nightshades","histamine"}', '{"vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 2, '{"vegetarian","gluten-free","dairy-free"}', '{"all_seasons"}', '{"brunch","casual","recovery"}', false, false, '{"virgin-mary","bloody-caesar","red-snapper","michelada"}', 'Highly customizable - ask customer about spice preference. Common garnishes include bacon, shrimp, pickles, olives, cheese. Rolling (not shaking) preserves texture. Worcestershire contains anchovies (not vegetarian).', 'low', 90, 'https://iba-world.com/bloody-mary/', 'IBA Official Recipe.', 1)
