-- 46. Champagne Cocktail


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('f1140af4-efb5-4df6-88f2-1a7eaba7d764', 'champagne-cocktail', 'champagne_cocktail_iba_contemporary_2025', '{"en":"Champagne Cocktail"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","champagne","elegant","celebratory","classic"}', '{"en":"An elegant classic combining champagne with a sugar cube, Angostura bitters, and cognac. Simple yet sophisticated, this drink elevates champagne with subtle sweetness and aromatic complexity. Perfect for celebrations."}'::jsonb, '{"created_year":"1862","origin":{"country":"USA"},"story":{"en":"The Champagne Cocktail first appeared in Jerry Thomas''s 1862 \"How to Mix Drinks\" bartending guide, making it one of the oldest recorded cocktails. It gained popularity in high society and became synonymous with elegance and celebration. The drink was famously enjoyed in the film \"Casablanca\" (1942) and has remained a symbol of sophistication ever since."},"named_after":{"en":"Named for its primary ingredient, champagne."}}'::jsonb, '{"profile":["bubbly","sweet","aromatic","elegant"],"description":{"en":"Elegant and effervescent. The sugar cube slowly dissolves, creating a gradient of sweetness. Angostura bitters add aromatic spice, cognac provides depth, and champagne delivers crisp bubbles. A refined drinking experience."},"first_impression":{"en":"Aromatic bitters and champagne bubbles"},"finish":{"en":"Clean, crisp finish with lingering aromatic notes"},"balance":{"en":"Well-balanced between champagne dryness and subtle sweetness"}}'::jsonb, '{"best_time":["aperitivo","evening"],"occasions":["celebration","wedding","new_years","formal_event"],"seasons":["all_seasons"],"food_pairings":{"en":"Perfect with oysters, caviar, canapés, smoked salmon, or light appetizers. Excellent for toasting special occasions."},"ideal_for":{"en":"Perfect for celebrations, weddings, New Year''s Eve, and formal events. Ideal for those who appreciate elegant, classic cocktails."}}'::jsonb, '[{"ingredient_id":"ING_CHAMPAGNE","quantity":{"amount":90,"unit":"ml"},"display_name":"Champagne","notes":null},{"ingredient_id":"ING_COGNAC","quantity":{"amount":10,"unit":"ml"},"display_name":"Cognac","notes":null},{"ingredient_id":"ING_ANGOSTURA_BITTERS","quantity":{"amount":2,"unit":"dash"},"display_name":"Angostura bitters","notes":null},{"ingredient_id":"ING_SUGAR_CUBE","quantity":{"amount":1,"unit":"cube"},"display_name":"Sugar cube","notes":null}]'::jsonb, 'build', '{"en":"Place sugar cube in a chilled champagne flute. Saturate the sugar cube with Angostura bitters. Add cognac. Top with chilled champagne. Garnish with orange twist or lemon twist."}'::jsonb, 'Champagne flute', '{"en":"Orange twist or lemon twist"}'::jsonb, 'none', 'built', '{"ING_CHAMPAGNE","ING_COGNAC"}', '{"bubbly","sweet","aromatic","elegant"}', 13, 120, 'easy', 30, '{"sulphites"}', '{"alcohol","sulphites_intolerance","histamine"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"all_seasons"}', '{"celebration","wedding","new_years","formal_event"}', false, false, '{"seelbach-cocktail","champagne-royale"}', 'Use quality champagne or good sparkling wine. Saturate sugar cube fully with bitters for best flavor. Build directly in flute to preserve bubbles. Can substitute brandy for cognac if needed.', 'high', 75, 'https://iba-world.com/champagne-cocktail/', 'IBA Official Recipe. First recorded in Jerry Thomas 1862.', 1)
