-- 48. Cosmopolitan


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('18d6b555-a91a-4108-8255-56d6a323d822', 'cosmopolitan', 'cosmopolitan_iba_contemporary_classic', '{"en":"Cosmopolitan"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","modern-classic","citrus","tart","elegant","iconic"}', '{"en":"The quintessential modern cocktail - vodka, Cointreau, cranberry, and lime in perfect harmony. Made famous by Sex and the City, the Cosmopolitan is sophisticated, pink, and utterly chic."}'::jsonb, '{"created_year":"1987","origin":{"city":"Miami / New York","bar":"Multiple claims","country":"USA"},"creator":{"name":"Toby Cecchini (popularized)","profession":"bartender"},"story":{"en":"While several bartenders claim to have invented the Cosmopolitan in the 1970s-80s, it was Toby Cecchini at The Odeon in Manhattan who created the modern version in 1987-88 using Absolut Citron. The drink exploded in popularity after being featured regularly on HBO''s Sex and the City (1998-2004), becoming synonymous with urban sophistication."},"named_after":{"en":"Named \"Cosmopolitan\" to evoke sophistication, worldliness, and urban elegance."}}'::jsonb, '{"profile":["citrus","tart","fruity"],"description":{"en":"Bright, tart, and perfectly balanced. Cranberry provides tartness and color, lime adds citrus zing, Cointreau brings orange sweetness, and vodka keeps it clean and crisp. Sophisticated and refreshing."},"first_impression":{"en":"Tart cranberry and bright citrus with orange notes"},"finish":{"en":"Clean, crisp finish with lingering citrus"},"balance":{"en":"Perfectly balanced between tart, sweet, and citrus"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["date_night","celebration","girls_night","cocktail_party"],"seasons":["spring","summer","autumn","winter"],"food_pairings":{"en":"Perfect with appetizers, sushi, seafood, light salads, and sophisticated finger foods."},"ideal_for":{"en":"Perfect for anyone seeking a sophisticated, stylish cocktail. The ultimate choice for cosmopolitan urbanites and cocktail connoisseurs."}}'::jsonb, '[{"ingredient_id":"ING_VODKA_CITRON","quantity":{"amount":40,"unit":"ml"},"display_name":"Vodka Citron","notes":null},{"ingredient_id":"ING_COINTREAU","quantity":{"amount":15,"unit":"ml"},"display_name":"Cointreau","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_CRANBERRY_JUICE","quantity":{"amount":30,"unit":"ml"},"display_name":"Cranberry juice","notes":null}]'::jsonb, 'shake', '{"en":"Pour all ingredients into a cocktail shaker filled with ice cubes. Shake well. Strain into a chilled cocktail glass. Garnish with a flamed orange peel or lime wheel."}'::jsonb, 'Cocktail glass (Martini)', '{"en":"Flamed orange peel or lime wheel"}'::jsonb, 'none', 'up', '{"ING_VODKA_CITRON"}', '{"citrus","tart","fruity"}', 18, 160, 'easy', 60, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer","autumn","winter"}', '{"date_night","celebration","girls_night","cocktail_party"}', false, false, '{"metropolitan","flirtini","white-cosmopolitan"}', 'Use Absolut Citron or similar citrus vodka for authenticity. Fresh lime juice is essential. Shake hard for proper dilution. The drink should be a distinctive pink color. Flame the orange peel for added aromatics.', 'mid', 90, 'https://iba-world.com/cosmopolitan/', 'IBA Official Recipe. Made famous by Sex and the City.', 1)
