-- 49. Cuba Libre


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('2fd916ac-c347-4fce-8b0f-f3dfa21f2a16', 'cuba-libre', 'cuba_libre_iba_contemporary_2025', '{"en":"Cuba Libre"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","refreshing","highball","easy","summer"}', '{"en":"A refreshing highball combining white rum, cola, and fresh lime juice. More than just a \"rum and coke,\" the Cuba Libre''s addition of lime transforms it into a balanced, citrus-forward cocktail with historical significance."}'::jsonb, '{"created_year":"1900","origin":{"city":"Havana","country":"Cuba"},"story":{"en":"The Cuba Libre was born during the Spanish-American War around 1900 in Havana. American soldiers mixed their Coca-Cola (newly arrived in Cuba) with local rum and lime, creating this iconic drink. The name means \"Free Cuba\" in Spanish, celebrating Cuba''s independence from Spain. The drink became a symbol of the Cuban independence movement and American-Cuban friendship of that era."},"named_after":{"en":"Named \"Cuba Libre\" (Free Cuba) to commemorate Cuba''s independence from Spain and the end of Spanish colonial rule."}}'::jsonb, '{"profile":["sweet","refreshing","citrus"],"description":{"en":"Refreshing and balanced. The sweetness of cola is lifted by bright lime acidity, while rum provides a smooth alcoholic backbone. The lime makes all the difference, elevating this beyond a simple mixed drink."},"first_impression":{"en":"Bright lime and cola sweetness hit immediately"},"finish":{"en":"Clean, refreshing finish with lingering citrus notes"},"balance":{"en":"Well-balanced between sweet cola, tart lime, and smooth rum"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["casual","party","beach","bbq"],"seasons":["spring","summer"],"food_pairings":{"en":"Perfect with Cuban sandwiches, grilled meats, BBQ ribs, burgers, tacos, and spicy foods. The sweetness and citrus cut through rich, fatty foods beautifully."},"ideal_for":{"en":"Perfect for casual gatherings, beach parties, and anyone who wants an easy-drinking, refreshing cocktail. Great for those new to rum cocktails."}}'::jsonb, '[{"ingredient_id":"ING_RUM_WHITE","quantity":{"amount":50,"unit":"ml"},"display_name":"White rum","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":12,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_COLA","quantity":{"amount":120,"unit":"ml"},"display_name":"Cola","notes":null}]'::jsonb, 'build', '{"en":"Build all ingredients in a highball glass filled with ice. Pour the lime juice and rum directly over ice, top with cola. Stir gently. Garnish with a lime wedge."}'::jsonb, 'Highball glass', '{"en":"Lime wedge"}'::jsonb, 'cubes', 'built', '{"ING_RUM_WHITE"}', '{"sweet","refreshing","citrus"}', 12, 180, 'easy', 30, '{"sulphites"}', '{"alcohol","caffeine","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"casual","party","beach","bbq"}', false, false, '{"rum-and-coke","cuba-libre-premium"}', 'The lime is what differentiates a Cuba Libre from a simple rum and coke - never skip it. Use fresh lime juice, not bottled. Build in glass to preserve carbonation. Mexican Coke (made with cane sugar) is preferred by some bartenders.', 'low', 95, 'https://iba-world.com/cuba-libre/', 'IBA Official Recipe. Historical information from Cuban cocktail history sources.', 1)
