INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('3bc42586-3f4a-4d7d-8609-96b6f002c1f7', 'french-connection', 'french_connection_iba_contemporary_classic', '{"en":"French Connection"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","after-dinner","digestivo","simple","spirit-forward"}', '{"en":"A sophisticated after-dinner drink combining cognac with Amaretto. Simple yet elegant, this two-ingredient cocktail delivers rich almond sweetness balanced by smooth brandy warmth."}'::jsonb, '{"created_year":"1970","origin":{"city":"Unknown","bar":"Unknown","country":"USA"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The French Connection gained popularity in the 1970s, possibly inspired by the 1971 film of the same name starring Gene Hackman. The combination of French cognac and Italian amaretto creates an international \"connection\" that delivers sophisticated simplicity."},"named_after":{"en":"Likely named after the 1971 crime film \"The French Connection,\" though the exact origin is disputed."}}'::jsonb, '{"profile":["sweet","nutty","boozy"],"description":{"en":"Rich and warming with prominent almond sweetness from Amaretto balanced by the complex, oaky depth of cognac. Smooth, sippable, and luxurious - perfect for slow enjoyment."},"first_impression":{"en":"Sweet almond and warm cognac with oak notes"},"finish":{"en":"Long, warming finish with lingering almond and oak"},"balance":{"en":"Well-balanced between sweet amaretto and dry cognac"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["digestivo","nightcap","relaxation","celebration"],"seasons":["autumn","winter"],"food_pairings":{"en":"Perfect as a digestif after dinner. Pairs well with chocolate desserts, biscotti, or almond-based sweets. Also excellent with coffee."},"ideal_for":{"en":"Ideal for those who appreciate simple, spirit-forward cocktails. Perfect for cognac and amaretto lovers seeking an elegant nightcap."}}'::jsonb, '[{"ingredient_id":"ING_COGNAC","quantity":{"amount":35,"unit":"ml"},"display_name":"Cognac","notes":null},{"ingredient_id":"ING_AMARETTO","quantity":{"amount":35,"unit":"ml"},"display_name":"Amaretto","notes":null}]'::jsonb, 'build', '{"en":"Build into an old fashioned glass filled with ice. Stir gently. No garnish required."}'::jsonb, 'Old Fashioned glass', '{"en":"None"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_COGNAC"}', '{"sweet","nutty","boozy"}', 30, 200, 'easy', 30, '{"sulphites","tree_nuts"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","pescatarian","gluten_free","dairy_free"}', 0, '{"vegetarian","gluten-free","dairy-free"}', '{"autumn","winter"}', '{"digestivo","nightcap","relaxation","celebration"}', false, false, '{"italian-connection","godfather"}', 'Equal parts cognac and amaretto. Can be served neat or on the rocks. The Godfather is a similar drink using scotch instead of cognac. Simple build - no shaking required.', 'mid', 55, 'https://iba-world.com/french-connection/', 'IBA Official Recipe.', 1)
