-- 57. Irish Coffee


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('1e1679d2-8551-4480-af15-61c58a7023f0', 'irish-coffee', 'irish_coffee_iba_contemporary_classic', '{"en":"Irish Coffee"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","hot","coffee","irish","after-dinner","winter"}', '{"en":"The perfect marriage of hot coffee, Irish whiskey, and cream. Warming, comforting, and sophisticated - this iconic Irish creation is the ultimate cold-weather indulgence."}'::jsonb, '{"created_year":"1943","origin":{"city":"Foynes","bar":"Foynes Airbase Restaurant","country":"Ireland"},"creator":{"name":"Joe Sheridan","profession":"chef"},"story":{"en":"Created by chef Joe Sheridan at Foynes Airbase (now Shannon Airport) in Ireland during winter 1943. When a Pan Am flying boat returned to port due to bad weather, Sheridan added whiskey to the passengers'' coffee to warm them. When asked if it was Brazilian coffee, he replied, \"No, that''s Irish coffee!\" The drink was later popularized in America by travel writer Stanton Delaplane and the Buena Vista Cafe in San Francisco."},"named_after":{"en":"Named \"Irish Coffee\" by its creator Joe Sheridan to distinguish it from regular coffee."}}'::jsonb, '{"profile":["coffee","creamy","warm"],"description":{"en":"Rich coffee with smooth Irish whiskey warmth, balanced by sweet brown sugar and topped with luxurious cream. The cream should be sipped through, creating a perfect contrast between hot coffee and cold cream."},"first_impression":{"en":"Hot coffee and whiskey warmth with cold cream"},"finish":{"en":"Long, warming finish with lingering coffee and whiskey"},"balance":{"en":"Perfect balance between coffee bitterness, whiskey warmth, and cream richness"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["after-dinner","nightcap","winter_gathering","digestivo"],"seasons":["autumn","winter"],"food_pairings":{"en":"Perfect after dinner, especially with chocolate desserts, Irish desserts like bread pudding, or as a standalone digestif."},"ideal_for":{"en":"Perfect for coffee and whiskey lovers seeking warmth and comfort. Ideal for cold evenings, après-ski, or any time you need warming up."}}'::jsonb, '[{"ingredient_id":"ING_IRISH_WHISKEY","quantity":{"amount":40,"unit":"ml"},"display_name":"Irish whiskey","notes":null},{"ingredient_id":"ING_HOT_COFFEE","quantity":{"amount":80,"unit":"ml"},"display_name":"Hot coffee","notes":null},{"ingredient_id":"ING_HEAVY_CREAM","quantity":{"amount":30,"unit":"ml"},"display_name":"Fresh cream","notes":null},{"ingredient_id":"ING_BROWN_SUGAR","quantity":{"amount":1,"unit":"tsp"},"display_name":"Brown sugar","notes":null}]'::jsonb, 'build', '{"en":"Warm an Irish coffee glass with hot water, then empty. Pour in hot coffee and dissolve brown sugar. Add Irish whiskey and stir. Top with lightly whipped cream by pouring it over the back of a spoon so it floats on top. Do not stir after adding cream."}'::jsonb, 'Irish coffee glass', '{"en":"None (cream topping)"}'::jsonb, 'none', 'hot', '{"ING_IRISH_WHISKEY"}', '{"coffee","creamy","warm"}', 8, 210, 'medium', 120, '{"milk","sulphites"}', '{"lactose","alcohol","sulphites_intolerance","caffeine"}', '{"vegetarian","pescatarian","gluten_free","nut_free"}', 0, '{"vegetarian","gluten-free"}', '{"autumn","winter"}', '{"after-dinner","nightcap","winter_gathering","digestivo"}', false, false, '{"baileys-coffee","spanish-coffee","italian-coffee"}', 'The cream must float on top - lightly whip it to proper consistency (not too thick) and pour over the back of a spoon. Pre-warm the glass. Use good quality Irish whiskey and fresh, hot coffee. The drink is meant to be sipped through the cream, not stirred.', 'mid', 85, 'https://iba-world.com/irish-coffee/', 'IBA Official Recipe. Created by Joe Sheridan in 1943.', 1)
