-- 58. Kir


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('88eef8d8-1d28-4c41-9dd1-1fe6e53ebd81', 'kir', 'kir_iba_contemporary_classic', '{"en":"Kir"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","french","aperitif","wine-based","elegant","simple"}', '{"en":"A classic French apéritif combining dry white wine with a touch of crème de cassis. Elegant, simple, and quintessentially French - the perfect way to begin an evening."}'::jsonb, '{"created_year":"1945","origin":{"city":"Dijon","bar":"Unknown","country":"France"},"creator":{"name":"Félix Kir","profession":"mayor"},"story":{"en":"Named after Félix Kir, the mayor of Dijon, France, who popularized the drink after World War II. Originally called \"blanc-cassis\" (white-cassis), it was renamed in his honor. Kir served it to visiting dignitaries to promote local Burgundian products - Aligoté wine and crème de cassis from blackcurrants."},"named_after":{"en":"Named after Félix Kir, the mayor of Dijon who popularized the drink."}}'::jsonb, '{"profile":["fruity","dry","light"],"description":{"en":"Light, crisp, and refreshing with subtle blackcurrant sweetness complementing the dry white wine. Elegant and easy-drinking with beautiful berry aromatics."},"first_impression":{"en":"Bright blackcurrant with crisp white wine"},"finish":{"en":"Clean, dry finish with lingering berry notes"},"balance":{"en":"Well-balanced between dry wine and sweet cassis"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["aperitif","casual","outdoor","french_dining"],"seasons":["spring","summer","autumn"],"food_pairings":{"en":"Perfect as an apéritif before dinner. Pairs well with French cheeses, charcuterie, olives, and light appetizers."},"ideal_for":{"en":"Ideal for wine lovers seeking a lighter apéritif. Perfect for those who appreciate French elegance and simplicity."}}'::jsonb, '[{"ingredient_id":"ING_DRY_WHITE_WINE","quantity":{"amount":90,"unit":"ml"},"display_name":"Dry white wine (preferably Aligoté)","notes":null},{"ingredient_id":"ING_CREME_DE_CASSIS","quantity":{"amount":10,"unit":"ml"},"display_name":"Crème de Cassis","notes":null}]'::jsonb, 'build', '{"en":"Pour crème de cassis into a wine glass. Top with chilled white wine. Stir gently. No garnish required."}'::jsonb, 'White wine glass', '{"en":"None"}'::jsonb, 'none', 'up', '{"ING_DRY_WHITE_WINE"}', '{"fruity","dry","light"}', 10, 120, 'easy', 30, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer","autumn"}', '{"aperitif","casual","outdoor","french_dining"}', false, false, '{"kir-royale","kir-imperial","kir-breton"}', 'Traditionally made with Burgundian Aligoté wine, but any dry white wine works. The Kir Royale variation uses champagne instead of white wine. Ratio can be adjusted - typically 1:9 cassis to wine. Serve well chilled.', 'low', 60, 'https://iba-world.com/kir/', 'IBA Official Recipe. Traditional French apéritif.', 1)
