-- 59. Long Island Iced Tea


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('b750feaf-42cc-42d0-81e7-2ee593d153dd', 'long-island-iced-tea', 'long_island_iced_tea_iba_contemporary_2025', '{"en":"Long Island Iced Tea"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","strong","party","deceptive"}', '{"en":"A deceptively strong cocktail combining five spirits with cola and lemon juice. Despite its name and appearance, it contains no tea but resembles iced tea in color. Famous for its potency masked by refreshing sweetness."}'::jsonb, '{"created_year":"1972","origin":{"city":"Long Island","bar":"Oak Beach Inn","country":"USA"},"creator":{"name":"Robert \"Rosebud\" Butt","profession":"bartender"},"story":{"en":"Created in 1972 by Robert \"Rosebud\" Butt at the Oak Beach Inn in Hampton Bays, Long Island. According to Butt, he created it for a cocktail competition where the challenge was to create a new mixed drink using triple sec. The drink became famous for containing nearly equal parts of vodka, tequila, rum, gin, and triple sec, making it one of the strongest cocktails served in bars."},"named_after":{"en":"Named after Long Island, New York, where it was created. The \"iced tea\" part refers to its resemblance to the non-alcoholic beverage."}}'::jsonb, '{"profile":["sweet","strong","citrus"],"description":{"en":"Surprisingly smooth despite high alcohol content. The combination of spirits creates a complex flavor that''s balanced by cola sweetness and lemon tartness. Dangerously easy to drink for its strength."},"first_impression":{"en":"Sweet and citrusy like iced tea, masking the alcohol strength"},"finish":{"en":"Warm alcohol finish that reveals the drink''s true strength"},"balance":{"en":"Carefully balanced to hide its potency - deceptively smooth"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["party","celebration","nightclub"],"seasons":["spring","summer"],"food_pairings":{"en":"Best enjoyed on its own or with light snacks like chips, pretzels, or bar nuts. The high alcohol content makes it less suitable for pairing with full meals."},"ideal_for":{"en":"Perfect for experienced drinkers who want a strong cocktail. Be cautious - the smooth taste masks significant alcohol content. Not recommended for those new to cocktails."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":15,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_TEQUILA","quantity":{"amount":15,"unit":"ml"},"display_name":"Tequila","notes":null},{"ingredient_id":"ING_RUM_WHITE","quantity":{"amount":15,"unit":"ml"},"display_name":"White rum","notes":null},{"ingredient_id":"ING_GIN","quantity":{"amount":15,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_TRIPLE_SEC","quantity":{"amount":15,"unit":"ml"},"display_name":"Triple sec","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":25,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":30,"unit":"ml"},"display_name":"Simple syrup","notes":null},{"ingredient_id":"ING_COLA","quantity":{"amount":1,"unit":"splash"},"display_name":"Cola","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients except cola into a shaker with ice and shake. Strain into a highball glass filled with fresh ice. Top with a splash of cola for color. Garnish with a lemon slice."}'::jsonb, 'Highball glass', '{"en":"Lemon slice"}'::jsonb, 'cubes', 'on_the_rocks', '{"ING_VODKA","ING_TEQUILA","ING_RUM_WHITE","ING_GIN","ING_TRIPLE_SEC"}', '{"sweet","strong","citrus"}', 22, 275, 'medium', 90, '{"sulphites"}', '{"alcohol","caffeine","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"party","celebration","nightclub"}', false, false, '{"tokyo-tea","texas-tea","electric-iced-tea"}', 'WARNING: Very high alcohol content. Advise customers to drink responsibly. Use only a splash of cola - too much will overpower. All spirits should be measured precisely. This is NOT a drink to make carelessly.', 'mid', 85, 'https://iba-world.com/long-island-iced-tea/', 'IBA Official Recipe.', 1)
