-- 62. Mimosa


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('02f58ed2-b8d2-4a41-bfc7-db1947d123e9', 'mimosa', 'mimosa_iba_contemporary_classic', '{"en":"Mimosa"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","brunch","celebration","sparkling","simple"}', '{"en":"An elegant and refreshing champagne cocktail combining sparkling wine with fresh orange juice. The quintessential brunch cocktail, beloved for its simplicity and bright, effervescent character."}'::jsonb, '{"created_year":"1925","origin":{"city":"Paris","bar":"Hôtel Ritz Paris","country":"France"},"creator":{"name":"Frank Meier","profession":"bartender"},"story":{"en":"Created at the Hôtel Ritz Paris in 1925, allegedly by bartender Frank Meier. The drink was named after the mimosa flower due to its similar yellow-orange color. It became the signature drink of the Parisian elite and gained international fame as the ultimate brunch cocktail."},"named_after":{"en":"Named after the mimosa flower, which has a similar yellow-orange color to the cocktail."}}'::jsonb, '{"profile":["citrus","sparkling","refreshing"],"description":{"en":"Light, bright, and effervescent. Fresh orange juice provides natural sweetness and acidity, while champagne adds sophisticated bubbles and crisp dryness. Simple yet elegant."},"first_impression":{"en":"Bright citrus and lively bubbles"},"finish":{"en":"Clean, crisp finish with lingering orange notes"},"balance":{"en":"Perfect balance between champagne dryness and orange sweetness"}}'::jsonb, '{"best_time":["morning","afternoon"],"occasions":["brunch","celebration","wedding","holiday"],"seasons":["spring","summer","autumn","winter"],"food_pairings":{"en":"Perfect with brunch dishes like eggs Benedict, French toast, pastries, and fresh fruit. Also excellent with light appetizers and seafood."},"ideal_for":{"en":"Ideal for anyone who enjoys light, refreshing cocktails. The perfect choice for brunch celebrations, morning gatherings, or any occasion calling for elegance."}}'::jsonb, '[{"ingredient_id":"ING_CHAMPAGNE","quantity":{"amount":75,"unit":"ml"},"display_name":"Champagne (or sparkling wine)","notes":null},{"ingredient_id":"ING_ORANGE_JUICE","quantity":{"amount":75,"unit":"ml"},"display_name":"Fresh orange juice","notes":null}]'::jsonb, 'build', '{"en":"Pour orange juice into a champagne flute. Gently top with chilled champagne. Stir gently and briefly. Optionally garnish with an orange twist."}'::jsonb, 'Champagne flute', '{"en":"Orange twist (optional)"}'::jsonb, 'none', 'up', '{"ING_CHAMPAGNE"}', '{"citrus","sparkling","refreshing"}', 6, 120, 'easy', 30, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer","autumn","winter"}', '{"brunch","celebration","wedding","holiday"}', false, false, '{"grand-mimosa","poinsettia","bellini"}', 'Always use fresh-squeezed orange juice for best results. Champagne should be well-chilled. Pour orange juice first, then top with champagne. Traditional ratio is 1:1 but can be adjusted to taste.', 'mid', 95, 'https://iba-world.com/mimosa/', 'IBA Official Recipe. Historical information from Ritz Paris archives.', 1)
