-- 66. Piña Colada


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('1c540b10-8d5f-47ab-8272-39359478daf4', 'pina-colada', 'pina_colada_iba_contemporary_classic', '{"en":"Piña Colada"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","tropical","creamy","summer","beach","iconic"}', '{"en":"The national drink of Puerto Rico - a luscious blend of rum, coconut cream, and pineapple juice. Creamy, tropical, and utterly refreshing, it''s the quintessential beach cocktail that tastes like paradise."}'::jsonb, '{"created_year":"1954","origin":{"city":"San Juan","bar":"Caribe Hilton Hotel","country":"Puerto Rico"},"creator":{"name":"Ramón \"Monchito\" Marrero","profession":"bartender"},"story":{"en":"Created by bartender Ramón \"Monchito\" Marrero at the Caribe Hilton Hotel in San Juan, Puerto Rico in 1954. Marrero spent three months perfecting the recipe to capture the essence of Puerto Rico. The drink became the hotel''s signature cocktail and later Puerto Rico''s national drink. The name means \"strained pineapple\" in Spanish."},"named_after":{"en":"Spanish for \"strained pineapple,\" referring to the fresh pineapple juice used in the cocktail."}}'::jsonb, '{"profile":["tropical","creamy","sweet"],"description":{"en":"Rich, creamy, and intensely tropical. Coconut cream provides luxurious texture while pineapple adds bright, tangy sweetness. White rum ties it together with subtle warmth. Like a vacation in a glass."},"first_impression":{"en":"Sweet coconut and bright pineapple with creamy texture"},"finish":{"en":"Long, tropical finish with lingering coconut sweetness"},"balance":{"en":"Well-balanced between creamy richness and fruity brightness"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["beach","pool_party","tropical_party","vacation"],"seasons":["spring","summer"],"food_pairings":{"en":"Perfect with Caribbean cuisine, grilled seafood, jerk chicken, tropical fruits, and coconut-based desserts."},"ideal_for":{"en":"Ideal for anyone seeking a tropical escape. Perfect for beach vacations, poolside relaxation, or bringing summer vibes to any occasion."}}'::jsonb, '[{"ingredient_id":"ING_WHITE_RUM","quantity":{"amount":50,"unit":"ml"},"display_name":"White rum","notes":null},{"ingredient_id":"ING_COCONUT_CREAM","quantity":{"amount":30,"unit":"ml"},"display_name":"Coconut cream","notes":null},{"ingredient_id":"ING_PINEAPPLE_JUICE","quantity":{"amount":50,"unit":"ml"},"display_name":"Fresh pineapple juice","notes":null}]'::jsonb, 'blend', '{"en":"Blend all ingredients with ice in an electric blender at high speed. Pour into a large goblet or Hurricane glass. Garnish with a pineapple wedge and maraschino cherry."}'::jsonb, 'Hurricane glass', '{"en":"Pineapple wedge and maraschino cherry"}'::jsonb, 'blended', 'frozen', '{"ING_WHITE_RUM"}', '{"tropical","creamy","sweet"}', 10, 300, 'easy', 90, '{"sulphites","tree_nuts"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"beach","pool_party","tropical_party","vacation"}', false, false, '{"miami-vice","chi-chi","amaretto-colada"}', 'Blend with plenty of ice for proper frozen consistency. Use Coco López or similar coconut cream, not coconut milk. Fresh pineapple juice is essential. Can be made in batches for parties. The Miami Vice combines with frozen Strawberry Daiquiri.', 'mid', 95, 'https://iba-world.com/pina-colada/', 'IBA Official Recipe. National drink of Puerto Rico since 1978.', 1)
