-- 67. Pisco Sour


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('9284d9cf-7b71-47a4-abcd-790660498628', 'pisco-sour', 'pisco_sour_iba_contemporary_2025', '{"en":"Pisco Sour"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","peruvian","sour","classic","foamy"}', '{"en":"Peru''s national cocktail, combining pisco (grape brandy) with lime juice, simple syrup, egg white, and Angostura bitters. The egg white creates a luxurious foam cap, making this sour cocktail exceptionally smooth and elegant."}'::jsonb, '{"created_year":"1920","origin":{"city":"Lima","bar":"Morris'' Bar","country":"Peru"},"creator":{"name":"Victor Vaughen Morris","profession":"bartender"},"story":{"en":"Created by American bartender Victor Vaughen Morris in the early 1920s at his bar in Lima, Peru. Morris adapted the classic whiskey sour recipe using pisco, Peru''s national spirit made from grapes. The drink became Peru''s national cocktail and is celebrated annually on the first Saturday of February as \"Pisco Sour Day.\" Chile also claims the Pisco Sour, leading to an ongoing friendly rivalry between the two countries."},"named_after":{"en":"Named after pisco, the grape brandy spirit that forms its base."}}'::jsonb, '{"profile":["sour","citrus","smooth","aromatic"],"description":{"en":"Silky smooth with a perfect balance of tart and sweet. Fresh lime provides bright acidity, pisco brings floral, fruity grape notes, and egg white creates velvety texture. Angostura bitters add aromatic complexity to the foam."},"first_impression":{"en":"Aromatic foam with bitters, followed by tart lime"},"finish":{"en":"Clean, crisp finish with lingering citrus and grape notes"},"balance":{"en":"Perfectly balanced sour cocktail with luxurious texture"}}'::jsonb, '{"best_time":["aperitivo","evening"],"occasions":["aperitivo","celebration","date_night"],"seasons":["spring","summer","autumn"],"food_pairings":{"en":"Excellent with Peruvian ceviche, seafood, grilled fish, empanadas, and light appetizers. The acidity complements seafood beautifully."},"ideal_for":{"en":"Perfect for sour cocktail lovers and those who appreciate elegant, classic drinks. Great for special occasions and introducing people to pisco."}}'::jsonb, '[{"ingredient_id":"ING_PISCO","quantity":{"amount":45,"unit":"ml"},"display_name":"Pisco","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":30,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":20,"unit":"ml"},"display_name":"Simple syrup","notes":null},{"ingredient_id":"ING_EGG_WHITE","quantity":{"amount":1,"unit":"whole"},"display_name":"Egg white","notes":null},{"ingredient_id":"ING_ANGOSTURA_BITTERS","quantity":{"amount":3,"unit":"dash"},"display_name":"Angostura bitters","notes":null}]'::jsonb, 'shake', '{"en":"Add pisco, lime juice, simple syrup, and egg white to shaker. Dry shake (without ice) vigorously for 15 seconds. Add ice and shake again until well chilled. Strain into a chilled cocktail glass. Drop 3 dashes of Angostura bitters on foam and garnish."}'::jsonb, 'Cocktail glass (Coupe)', '{"en":"3 drops Angostura bitters on foam"}'::jsonb, 'none', 'up', '{"ING_PISCO"}', '{"sour","citrus","smooth","aromatic"}', 15, 180, 'medium', 120, '{"eggs","sulphites"}', '{"alcohol","eggs","sulphites_intolerance"}', '{"pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"gluten-free","dairy-free"}', '{"spring","summer","autumn"}', '{"aperitivo","celebration","date_night"}', false, false, '{"chilean-pisco-sour","pisco-punch"}', 'Dry shake first (no ice) to emulsify egg white properly. Then add ice and shake again. Use fresh egg white - pasteurized works but creates less foam. Drop bitters precisely on foam for visual effect.', 'mid', 75, 'https://iba-world.com/pisco-sour/', 'IBA Official Recipe.', 1)
