-- 68. Rabo-de-Galo


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('9ace6646-50db-4c22-85e0-3b607daba9b9', 'rabo-de-galo', 'rabo_de_galo_iba_contemporary_2025', '{"en":"Rabo-de-Galo"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","brazilian","simple","strong"}', '{"en":"Brazil''s classic cachaça cocktail, combining cachaça with sweet vermouth and a touch of Cynar. Simple yet sophisticated, this drink showcases the grassy, vegetal notes of cachaça balanced with Italian bitters."}'::jsonb, '{"created_year":"1940","origin":{"city":"São Paulo","country":"Brazil"},"story":{"en":"The Rabo-de-Galo (meaning \"rooster''s tail\" in Portuguese) is a traditional Brazilian cocktail that emerged in São Paulo bars in the 1940s. It represents Brazil''s embrace of its national spirit cachaça mixed with European liqueurs. The drink gained official recognition when added to the IBA Contemporary Classics list, bringing Brazilian cocktail culture to international bartending."},"named_after":{"en":"Named \"Rabo-de-Galo\" (rooster''s tail) possibly referring to the drink''s appearance or the colorful nature of Brazilian cocktail culture."}}'::jsonb, '{"profile":["herbal","sweet","bitter"],"description":{"en":"Complex and balanced. Cachaça brings grassy, vegetal notes, sweet vermouth adds richness and spice, while Cynar contributes artichoke bitterness. A sophisticated sip showcasing Brazilian spirit culture."},"first_impression":{"en":"Sweet vermouth and herbal notes, followed by cachaça grassiness"},"finish":{"en":"Lingering bitter-sweet finish with artichoke and spice notes"},"balance":{"en":"Well-balanced between sweet vermouth and bitter Cynar"}}'::jsonb, '{"best_time":["aperitivo","evening"],"occasions":["aperitivo","digestivo","date_night"],"seasons":["autumn","winter","spring"],"food_pairings":{"en":"Excellent as an aperitivo with Brazilian snacks, coxinha, pão de queijo, olives, or charcuterie. The bitter notes stimulate appetite."},"ideal_for":{"en":"Perfect for those who appreciate bitter, complex cocktails and want to explore Brazilian spirits. Great for aperitivo hour."}}'::jsonb, '[{"ingredient_id":"ING_CACHACA","quantity":{"amount":40,"unit":"ml"},"display_name":"Cachaça","notes":null},{"ingredient_id":"ING_VERMOUTH_SWEET","quantity":{"amount":20,"unit":"ml"},"display_name":"Sweet vermouth","notes":null},{"ingredient_id":"ING_CYNAR","quantity":{"amount":10,"unit":"ml"},"display_name":"Cynar","notes":null}]'::jsonb, 'stir', '{"en":"Pour all ingredients into a mixing glass with ice. Stir well. Strain into a chilled cocktail glass. Garnish with an orange twist."}'::jsonb, 'Cocktail glass (Martini)', '{"en":"Orange twist"}'::jsonb, 'none', 'up', '{"ING_CACHACA"}', '{"herbal","sweet","bitter"}', 25, 140, 'medium', 60, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"autumn","winter","spring"}', '{"aperitivo","digestivo","date_night"}', false, false, '{"rabo-de-galo-aged"}', 'Stir, don''t shake - maintains clarity. Use quality cachaça (aged is nice but not required). Cynar is essential - don''t substitute. Express orange oils over drink before garnishing.', 'mid', 45, 'https://iba-world.com/rabo-de-galo/', 'IBA Official Recipe.', 1)
