VALUES ('f3da168c-9548-4e17-8629-54dd1a8b3110', 'sea-breeze', 'sea_breeze_iba_contemporary_classic', '{"en":"Sea Breeze"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","refreshing","fruity","summer","beach"}', '{"en":"A refreshing vodka-based cocktail combining cranberry and grapefruit juices. Light, fruity, and perfectly balanced, it''s the ideal beach-side companion with its beautiful pink hue."}'::jsonb, '{"created_year":"1920","origin":{"city":"Unknown","bar":"Unknown","country":"USA"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The Sea Breeze evolved from earlier gin-based recipes in the 1920s-30s. The modern vodka version with cranberry and grapefruit juice became popular in the 1970s, particularly during the cranberry juice marketing boom by Ocean Spray."},"named_after":{"en":"Named for its refreshing, ocean-breeze quality - perfect for seaside enjoyment."}}'::jsonb, '{"profile":["fruity","tart","refreshing"],"description":{"en":"Bright and refreshing with a perfect balance of sweet and tart. Cranberry provides tartness while grapefruit adds citrusy bitterness. Vodka keeps it clean and crisp."},"first_impression":{"en":"Tart cranberry and citrus grapefruit with clean vodka"},"finish":{"en":"Clean, refreshing finish with lingering fruit notes"},"balance":{"en":"Well-balanced between tart cranberry and bitter grapefruit"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["beach","pool_party","summer_gathering","casual"],"seasons":["spring","summer"],"food_pairings":{"en":"Perfect with seafood, grilled fish, summer salads, and light appetizers. Also pairs well with beach snacks and barbecue."},"ideal_for":{"en":"Ideal for anyone seeking a light, refreshing cocktail. Perfect for beach days, poolside relaxation, or any summer occasion."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":40,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_CRANBERRY_JUICE","quantity":{"amount":120,"unit":"ml"},"display_name":"Cranberry juice","notes":null},{"ingredient_id":"ING_GRAPEFRUIT_JUICE","quantity":{"amount":30,"unit":"ml"},"display_name":"Grapefruit juice","notes":null}]'::jsonb, 'build', '{"en":"Build all ingredients in a highball glass filled with ice. Stir gently. Garnish with a lime wedge."}'::jsonb, 'Highball glass', '{"en":"Lime wedge"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_VODKA"}', '{"fruity","tart","refreshing"}', 8, 180, 'easy', 45, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"beach","pool_party","summer_gathering","casual"}', false, false, '{"bay-breeze","cape-codder","madras"}', 'Use fresh-squeezed grapefruit juice when possible. The Bay Breeze variation uses pineapple instead of grapefruit. Build in glass - no shaking needed.', 'low', 75, 'https://iba-world.com/sea-breeze/', 'IBA Official Recipe.', 1)
