VALUES ('79e14a88-00b3-4eac-a1a3-85d7c0ee4f7a', 'singapore-sling', 'singapore_sling_iba_contemporary_classic', '{"en":"Singapore Sling"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","tropical","fruity","classic","iconic"}', '{"en":"A legendary gin-based cocktail with a complex blend of citrus, cherry liqueur, and tropical flavors. Created at the iconic Raffles Hotel, this pink-hued classic is one of the most famous cocktails in the world."}'::jsonb, '{"created_year":"1915","origin":{"city":"Singapore","bar":"Raffles Hotel Long Bar","country":"Singapore"},"creator":{"name":"Ngiam Tong Boon","profession":"bartender"},"story":{"en":"Created by Hainanese-Chinese bartender Ngiam Tong Boon at Raffles Hotel''s Long Bar around 1915. Originally designed to appeal to ladies who weren''t supposed to drink in public, the cocktail''s fruity appearance disguised its alcoholic content. The original recipe was lost and recreated in the 1970s."},"named_after":{"en":"Named after Singapore, where it was created at the legendary Raffles Hotel."}}'::jsonb, '{"profile":["fruity","sweet","tropical"],"description":{"en":"Complex and tropical with layers of cherry, pineapple, and citrus. The gin provides a botanical backbone while Benedictine adds herbal depth. Sweet yet refreshing with a beautiful balance."},"first_impression":{"en":"Bright cherry and pineapple flavors with gin botanicals"},"finish":{"en":"Long, fruity finish with lingering herbal notes from Benedictine"},"balance":{"en":"Well-balanced between sweetness, citrus acidity, and botanical complexity"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["celebration","special_occasion","tropical_party"],"seasons":["spring","summer"],"food_pairings":{"en":"Pairs well with Asian cuisine, especially Singaporean dishes, seafood, and tropical fruits."},"ideal_for":{"en":"Perfect for those who enjoy complex, fruity cocktails with historical significance. A must-try for anyone visiting Singapore."}}'::jsonb, '[{"ingredient_id":"ING_GIN","quantity":{"amount":30,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_CHERRY_LIQUEUR","quantity":{"amount":15,"unit":"ml"},"display_name":"Cherry liqueur","notes":null},{"ingredient_id":"ING_BENEDICTINE","quantity":{"amount":7.5,"unit":"ml"},"display_name":"Bénédictine","notes":null},{"ingredient_id":"ING_COINTREAU","quantity":{"amount":7.5,"unit":"ml"},"display_name":"Cointreau","notes":null},{"ingredient_id":"ING_GRENADINE","quantity":{"amount":10,"unit":"ml"},"display_name":"Grenadine","notes":null},{"ingredient_id":"ING_PINEAPPLE_JUICE","quantity":{"amount":120,"unit":"ml"},"display_name":"Pineapple juice","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_ANGOSTURA_BITTERS","quantity":{"amount":1,"unit":"dash"},"display_name":"Angostura bitters","notes":null}]'::jsonb, 'shake', '{"en":"Pour all ingredients into a cocktail shaker filled with ice cubes. Shake well. Strain into a highball glass. Garnish with a slice of pineapple and a maraschino cherry."}'::jsonb, 'Highball glass', '{"en":"Pineapple slice and maraschino cherry"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_GIN"}', '{"fruity","sweet","tropical"}', 12, 240, 'medium', 180, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"celebration","special_occasion","tropical_party"}', false, false, '{"classic-singapore-sling","modern-singapore-sling"}', 'Use fresh pineapple juice if possible. The original recipe was lost and multiple versions exist. This is the IBA official version. Shake vigorously to properly integrate all ingredients.', 'mid', 85, 'https://iba-world.com/singapore-sling/', 'IBA Official Recipe. Historical information from Raffles Hotel archives.', 1)
