-- 73. Vesper


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('19ac91b7-9ee9-4476-a9fc-85f9fa447300', 'vesper', 'vesper_iba_contemporary_classic', '{"en":"Vesper"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","james-bond","literary","spirit-forward","iconic"}', '{"en":"James Bond''s signature cocktail - a powerful combination of gin, vodka, and Lillet Blanc. Created by Ian Fleming in Casino Royale, this drink is strong, sophisticated, and distinctly literary."}'::jsonb, '{"created_year":"1953","origin":{"city":"London","bar":"Fictional (Literary)","country":"UK"},"creator":{"name":"Ian Fleming (via James Bond)","profession":"author"},"story":{"en":"The Vesper was invented by author Ian Fleming and appeared in his 1953 James Bond novel \"Casino Royale.\" Bond orders it and names it after Vesper Lynd, the book''s female lead. The original called for Kina Lillet, which no longer exists - modern versions use Lillet Blanc or Cocchi Americano."},"named_after":{"en":"Named after Vesper Lynd, the female protagonist in Ian Fleming''s \"Casino Royale.\""}}'::jsonb, '{"profile":["spirit-forward","dry","citrus"],"description":{"en":"Bold, strong, and bracingly dry. The combination of gin and vodka creates a powerful backbone, while Lillet adds subtle fruitiness and complexity. Lemon provides a bright, aromatic finish."},"first_impression":{"en":"Strong gin botanicals with vodka smoothness and citrus"},"finish":{"en":"Long, dry finish with lingering botanical and citrus notes"},"balance":{"en":"Spirit-forward with subtle aromatic complexity from Lillet"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["special_occasion","celebration","date_night"],"seasons":["autumn","winter","spring","summer"],"food_pairings":{"en":"Pairs well with seafood, particularly oysters, caviar, and smoked salmon. Also excellent with light appetizers and olives."},"ideal_for":{"en":"Perfect for spirit-forward cocktail enthusiasts and James Bond fans. Ideal for those who appreciate strong, sophisticated drinks with literary pedigree."}}'::jsonb, '[{"ingredient_id":"ING_GIN","quantity":{"amount":60,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_VODKA","quantity":{"amount":15,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_LILLET_BLANC","quantity":{"amount":7.5,"unit":"ml"},"display_name":"Lillet Blanc","notes":null}]'::jsonb, 'shake', '{"en":"Pour all ingredients into a cocktail shaker filled with ice cubes. Shake well. Strain into a chilled cocktail glass. Garnish with a large, thin slice of lemon peel."}'::jsonb, 'Cocktail glass (Martini)', '{"en":"Large, thin lemon peel"}'::jsonb, 'none', 'up', '{"ING_GIN","ING_VODKA"}', '{"spirit-forward","dry","citrus"}', 28, 190, 'easy', 60, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"autumn","winter","spring","summer"}', '{"special_occasion","celebration","date_night"}', false, false, '{"vesper-with-cocchi-americano","reverse-vesper"}', 'Bond''s original spec: "Three measures of Gordon''s, one of vodka, half a measure of Kina Lillet. Shake it very well until it''s ice-cold, then add a large thin slice of lemon peel." Modern Lillet Blanc is sweeter than original Kina Lillet - some bartenders prefer Cocchi Americano for authenticity.', 'mid', 70, 'https://iba-world.com/vesper/', 'IBA Official Recipe. Originally from Ian Fleming''s "Casino Royale" (1953).', 1)
