-- 74. White Russian


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('ccb36b0d-3be9-46c7-a72e-89381989a6ef', 'white-russian', 'white_russian_iba_contemporary_classic', '{"en":"White Russian"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","creamy","coffee","dessert","after-dinner","cult-classic"}', '{"en":"A decadent, creamy cocktail that adds fresh cream to the classic Black Russian. Smooth, sweet, and dangerously easy to drink - this coffee-flavored indulgence became a cultural icon thanks to \"The Big Lebowski.\""}'::jsonb, '{"created_year":"1960s","origin":{"city":"Oakland","bar":"Unknown","country":"USA"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The White Russian is a variation of the Black Russian (created 1949), with cream added. The exact origins are unclear, but it gained popularity in the 1960s-70s. The drink was relatively obscure until the 1998 Coen Brothers film \"The Big Lebowski,\" where Jeff Bridges'' character \"The Dude\" drinks White Russians throughout the movie. This catapulted the cocktail to cult status and renewed popularity."},"named_after":{"en":"Named \"White Russian\" due to the white color from cream, contrasting with the \"Black Russian\" which lacks cream."}}'::jsonb, '{"profile":["creamy","sweet","smooth","boozy"],"description":{"en":"Rich and velvety with prominent coffee flavor from Kahlúa, smooth vodka backbone, and luxurious cream. Sweet, indulgent, and dessert-like with balanced coffee and dairy notes."},"first_impression":{"en":"Smooth cream and sweet coffee with vodka warmth"},"finish":{"en":"Long, creamy finish with lingering coffee and vanilla notes"},"balance":{"en":"Well-balanced between sweet coffee liqueur, neutral vodka, and rich cream"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["digestivo","nightcap","casual","celebration"],"seasons":["autumn","winter","spring","summer"],"food_pairings":{"en":"Perfect as a dessert replacement or with chocolate desserts, brownies, tiramisu, coffee cake, or ice cream."},"ideal_for":{"en":"Perfect for dessert cocktail lovers and fans of \"The Big Lebowski.\" Ideal for those who enjoy creamy, sweet drinks with coffee flavor. A great introduction to cocktails for non-drinkers transitioning to spirits."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":50,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_COFFEE_LIQUEUR","quantity":{"amount":20,"unit":"ml"},"display_name":"Coffee liqueur (Kahlúa)","notes":null},{"ingredient_id":"ING_HEAVY_CREAM","quantity":{"amount":30,"unit":"ml"},"display_name":"Fresh cream","notes":null}]'::jsonb, 'build', '{"en":"Pour vodka and coffee liqueur into an old fashioned glass filled with ice cubes. Float fresh cream on top. Stir gently before drinking, or drink through the cream."}'::jsonb, 'Old Fashioned glass', '{"en":"None (optional: coffee beans)"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_VODKA"}', '{"creamy","sweet","smooth","boozy"}', 18, 300, 'easy', 45, '{"milk","sulphites"}', '{"lactose","alcohol","sulphites_intolerance","caffeine"}', '{"vegetarian","pescatarian","gluten_free","nut_free"}', 0, '{"vegetarian","gluten-free"}', '{"autumn","winter","spring","summer"}', '{"digestivo","nightcap","casual","celebration"}', false, false, '{"black-russian","dirty-russian","mudslide","colorado-bulldog"}', 'The cream can be floated on top (layered) for visual appeal, or stirred in for consistency. "The Dude" from The Big Lebowski is famous for drinking these - popular culture reference. Can substitute half-and-half for cream if preferred lighter. Kahlúa is traditional but any coffee liqueur works. Deceptively strong despite creamy sweetness.', 'low', 85, 'https://iba-world.com/white-russian/', 'IBA Official Recipe. Cultural reference from "The Big Lebowski" (1998).', 1)
