-- 77. Bee's Knees


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('fde8c68a-53d6-4a58-a1dc-36a00c42811f', 'bees-knees', 'b4c5d6e7f8a9b0c1d2e3f4a5b6c7d8e9f0a1b2c3', '{"en":"Bee''s Knees"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","gin","honey","prohibition","classic"}', '{"en":"A Prohibition-era classic combining gin with honey and lemon. This simple yet sophisticated cocktail features floral honey sweetness balanced by bright citrus - it''s truly \"the bee''s knees!\""}'::jsonb, '{"created_year":"1920","origin":{"city":"Unknown","bar":"Unknown","country":"USA"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The Bee''s Knees emerged during Prohibition in the 1920s. The name comes from a popular slang expression of that era meaning \"the best\" or \"the height of excellence.\" During Prohibition, honey was often used to mask the harsh flavor of bathtub gin. The cocktail gained renewed popularity during the craft cocktail revival of the 2000s, appreciated for its simple elegance and historical significance."},"named_after":{"en":"Named after the 1920s slang phrase \"the bee''s knees,\" meaning something excellent or outstanding."}}'::jsonb, '{"profile":["floral","citrus","smooth"],"description":{"en":"Bright lemon tartness balanced by floral honey sweetness and gin botanicals. Smooth, refreshing, and perfectly balanced with a silky texture from the honey."},"first_impression":{"en":"Bright lemon with floral honey and gin botanicals"},"finish":{"en":"Smooth, lingering finish with honey sweetness and gin complexity"},"balance":{"en":"Perfectly balanced between lemon tartness, honey sweetness, and gin dryness"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["brunch","garden_party","afternoon_tea","date_night"],"seasons":["spring","summer"],"food_pairings":{"en":"Excellent with light salads, goat cheese, honey-glazed appetizers, or afternoon tea sandwiches."},"ideal_for":{"en":"Perfect for gin lovers who enjoy smooth, citrusy cocktails. Ideal for those interested in Prohibition-era classics or anyone seeking a sophisticated yet simple drink."}}'::jsonb, '[{"ingredient_id":"ING_GIN","quantity":{"amount":50,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":25,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null},{"ingredient_id":"ING_HONEY_SYRUP","quantity":{"amount":12.5,"unit":"ml"},"display_name":"Honey syrup","notes":null}]'::jsonb, 'shake', '{"en":"Shake all ingredients with ice. Strain into a chilled cocktail glass."}'::jsonb, 'Cocktail glass (Coupe)', '{"en":"Lemon twist"}'::jsonb, 'none', 'up', '{"ING_GIN"}', '{"floral","citrus","smooth"}', 18, 160, 'easy', 45, '{}', '{"alcohol"}', '{"vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","gluten-free","dairy-free"}', '{"spring","summer"}', '{"brunch","garden_party","date_night"}', false, false, '{}', 'Use honey syrup (3:1 honey to water) for easier mixing. Shake vigorously to properly emulsify the honey. Quality honey makes a big difference.', 'mid', 70, 'https://iba-world.com/bees-knees/', 'IBA Official Recipe', 1)
