-- 78. Bramble


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('53dfc02d-1d4f-46d0-bb58-9c81fefa4ba6', 'bramble', 'd0e1f2a3b4c5d6e7f8a9b0c1d2e3f4a5b6c7d8e9', '{"en":"Bramble"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","gin","blackberry","british","fruity"}', '{"en":"A modern British classic combining gin with fresh lemon juice and crème de mûre (blackberry liqueur). This elegant cocktail features a beautiful berry cascade and perfectly balanced sweet-tart flavors."}'::jsonb, '{"created_year":"1984","origin":{"city":"London","bar":"Fred''s Club","country":"UK"},"creator":{"name":"Dick Bradsell","profession":"bartender"},"story":{"en":"Created in 1984 by legendary bartender Dick Bradsell at Fred''s Club in London. Inspired by his memories of picking wild blackberries on the Isle of Wight as a child, Bradsell created this gin cocktail that celebrates British ingredients and flavors. The drink became one of the defining cocktails of the modern British cocktail renaissance and showcases Bradsell''s talent for creating accessible yet sophisticated drinks."},"named_after":{"en":"Named after bramble bushes, the thorny plants that produce blackberries in the British countryside."}}'::jsonb, '{"profile":["fruity","tart","refreshing"],"description":{"en":"Bright gin botanicals with tart lemon and sweet blackberry liqueur. The berry flavor adds depth while maintaining refreshing citrus notes. Complex and beautifully balanced."},"first_impression":{"en":"Bright gin and lemon with sweet blackberry undertones"},"finish":{"en":"Long finish with lingering berry sweetness and gin botanicals"},"balance":{"en":"Perfectly balanced between gin dryness, lemon tartness, and blackberry sweetness"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["garden_party","brunch","date_night","summer_gathering"],"seasons":["summer","spring","autumn"],"food_pairings":{"en":"Excellent with British afternoon tea, scones with clotted cream, berry desserts, or light salads."},"ideal_for":{"en":"Perfect for gin lovers who enjoy fruity, refreshing cocktails. Ideal for those seeking a sophisticated yet approachable British classic."}}'::jsonb, '[{"ingredient_id":"ING_GIN","quantity":{"amount":40,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":25,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":12.5,"unit":"ml"},"display_name":"Simple syrup","notes":null},{"ingredient_id":"ING_CREME_DE_MURE","quantity":{"amount":15,"unit":"ml"},"display_name":"Crème de mûre","notes":null}]'::jsonb, 'build', '{"en":"Build gin, lemon juice, and simple syrup in a rocks glass filled with crushed ice. Stir gently. Drizzle crème de mûre over the top to create a \"bleeding\" effect."}'::jsonb, 'Rocks glass', '{"en":"Fresh blackberries and lemon slice"}'::jsonb, 'crushed', 'on_the_rocks', '{"ING_GIN"}', '{"fruity","tart","refreshing"}', 15, 190, 'easy', 45, '{}', '{"alcohol"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"summer","spring","autumn"}', '{"garden_party","brunch","date_night"}', false, false, '{"bramble-royale"}', 'Drizzle crème de mûre slowly over crushed ice to create the signature "bleeding" cascade effect. Fresh blackberries enhance presentation.', 'mid', 75, 'https://iba-world.com/bramble/', 'IBA Official Recipe', 1)
