-- 86. Gin Basil Smash


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('cdba0958-8a8d-4010-8aee-65e64a05f5bb', 'gin-basil-smash', 'a6b7c8d9e0f1a2b3c4d5e6f7a8b9c0d1e2f3a4b5', '{"en":"Gin Basil Smash"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","modern","gin","herbal","refreshing","contemporary"}', '{"en":"A modern classic that combines the aromatic complexity of gin with the fresh, peppery notes of muddled basil and tart lemon juice. This refreshing cocktail showcases how simple, quality ingredients can create something extraordinary when handled with care."}'::jsonb, '{"created_year":"2008","origin":{"city":"Hamburg","bar":"Le Lion Bar de Paris","country":"Germany"},"creator":{"name":"Jörg Meyer","profession":"bartender"},"story":{"en":"Created by Jörg Meyer at Le Lion Bar de Paris in Hamburg in 2008, the Gin Basil Smash quickly became a modern classic. Meyer was inspired by the Whiskey Smash and wanted to create something fresh and herbaceous with gin. The drink gained international recognition and helped spark the modern cocktail renaissance in Germany. Its simplicity and flavor made it an instant hit, spreading to bars worldwide."},"named_after":{"en":"Named for the technique of \"smashing\" (muddling) fresh basil leaves with the other ingredients, inspired by the classic Whiskey Smash."}}'::jsonb, '{"profile":["herbal","refreshing","citrus","aromatic"],"description":{"en":"Bright and herbaceous with a perfect balance of botanical gin, fresh basil''s peppery sweetness, and zesty lemon tartness. The muddled basil releases essential oils that complement the gin''s botanicals beautifully."},"first_impression":{"en":"Fresh basil and juniper hit immediately with bright lemon acidity"},"finish":{"en":"Clean, refreshing finish with lingering herbal and citrus notes"},"balance":{"en":"Expertly balanced between herbal complexity, citrus brightness, and spirit strength"}}'::jsonb, '{"best_time":["afternoon","evening","aperitif"],"occasions":["aperitivo","garden_party","casual_gathering","summer_evening"],"seasons":["spring","summer"],"food_pairings":{"en":"Perfect with Mediterranean cuisine, caprese salad, grilled vegetables, fresh seafood, Italian antipasti, and light pasta dishes."},"ideal_for":{"en":"Perfect for gin enthusiasts who appreciate fresh, herbaceous cocktails. Ideal for those seeking a sophisticated yet approachable modern classic. Great for summer gatherings and as a refreshing aperitif."}}'::jsonb, '[{"ingredient_id":"ING_GIN","quantity":{"amount":60,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":20,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":20,"unit":"ml"},"display_name":"Simple syrup","notes":null},{"ingredient_id":"ING_BASIL_LEAVES","quantity":{"amount":8,"unit":"leaves"},"display_name":"Fresh basil leaves","notes":null}]'::jsonb, 'shake', '{"en":"Place basil leaves in a cocktail shaker and gently muddle to release the oils (do not over-muddle or it will become bitter). Add gin, lemon juice, simple syrup, and ice. Shake vigorously until well-chilled. Double strain into a rocks glass filled with fresh ice. Garnish with a fresh basil sprig."}'::jsonb, 'Rocks glass (Old Fashioned)', '{"en":"Fresh basil sprig"}'::jsonb, 'cubes', 'on_the_rocks', '{"ING_GIN"}', '{"herbal","refreshing","citrus","aromatic"}', 18, 160, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"aperitivo","casual","garden_party"}', false, false, '{"strawberry-basil-smash","cucumber-basil-smash"}', 'Fresh basil is critical - do not use dried. Muddle gently to avoid bitterness. Double strain to remove basil pieces. Use a quality gin with good botanical profile. Can substitute lime for lemon for variation.', 'mid', 85, 'https://iba-world.com/gin-basil-smash/', 'IBA Official Recipe. Created by Jörg Meyer at Le Lion Bar de Paris, Hamburg.', 1)
