-- 93. New York Sour


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('8877580a-a1d4-42c0-9fae-2a338ec3d7d5', 'new-york-sour', 'a1b2c3d4e5f6a7b8c9d0e1f2a3b4c5d6e7f8a9b0', '{"en":"New York Sour"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","whiskey","sour","wine","citrus"}', '{"en":"A sophisticated twist on the classic Whiskey Sour, the New York Sour features a red wine float that creates a beautiful gradient effect. This elegant cocktail combines the bright citrus notes of a sour with the rich depth of red wine."}'::jsonb, '{"created_year":"1880","origin":{"city":"New York City","bar":"Unknown","country":"USA"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The New York Sour originated in the 1880s, originally known as the Continental Sour or Southern Whiskey Sour. The addition of a red wine float transformed the classic whiskey sour into an elegant layered cocktail. The drink became popular in New York establishments and eventually took on the name New York Sour."},"named_after":{"en":"Named after New York City, where the cocktail gained popularity and became synonymous with sophisticated drinking culture."}}'::jsonb, '{"profile":["sour","fruity","complex"],"description":{"en":"Bright and citrusy upfront from the lemon juice, balanced by whiskey warmth and sweetness, with a sophisticated tannic finish from the red wine float."},"first_impression":{"en":"Tart lemon and whiskey warmth, followed by fruity wine notes"},"finish":{"en":"Long finish with tannic red wine notes and lingering whiskey warmth"},"balance":{"en":"Perfectly balanced between sour, sweet, and the wine''s complexity"}}'::jsonb, '{"best_time":["evening","afternoon"],"occasions":["date_night","celebration","aperitivo","dinner_party"],"seasons":["autumn","winter","spring"],"food_pairings":{"en":"Excellent with charcuterie boards, grilled meats, aged cheeses, or as an aperitif before a fine dining experience."},"ideal_for":{"en":"Perfect for whiskey lovers looking for something more sophisticated than a standard sour, or wine enthusiasts who want to explore cocktails."}}'::jsonb, '[{"ingredient_id":"ING_WHISKEY_RYE","quantity":{"amount":60,"unit":"ml"},"display_name":"Rye whiskey","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":30,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":20,"unit":"ml"},"display_name":"Simple syrup","notes":null},{"ingredient_id":"ING_EGG_WHITE","quantity":{"amount":1,"unit":"whole"},"display_name":"Egg white","optional":true,"notes":null},{"ingredient_id":"ING_RED_WINE","quantity":{"amount":15,"unit":"ml"},"display_name":"Red wine","notes":null}]'::jsonb, 'shake', '{"en":"Shake whiskey, lemon juice, simple syrup, and egg white (optional) with ice. Strain into an ice-filled rocks glass. Carefully pour red wine over the back of a spoon to create a float on top."}'::jsonb, 'Rocks glass', '{"en":"Lemon peel or cherry"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_WHISKEY_RYE"}', '{"sour","fruity","complex"}', 15, 220, 'medium', 90, '{"egg","sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"gluten-free","dairy-free"}', '{"autumn","winter","spring"}', '{"date_night","celebration","aperitivo"}', false, false, '{"boston-sour","continental-sour","claret-snap"}', 'Pour the red wine float slowly over the back of a spoon to create the signature layered effect. Use a dry, fruity red wine like Shiraz or Malbec. Egg white is optional but adds a nice foam.', 'mid', 75, 'https://iba-world.com/new-york-sour/', 'IBA Official Recipe', 1)
